@-moz-document url-prefix("https://www.youtube.com/") {
/* RED THEME */
/* ORIGINAL THEME BY CELLO */

#yt-masthead-container {
       /* Headercolor */
       background: #CC181E;
}

#progress {  
       /* Progressbar */ 
       background: #ffffff;   
}

#progress dd, #progress dt{  
       /* Progressbar */
       background: #ffffff;
       box-shadow: #ffffff 1px 0 6px 1px;
}

#masthead-search-terms input:focus {
       /* Searchfield and text-color when active */
       background: #ffffff !important;
       color: black;
}

#masthead-search-terms input {
       /* Searchfield text-color when not active */
       color: #D8D8D8;
}

#upload-btn {
       /* Uploadbutton text-color */
       color: #ffffff;
}

/* Appbar */

#masthead-appbar {
       /* Appbar-color*/
       background-color: #CC181E;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
       /* Text-color when not active */
       color: rgba(255, 255, 255, 0.85);
}

#appbar-nav .epic-nav-item-heading {
       /* Text-color when active */
       color: #ffffff;
}

/* Navigation */

#appbar-guide-menu {
       /* Navigation-color */
       background-color: #AE141A !important;
}

#guide-container .guide-item.guide-item {
       /* Navigation text-color */
       color: #D8D8D8;
}

.guide-section-separator {
       /* Separatorline */
       border-bottom: 1px solid #720D12;
}

.guide-item .guide-count, .guide-badge-live {
       /* Videoamount text-color */
       color: #D8D8D8;
}

.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
	   background: #CC181E;
}

.guide-flyout-trigger.on-hover, .guide-item:hover, .guide-item:hover .yt-deemphasized-text, .guide-collapsible-item .yt-uix-expander-head:hover {
       /* Color on hover */
       background: #CC181E;
       color: #ffffff !important;
}

button.guide-view-more {
       /* text-color show-more-button */
       color: #D8D8D8;
}

li.guide-section h3 a {
       /* Headline text-color */
       color: #D8D8D8;
}

.compact-shelf .yt-uix-button-shelf-slider-pager {
       /* Slidebuttons */
       background: #CC181E;
}

/* Scrollbar */

::-webkit-scrollbar-thumb {
       background-color: #CC181E;
       border: 2px solid #720D12;
}

::-webkit-scrollbar-track {
       background-color: #720D12;
}

.yt-scrollbar ::-webkit-scrollbar-thumb:hover {
       background-color: #CC181E;
       border: 2px solid #720D12;
}

.yt-scrollbar ::-webkit-scrollbar-thumb {
       background-color: #CC181E;
       border: 2px solid #720D12;
}
/* LIGHT VERSION */

.caption-line-highlight {
       background-color: rgba(0, 0, 0, 0.1);
       border: none;
       padding: 5px !important;
       font-weight: 500;
}

.caption-line-highlight:hover {
       background-color: rgba(0, 0, 0, 0.2);
       border: none;
}

.caption-line {
       border: none;
       padding: 2px;
       padding-left: 10px;
}

.caption-line:hover {
       background-color: rgba(0, 0, 0, 0.15);
       border: none;
}

#watch-action-panels .yt-uix-button,
#watch-action-panels .yt-uix-button:hover {
       background: rgba(0, 0, 0, 0.1);
}
}

@-moz-document url-prefix("https://www.youtube.com/") {
/* CHANGES */
/* Header  */

#masthead-positioner {
       /* Headershadow */
       
       box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
}

#yt-masthead-container {
       /* Headercontainer & Headercolor */
       
       position: relative;
       padding-top: 7px;
       padding-bottom: 10px;
       border-bottom: 0px;
}

#yt-masthead-signin {
       /* Align sign-in button  */
       
       margin-right: -19px !important;
       margin-top: 6px !important;
}

.signin-container .yt-uix-button-primary {
       /* New sign-in button */
       
       height: 32px;
       width: 32px;
       border-radius: 50%;
       border: none;
       background: no-repeat url(http://i.imgur.com/5BJ2VS8.png) 4px 4px !important;
       color: transparent;
       -webkit-user-select: none;
}

.signin-container {
       /* New sign-in button */
       
       min-height: 32px;
       min-width: 32px;
       background: rgba(0, 0, 0, 0.15) !important;
       border-radius: 50%;
       border: none;
}

#logo-container {
       /* Align Logo-Container */
       
       margin-left: 15px;
}

.logo {
       /* Old YouTube Logo */
       
       visibility: hidden;
}

.exp-responsive #yt-masthead #logo-container {
       /* New YouTube logo */
       
       background: no-repeat url(http://i.imgur.com/2IY6ort.png);
       background-size: 73px 30px !important;
       width: 73px;
       height: 30px;
}

#yt-masthead .yt-masthead-logo-container {
       /* Align leftside menu icon */
       
       margin-top: 10px;
       -webkit-user-select: none;
}

.yt-uix-button-icon-appbar-guide {
       /* New leftside menu icon */
       
       background: no-repeat url(http://i.imgur.com/UrksOso.png);
       background-size: 18px 12px;
       width: 18px;
       height: 12px;
       margin-left: -7px;
       margin-top: -4px;
}

#masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
       /* New leftside menu icon on hover */
       
       background: no-repeat url(http://i.imgur.com/UrksOso.png);
       background-size: 18px 12px;
       width: 18px;
       height: 12px;
       margin-left: -7px;
       margin-top: -4px;
}

#masthead-search {
       /* Searchfield Dimensions */
       
       width: 849px;
       height: 44px;
       max-width: 849px;
       min-width: 500px;
       margin-left: 23px;
}

#masthead-search-terms {
       /* Searchfield */
       
       background: rgba(0, 0, 0, 0.15) !important;
       transition: 300ms background-color;
       height: 44px;
       border-radius: 2px;
}

#masthead-search-terms:hover {
       /* Searchfield on hover */
       
       background: rgba(0, 0, 0, 0.20) !important;
       transition-delay: 100ms;
       height: 44px;
       border-radius: 2px;
}

#masthead-search-terms input:focus {
       /* Searchfield on input */
       
       transition: 250ms background-color;
}

#masthead-search-terms input {
       /* Searchfield fontstyle */
       
       transition: 150ms background-color;
       font-size: 18px;
       padding: 2px 65px;
}

.masthead-search-terms-border {
       /* Searchfield border & shadow */
       
       border: none;
       box-shadow: none;
}

.search-btn-component {
       /* Align & edited searchbutton */
       
       position: absolute;
       z-index: 1;
       border: none;
       box-shadow: none;
       margin-top: 8px;
       height: 0px;
       width: 0px;
}

#masthead-search .search-btn-component .yt-uix-button-content {
       /* New search icon */
       
       opacity: 1;
       background: no-repeat url(http://i.imgur.com/d1AJALV.png) 0px 0px;
       background-size: 22px 22px;
       width: 24px;
       height: 24px;
}

#yt-masthead-user {
