}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
       /* Box-shadow */
       
       border: none;
       box-shadow: 3px 7px 7px 0px rgba(0, 0, 0, 0.45);
}
/* Content */

body {
       overflow-x: hidden;
}

