css:
@-moz-document domain("www.google.co.in") {
body, #cnt, #viewport, html, #main {
    background: url(http://www.youwall.com/wallpapers/201112/red-christmas-ornaments-wallpaper.jpg) !important;
    background-attachment: fixed!important;
    background-position: center!important;
    background-size: cover!important;
}
#gb#gb a.gb_P, #gb#gb span.gb_P {
    color: rgba(241, 235, 0, 0.82) !important;
}
#fbar {
    background: #E51322 !important;
    border-top: 1px solid #e4e4e4;
    line-height: 40px;
    min-width: 980px;
}
.ftl, #footer a {
    color: #ffffff !important;
    margin: 2px 10px 0;
}

a {
    color: #ffffff !important;
    text-decoration: none;
}
body {
    background: #fff;
    color: #ffffff !important;
}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {
    background-image: -webkit-gradient(linear,left top,left bottom,from(#f5f5f5),to(#f1f1f1));
    background-image: -webkit-linear-gradient(top,#f5f5f5,#f1f1f1);
    -webkit-border-radius: 2px;
    -webkit-user-select: none;
    background-color: #cc8686;
    border: 1px solid #ff0a0a !important;
    border-radius: 2px;
    color: #ff0a0a !important;
    cursor: default;
    font-family: arial,sans-serif;
    font-size: 13px;
    font-weight: bold;
    margin: 11px 4px;
    min-width: 54px;
    padding: 0 16px;
    text-align: center;
}
.sbib_a {
    background: #fff !important;
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
}
.sfbgg {
    background: linear-gradient(to left, #ee0979 , #ff6a00) !important;
    height: 65px;
}
.sfbgx {
    background: linear-gradient(to left, #ee0979 , #ff6a00) !important;
    border-bottom: 1px solid #ebebeb;
    height: 85px;
    position: absolute;
    width: 100%;
}
a, cite, cite a:link, cite a:visited, .cite, .cite:link, #_bGc>i, .bc a:link {
    color: rgb(9, 255, 166) !important;
    font-style: normal;
}
#hdtbSum {
    background: linear-gradient(to left, #ee0979 , #ff6a00) !important;
    height: 58px;
    padding: 0;
    position: relative;
    z-index: 102;
}
#appbar {
    background: linear-gradient(to left, #ee0979 , #ff6a00) !important;
    -webkit-box-sizing: border-box;
    width: 100%;
}
#resultStats {
    color: #fff7f7 !important;
}
.s {
    color: #0aafe2 !important;
}
.vk_gy {
    color: #820505 !important;
    background: linear-gradient(to left, #ee0979 , #ff6a00) !important;
}
.rc {
    background: rgba(0, 0, 0, 0.19) !important;
    border-radius: 4px !important;
}

.f, .f a:link {
    color: #ffffff !important;
}

.s .st em, .st.s.std em {
    color: #ffeeee !important;
}
.hdtb-mn-hd {
    color: #fff !important;
    display: inline-block;
    position: relative;
    padding-top: 0;
    padding-bottom: 0;
    padding-right: 22px;
    padding-left: 16px;
    line-height: 22px;
    cursor: pointer;
}
}
