css:
@-moz-document domain("www.ilpost.it") {
/* http://i.imgur.com/SfCoi6V.png logo */
  
  body {
    background: #000 !important;
}


  #wrap {
    background: #000E0B !important;
    color: #F3E803 !important;
}
  
   /*LOGO */
  img[alt="IlPost"]{
    visibility: visible !important;
  }
  header#site-head hgroup h1.site-title a {
   background-image: url(http://i.imgur.com/SfCoi6V.png) !important;
   display: block !important;
   background-size: 100% 100% !important;
  }
  img[alt="Daily Tag"]{
    display: none;
    visibility: hidden;
  } 
  .hot_tag img {
   background-image: url(http://i.imgur.com/L6NGdEx.png) !important;
   background-size: 100% 100% !important;
}
  
 /*-------------parte esterna---------------*/
  article , article.home , aside#sidebar .hpvideo ul li , nav#site-navigation , .single-post-new aside#sidebar .next-article .head-next-article  {
    border-bottom: 1px solid #031E24 !important;
}
   #thirdy .widget h6 , aside#sidebar .blogger_news h6 , aside#sidebar .commentate h6 , article .entry-content td {
    color: #0EC5B1 !important;
    border-bottom: 1px solid #01262c !important;
    border-top:  1px solid #01262c !important;
}
  aside#sidebar .blogger_news ul li , #thirdy .post_it ul li , #thirdy .redaz ul li , aside#sidebar .commentate ul li , #thirdy .gallerie ul li {
    border-bottom: 1px dotted #01262c !important;
}
  nav#site-navigation .menu-navi-main-container ul.nav-menu li a {
    color: #06B3C0 !important;
}
  header#site-head {
    border-bottom: 1px solid #005041;
}
  #main , footer#colophon {
    border-top: 3px solid #005041;
}
  hot_tag p a {
    color: #1C9692 !important;
}
  #thirdy .post_it ul li a , #thirdy .redaz ul li p , .lanci ul li a  {
    color: #0EC5B1 !important;
}
  #thirdy .redaz ul li a {
    color: #088DAA !important;
}
  #thirdy .post_it ul li time , article .entry-content ul.entry-meta-home li a {
    color: #927D0B !important;
}
  
  #thirdy .post_it ul li span , #thirdy .widget h6 , #thirdy .redaz ul li time , #thirdy .post_it p a , footer#colophon .site-info p {
    color: #05937F !important;
}
  .hot_tag p a {
    color: #4F9577 !important;
}
  aside#sidebar .brevi h6 {
    color: #0ec5b1 !important;
    background: #0e353e !important;
    border-radius: 5px !important;
}
  /*cerca*/
  #sea form#searchform {
    background: #07143b !important;
}
  #sea form#searchform input#s {
    background: #070720 !important;
    color: #a2cece !important;
}
  .gpt-new, div[id^="div-gpt-ad"] {
    display: none;
}
   aside#sidebar .flashes_hp {
    background: #082E41 !important;
}
  aside#sidebar .flashes_hp ul li {
    background: #000E0B !important;
    border-top: 1px solid #000E0B !important;
    border-bottom: 1px solid #082E41 !important;
}
  aside#sidebar .prima_pagina , aside#sidebar .comics_hp , aside#sidebar .hpvideo {
    background: #000E0B !important;
} 
  article {
    border-bottom: 1px solid #00A7D4 !important;
}
  .home aside#sidebar, .category aside#sidebar, .search aside#sidebar, .tag aside#sidebar {
    border-right: 1px solid #01262c;
    border-left: 1px solid #01262c;
}
  aside#sidebar .brevi ul li {
    border-bottom: 1px dotted #01262c;
  }
    
  /* ------------parte interna homepage ---------------- */
  
  #main #main-content , article .bord {
    background: #000E0B !important;
}
  .lanci , article.home.open , .archive-header , .page-header {
    border-bottom: 3px double #031e24 !important;
}
  /*tenue*/
  article.home .entry-content p a , article .entry-content p a  , article.photo .entry-content p, aside#sidebar .brevi ul li a , aside#sidebar .blogger_news ul li span , aside#sidebar .commentate ul li a , aside#sidebar .hpvideo span a , #thirdy .gallerie ul li a , .sub_comics .oggi .vignettista p {
    color: #078A65 !important;
}
  /* acceso */
  article.home .entry-content h2.entry-title a ,  article.blogger .entry-content h2 a, article.photo .entry-content h2 a , aside#sidebar .blogger_news ul li a , aside#sidebar .commentate h6 , aside#sidebar .flashes_hp h6 , aside#sidebar .hpvideo ul li p a , section#content article.full.attachment .gallery-meta h1.gallery-title a , .archive-header h1.archive-title , .single-post-new #wrap article footer.entry-meta .releated ul li h5 a , article.videos .entry-content h2.entry-title a , .f-title h2 , article.archive .entry-content h2.entry-title a , .page-header h1.page-title , article header.entry-header h1.entry-title a {
    color: #0EC5B1 !important;
} 
  article.blogger .entry-content span {
    background: #000E0B !important;
}
  article.virgo h3 a , aside#sidebar .citazione h6 a {
    color: #236769 !important;
}
  /* articolo */
  
  .single-post-new aside#sidebar .adv , .single-post-new aside#sidebar .next-article , .single-post-new #new-colophon .footer {
    background: #000E0B !important;
}
  .single-post-new article.leaf .new-comments {
    border: 4px solid #021816 !important;
    background-color: #062029 !important;
}
  .single-post-new footer#new-colophon {
    background-color: #062029 !important;
    border-top: 5px solid #021816 !important;
    border-bottom: 5px solid #021816 !important;
}
  /*---------------colore del testo dell'articolo intero ---------------*/
article .entry-content p {
    color: #055C4F !important;
}
 div[class="logo"] {
    visibility: hidden !important;
  }
  .single-post-new aside#sidebar .next-article .title-next h5 a , .single-post-new aside#sidebar .next-article .title-next a {
    color: #055C4F !important;
}
  .single-post-new footer#new-colophon .subscribe .sub-form form {
    background: #0A082C !important;
}
  .single-post-new #new-colophon .sub-form form input[type="text"] {
    color: white !important;
    background-color: black;
}
  article header.entry-header h1.entry-title {
    color: #2DA8C6 !important;
}
  article header.entry-header .sottit h2.tit2 {
    color: #06AD8F !important;
}
  .single-post-new article figure .caption {
    color: #115C54 !important;
  }
  .single-post-new header#site-head {
    background: #062029 !important;
}
  .single-post-new .entry-container .blogger_info, .single-post-new aside#sidebar .blogger_info {
    background: none repeat scroll 0% 0% #021014 !important;
}
  .EmbeddedTweet {
    overflow: visible !important;
    background-color: #00070F !important;
    border: 1px solid #132d41 !important;
    border-radius: 8px !important;
}
  ul.page-numbers li a {
    color: #054444 !important;
}
  .single-post-new article.leaf .entry-content blockquote p {
    background: none repeat scroll 0% 0% #000806 !important;
}
  article.comics {
    background: #00182d !important;
}
  .sub_comics , article.comics footer.entry-meta , article.archive  {
    background: #000E0B !important;
}

.swiper-slide-active, .swiper-slide-prev, .swiper-slide-next {
    background: none !important;
}
/*Parte a  sinistra homepage*/
#thirdy .post_it ul li a, #thirdy .bits ul li a {
    color: #048a65 !important;
}
#thirdy .post_it ul li time, #thirdy .bits ul li time {
    color: #088daa !important;
}


  /*DATE*/
  article.comics .all-week , .single-post-new article header.entry-header .cat .intera , .single-post-new article.comics header.entry-header h1.entry-title span , article.archive .entry-content .date {
    color: #FFFAFA !important;
} 
  #content {
    background-color: #000E0B !important;
}
 

 /* FB e twitter + liveblog */
._5pat {
    background-color: #6a8381 !important;
}
.SandboxRoot {
    color: #25c5c5 !important;
}
.liveblog-entry .liveblog-meta .liveblog-time .time a {
    color: #e2ff15 !important;
}
.liveblog-entry .liveblog-entry-text blockquote {
    background: #02231b !important;
}
.liveblog-entry {
    border-top: 1px solid #017C74 !important;
}
.liveblog-entry .liveblog-meta ul.liveblog-social li a .fa-facebook {
    background-color: #0E4A63 !important;
    color: #FFF !important;
}
.liveblog-entry .liveblog-meta ul.liveblog-social li a .fa {
    color: #43B5CA !important;
    border: 1px solid #25665B !important;
}
.liveblog-entry .liveblog-entry-text-title a , #ilcommento .lnk_comm a, .single-post-new footer#new-colophon .subscribe .sub-abstract{
    color: #87a61b !important;
}
.liveblog-entry::after {
    border-right: 15px solid #000 !important;
}

 /*UPDATE*/
article .lastRecents ul li a {
    color: #6fa59f !important;
}
.wp-embed {
    color: #4b7176  !important;
    background: #082c39  !important;
    border: 1px solid #07481a  !important;
}
.EmbeddedTweet--mediaForward .EmbeddedTweet-tweet {
    border: 1px solid #193d57 !important;
}
}
