css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.forocoches.com") {

  
   /* General */
  *
  {
    border-radius: 0!important;
    border-color: #333!important;
    background: transparent!important;
    text-shadow: none!important;
    box-shadow: none!important;
  }
  
  body 
  {
    background: #eee!important;
  }
  
  div, ul, li
  {
    border: none!important;
  }
  
  /* paginación */
  ul.ulpagenav li
  {
    background: #ddd!important;
    border: none!important;
    padding: 1% 0!important;
  }
  
  input[type="text"],input[type="password"], 
  textArea, iframe 
  {
    background: white!important;
    border: 1px solid #aaa!important;
  }
  
  input.button
  {
    text-transform: uppercase!important;
    font-size: 13px!important;
    background: #444!important;
    color: white!important;
  }
  input.button:hover
  {
    background: #333!important;
  }
  
  a 
  {
  }
  
  a:hover
  {
    color : #5284da!important;
  }
  
  /* botones */
  a.mobile_button, a.mobilebuttonbottom 
  {
    background: #5284da!important;
    text-transform: uppercase!important;
    font-size: 11px!important;
  }
  
  a.mobile_button:hover
  {
    color: white!important;
    background: #387df5!important;
  }
  
  ul 
  {
   border: 2px solid #ddd!important;  
  }
  
  /* excepción ul sin bordes en listado de hilos */
  h1.encabezado+div+ul
  {
    border : none!important;
  }
  ul li 
  {
    background: #ddd!important;
  }
  
  /* Solo hilos */
  h1.encabezado+div+ul li
  {
    margin-bottom : 5px!important;
    font-size: 14px!important;
  }
  
  ul li>img+a
  {
    color : #333!important;
  }
  
  /* barra superior */
  div.mobiletitle,  div.mobiletitle a 
  {
    color : #eee!important;
    background: transparent!important;
  }

    div.mobiletitle
  {
    background: url('https://d13yacurqjgara.cloudfront.net/users/13449/screenshots/1828176/lakeside_sunset.png') no-repeat center bottom!important;
    background-size: 100%!important;
  }
  
  /* caja de búsqueda */
  div.mobiletitle input[type="text"],
  div.mobiletitle input[type="password"] 
  {
   padding: 5px!important;
   border: none!important;
  }
  
  /* botones de búsqueda */
  div.mobiletitle input.cbutton
  {
   padding: 4px!important;
   margin: 15px 5px 0 5px!important;
   background: #5284da!important;
   border: none!important;
   color : white!important;
  } 
  
  div.mobiletitle form
  {
    color: white!important;
  }
  
  /* último mensaje de hilo */
  div.asmvinfo 
  { 
    margin-top: 7px!important;
    padding-top: 5px!important;
    border-top: 1px dotted #999!important;
  } 
  
  div.asmvinfo *
  {
   color : #cc3300!important;
  } 

  /* caja comentario */
  div#postlist * 
  {
    border: none!important;
  }
  
  /* cabecera comentario */
  div.posthead
  {
  }
  
  /* fecha comentario */
  span.postdate
  {
    color : #666!important;
  }
  
  /* nombre user comentario */
  a.ui-link
  {
  }
  
  /* cuerpo comentario */
  li.link
  {
   background: rgb(204,204,204)!important; /* Old browsers */
   background: -moz-linear-gradient(-45deg, rgba(204,204,204,1) 0%, rgba(221,221,221,1) 70%)!important;/* FF3.6-15 */
  }
  
  /* comentario destacado*/
  /*op*/
  ul[style="border-left: 3px solid #ca3415; border-right: 3px solid #ca3415;"]
  {
   border-left : 2px solid #a50000!important;
   border-right : 2px solid #a50000!important;
  }
  
  /*usuario*/
  ul[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"]
  {
   border-left : 2px solid #1a53ff!important;
   border-right : 2px solid #1a53ff!important;
  }
  
  /* acciones de comentario*/
  div[style="margin-top: 8px; border-top:1px solid #dddddd;"]
  {
    padding-top: 3px!important;
    border-top: 1px dotted #999!important;
  }

  /* cita */
  div.pre-quote 
  {
    border-width: 1px 1px 0 1px!important;
    border-style: solid!important;
    border-color: #777!important;
    background : #d7d7d7!important;
    border-radius: 10px 10px 0 0!important;
  }
  
  div.post-quote
  {
    border-width: 0 1px 1px 1px!important;
    border-style: solid!important;
    border-color: #777!important;
    background : #ebebeb!important;
    border-radius: 0 0 10px 10px!important;
  }
  
  /* notificaciones */
  div[style="text-align:left"]>
  div[style="padding:0px 6px 0px 4px"]>ul.ul2
  {
    border: none!important;
  }
  
  /* notificaciones sin alerta */
  div[style="text-align:left"]>
  div[style="padding:0px 6px 0px 4px"]>ul.ul2 *
  {
    text-align: center!important;
    border: none!important;
    background: #ddd;
    color : black!important;
  }
  
  /* notificaciones con alerta */
  ul.ul2[style="background-color:#ffffcc;"] * 
  {
   background: #F7943B!important;
  }
  
  /* spoiler */
  div.spoiler
  {
    background: #ccc!important;
    color: #ddd;
  }
  
  span[style="color:#afafaf; font-weight:normal;"]
  {
    color : #3d6eff!important;
  }
  
  
  /* Botón scroll up fijo */
  /*a[href="#top"]:before
  {
    margin: 0!important;
    padding: 0!important;
    text-align: center!important;
    background: transparent!important;
    position: fixed!important;
    content: url('http://i.imgur.com/GEa3xDo.png')!important;
    width: 20%!important;
    bottom: 0px!important;
    left: 40%!important;
    opacity: .5!important;
    
  }*/
}
