css:
@-moz-document domain("www.syfy.com"), domain("www.spot.im"), domain("spoxy-shard3.spot.im") {
div.view-content, span.teaser__content-title-inner, .four-up-breaker__color--blue, .news-feed__content, .pane-content, div.ql-editor.ql-blank, .ql-snow {
    Background-color: Black !important;
}

.teaser__content-title {
    font-size: 30px;
    line-height: 36px;
    color: #ebff00 !important;
}

.teaser .teaser__content-authors, .four-up-breaker__color--blue .teaser__content-author, main#main li > a, main#main p:nth-child(n) > a, 
div.field.field-name-field-sfw-listicle-title.field-type-text.field-label-hidden, .TweetAuthor-name {
    color: #bd0606 !important;
}

main#main li > a {
    font-size: 70px;
    padding-left: 250px;
}

.quote-breaker__color--blue, .quote-breaker__color--blue .quote-breaker__quote {
    background-color: #0e4688;
}

.four-up-breaker__color--blue .teaser__time-ago, .ql-editor {
    color: #777 !important;
}

.fonts-all-loaded .teaser.teaser--four-up-breaker .teaser__content-title, a.article-strip-teaser__content-title {
    color: #ebff00 !important;
}
    
.teaser.teaser--four-up-breaker .teaser__content-info {
    background-color: #353030;
    padding-top: 5px;
    padding-left: 5px;
    border-radius: 5px;
    font-size: 10px;
    font-weight: bold;
}

.blog-post__body-copy p, p {
    color: #05ce04;
}

main#main p:nth-child(n) > a, .blog-post__body-copy a {
    color: #ebff00 !important;
    background-image: linear-gradient(transparent 13px,#ff0000 0) !important;
}

.sp_rich-editor .sp_panel .sp_buttons {
    background-color: #ebff00;
}

.sp_text-entity {
    color: #ebff00;
}

.sp_message-details .sp_user-link .sp_username {
    color: #35b840;
}

.sp_badge {
    color: #0080ff;
}

.sp_message-reply, .sp_message-share-link, .sp_votes-count {
    color: #307fe2 !important;
}

.sp_message-details {
    color: #ff4d00 !important;
}

.fonts-all-loaded .teaser.teaser--four-up-breaker .teaser__content-title, a.article-strip-teaser__content-title {
    color: #000000 !important;
}


.four-up-breaker__color--blue .teaser__content-author, .four-up-breaker__color--light-blue .teaser__content-author, .four-up-breaker__color--green .teaser__content-author, .four-up-breaker__color--lime .teaser__content-author, .four-up-breaker__color--yellow .teaser__content-author {
    color: white  !important;
}

.four-up-breaker__color--blue .teaser__content-info, .four-up-breaker__color--blue .teaser__time-ago, .four-up-breaker__color--light-blue .teaser__content-info, .four-up-breaker__color--light-blue .teaser__time-ago, .four-up-breaker__color--green .teaser__content-info, .four-up-breaker__color--green .teaser__time-ago, .four-up-breaker__color--lime .teaser__content-info, .four-up-breaker__color--lime .teaser__time-ago, .four-up-breaker__color--yellow .teaser__content-info, .four-up-breaker__color--yellow .teaser__time-ago {
    color: Red  !important;
}

.four-up-breaker__color--blue .teaser__content-shares-count, .four-up-breaker__color--blue .teaser__content-comments-count, .four-up-breaker__color--light-blue .teaser__content-shares-count, .four-up-breaker__color--light-blue .teaser__content-comments-count, .four-up-breaker__color--green .teaser__content-shares-count, .four-up-breaker__color--green .teaser__content-comments-count, .four-up-breaker__color--lime .teaser__content-shares-count, .four-up-breaker__color--lime .teaser__content-comments-count, .four-up-breaker__color--yellow .teaser__content-shares-count, .four-up-breaker__color--yellow .teaser__content-comments-count {
    color: #ebff00 !important;
}

main#main div.four-up-breaker__color.four-up-breaker__color--blue > div.four-up-breaker > div.four-up-breaker__teasers > div:nth-child(n) > div.teaser__content > div > a {
    color: #ebff00 !important;
}

.ql-snow, .sppre_rich-editor .sppre_panel .sppre_buttons {
    background-color: black;
}

.sppre_rich-editor.sppre_expanded .sppre_rich-editor-container .ql-editor, .sppre_rich-editor.sppre_expanded .sppre_rich-editor-container .sppre_input-text, .sppre_rich-editor.sppre_expanded .sppre_rich-editor-container .sppre_input-text:empty:before, .sppre_rich-editor.sppre_expanded .sppre_rich-editor-container .sppre_input-wrapper {
    min-height: 84px;
    background-color: black;
}

.EmbeddedTweet-tweet, div#twitter-widget-1, .EmbeddedTweet {
    background-color: #000 !important;
}

.teaser.teaser--four-up-breaker .teaser__content-inner {
    background-color: #2196F3;
}
}
