css:
@-moz-document domain("animenewsnetwork.com") {
p, span.postbody {   
    color: #777 !important;
   }

h1, h2, h3, h4, h5, h6, td.quote {
    color: darkred !important;
}

a:visited {
    color: #c71232 !important;
}

a:hover, b {
    color: darkgreen !important;
}

a:active {
    color: yellow !important;
}
			
td {
    background: black !important;
}

div.top-area {
    background-color: #000000!important;
}

div.middle-area {
    background-color: #000000!important;
}

ul.filters {
    background-color: #000000!important;
}

div.view-mode {
    background-color: #000000!important;
}

#sidebar .box {
    width: 316px;
    margin: 0 0px 0px 0;
    background-color: black;
}

#sidebar .options-menu {
    background-color: black;
}

form.search input {
    background-color: rgb(16, 16, 16);
}

body.grid-mode-not-1 .grid-view .mainfeed-section .herald.box .preview:before {
    background-image: none  !important;
}

div.postdetails {
    color: goldenrod !important;
}

body {
    color: #777171 !important;
}

.php-css .gen, .php-css .genmed, .php-css .gensmall {
    color: #ab1212;
}

.notice {
    color: #090;
    background: #021b73;
}

.cse .gsc-control-cse, .gsc-control-cse {
    background-color: #000000;
    border: 1px solid #2d50a7;
}

form.search .search-suggest li {
    background: #000;
}

form.search .search-suggest li.selected {
    background: #2d50a7;
}

input.gsc-input  {
    background-color: #2d50a7 !important;
    color: white; 
}

.gs-result .gs-snippet {
    color: #d6d6d6;
}

.php-css .my-anime-rating, .php-css .encyc-interactive  {
    background-color: #000000; 
}

.php-css TABLE.layout TD.horizontal {
    height: 35px;
    background-image: url(http://www.animenewsnetwork.com/assets/aab83b13fa3cf12ec3972f59b47fcf05d21bdf48.gif) !important;
}

.php-css TABLE.layout TD.vertical {
    width: 35px;
    background-image: url(http://www.animenewsnetwork.com/assets/253717e319fd624907d7eaa4f8a789ee37c79ae2.gif) !important;
}

.php-css TABLE.layout TD.left-side, .php-css TABLE.layout TD.left-side DIV  {
    width: 28px;
    background-position: 0px 0px;
}

#header #searchbox input  {
        color: #2d50a7; 
}

.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, .gsc-webResult.gsc-result:hover
 {
    border-color: #FFFFFF;
    background-color: #000000;
}
}
