css:
@-moz-document url-prefix("http://togetter.com/li/") {
.header_navigator, #header, [class*="advertise"], .right,
.side_category, .ad_custom, form, .side_box, #footer, .title_box,
.amazon_reco_box, .simple_list, .ad_full_custom, #gotop,
#social_floating_box, #gototop, [id*="kouhaku"], ins, .ad_list_top
{ display: none !important; }

body {
  color: #333 !important;
  background-image: none;
  background-color: #E8F4F2 !important;
}
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
        background: #1b1b1b !important;
    border: inset 0px #222742 !important;
  }

body:before {
  background-image: none !important;
}
.contents {
  margin: 2em auto !important;
}
.contents, .contents .contents_main, .contents .left {
  float: none !important;
  width: 800px !important;
  padding: 20px !important;
}
.contents .contents_main {
  background-color: inherit !important;
  border: 0 !important;
  width: auto !important;
}
.contents .left {
  width: auto !important;
}
.tweet_wrap {
  border: 1px solid #444 !important;
}
.comment_box .comment {
  border: 1px solid #444 !important;
  padding: 5px !important;;
}

/* Tweet box */
.tweet_box .icon_48,
.tweet_box .icon_73 {
  width: 30px;
  height: 30px;
  border-radius: 5px;
}
.tweet_box .list_tweet_box {
  padding-left: 43px;
}
.tweet_box .status {
  display: none;
}
.tweet_box .list_tweet_box .tweet_wrap {
  padding: 8px;
}
.tweet_box .list_box {
  margin-bottom: 5px;
}
.tweet_box .user_link strong,
.tweet_box .user_link .status_name {
  color: #ddd;
}
.tweet_box .timestamp {
  color: #ddd;
}
.tweet_box .tweet {
  line-height: 1.5em;
}
.tweet_box .tweet a[data-origin-url^="http://twitter.com"],
.tweet_box .tweet a[data-origin-url^="https://twitter.com"] {
  color: #75AEBD;
  font-size: smaller;
  font-weight: 300;
}
.tweet_box .tweet_quote {
  display: none;
}

/* #Hashtag */
a[href*="twitter.com/search"] {
  color: #A1BCDD !important;
}
.balloon_body {
  box-shadow: 0 1px 0 #444 !important;
}
}

@-moz-document url-prefix("http://togetter.com") {
ins, .editor_recommend, #fixed_header,
.ad_rectangle
{ display: none !important; }
}
