@-moz-document domain("outlook.live.com"),  domain("outlook.office.com"), domain("outlook.office365.com") {
/*
 * Outlook.com Tidy v4.0.3 (2016-07-27)
 * by BoffinbraiN
 */


/* Outlook.com version 2016 */


/* Hide right panel and footer */

#primaryContainer> :last-child {
	right: 0 !important;
}

/* Allow reading pane to go to the far right */

._n_h {
	display: none !important;
}

/* Hide element containing ads and adblocker message */


/* Message List Header */

._n_V .ms-font-xxl {
	font-size: 20px;
	line-height: 40px;
}

/* Sensible font size on folder header */

._n_V ._n_01 {
	margin-top: -3px;
}

/* Adjust position of folder header checkbox */

._n_r1, ._n_a1 {
	border: none;
}

/* Remove left border from elements touching the left panel */


/* Compact Inbox */

._lvv_t {
	height: auto !important;
	padding-bottom: 6px;
}

/* Message row */

._lvv_t ._lvv_x {
	height: 100%;
}

/* Unread message marker bar */

._lvv_t .ms-font-l {
	font-size: 12px;
}

/* Reduce line 1 font size */

._lvv_t ._lvv_D {
	height: auto;
	padding-top: 5px
}

/* Pull up line 1 */

._lvv_t ._lvv_y {
	top: -3px;
}

/* Pull checkbox up */

._lvv_t ._lvv_G {
	margin-top: 0;
}

/* Pull up line 2 */

._lvv_t ._lvv_L {
	display: none;
}

/* Hide line 3 */


/* Compact Folder Lists */

.subfolders ._n_i4 {
	height: auto;
}

/* Auto height on folder name */

.subfolders ._n_a4 {
	padding-top: 0;
}

/* reduced padding */

.subfolders ._n_f4 {
	top: 1px;
}

/* Align unread message count */

.subfolders ._n_94 {
	padding-left: 0;
}

/* Reduce left indent */

.subfolders ._n_74 {
	padding-left: 2px;
}

/* Reduce left indent further */


/* Compact Message Pane */

._rp_U4, ._rp_w2 {
	margin-left: 0;
}

/* Remove left indent from main body and misc headers */


/* Outlook.com version 2015 */


/* Hide right panel and footer */

#RightRailContainer, .FooterContainer {
	display: none !important;
}
#MainContent {
	right: 0 !important;
	bottom: 0 !important
}
.WithRightRail {
	right: 0 !important;
}

/* Compact Inbox */

.InboxTableBody>li {
	height: auto !important;
	font-size: 90% !important;
}
.InboxTableBody>li>* {
	height: auto !important;
	padding-top: 1px !important;
}
.InboxTableBody .Ck {
	position: relative !important;
	top: 1px !important;
}
.InboxTableBody .Fm img {
	margin-top: 1px !important;
}

/* Compact Folder Lists */

ul.LeftNav>li>* {
	min-height: 0 !important;
}
ul.LeftNav>li>*>span {
	line-height: 1.3em !important;
	top: -2px !important;
}

/*
 * Sleek style for Outlook.com (2017-04-15)
 * by falconer
 */


/* NEW OUTLOOK.COM */


/* color */

#O365_NavHeader:not([style]) .ms-bgc-tp:not(:hover), #O365_NavHeader:not([style]).o365cs-base.o365cst.o365cs-topnavBGColor-2, #O365_NavHeader:not([style]).o365cs-base.o365cst .o365cs-topnavLinkBackground-2 {
	background-color: #0072C6 !important;
}

/* UI cleanup */

a#O365_MainLink_Logo,
/* logo */

.o365cs-nav-brandingText,
/* logo */

#o365cs-flexpane-overlay,
/* 1px vertical space in top right */

div[style="position: absolute; top: 50px; right: 0px; bottom: 0px; left: auto; height: auto; width: 165px;"],
/* 2016 right-side bar */

div[style="position: absolute; top: 50px; right: 0px; bottom: 0px; left: auto; height: auto; width: 160px;"],
/* 2017 right-side bar */

#owaadbar,
/* advertisements */

.o365cs-nav-topItem button[title="Give feedback"],
/* feedback button */

div[role="menu"] button[aria-label="RSS Feeds"],
/* RSS folder */

div[role="menu"] button[aria-label="RSS Subscriptions"], div.o365cs-mfp-header-displayname,
/* first/last name in header */

div.hiddenOverflow>div:last-child>div:last-child>div:first-child>a.o365buttonOutlined
/* Upgrade to Premium button */

{
	display: none !important;
}

/* some dot to the left of to Skype icon */

.o365cs-nav-header16 .o365cs-nav-pinnedAppsContainer {
	border: none !important;
}

/* 2017 account name/icon area */

.o365cs-nav-header16 .o365cs-mfp-headerbutton {
	width: 51px !important;
	min-width: 51px !important;
}
div.o365cs-mfp-header {
	position: absolute !important;
	top: 0 !important;
	right: 2px !important;
}

/* 2016 account popup menu */

.o365cs-me-contextMenu {
	position: relative !important;
	left: -1px !important;
	padding-right: 0 !important;
}

/* popup menus from top right */

.o365cs-nav-rightMenus, .o365cs-nav-contextMenu.contextMenuPopup {
	top: 50px !important;
}

/* push main view all the way to the right */

#primaryContainer>div:last-child {
	right: 0 !important;
}

/* entire Folders and Categories section */

.folderPaneGradient {
	background: none !important;
}
div[aria-label="Mail"]>div:first-child div.customScrollBar.scrollContainer>div>div>div {
	position: relative !important;
	left: 2px !important;
}

/* Folders and Categories headers */

.treeHeaderContainer {
	margin-top: 2px !important;
	margin-bottom: 2px !important;
}

/* new folder button */

.treeHeaderContainer .newNodeBtn {
	top: 0 !important;
	padding-right: 23px !important;
}

/* Folders items */

.subfolders div[role="treeitem"]>span {
	padding-left: 14px !important;
}

/*
 * Adds indent to subfolders:
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/1
 */

.subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 28px !important;
}
.subfolders .subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 42px !important;
}
.subfolders .subfolders .subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 56px !important;
}
.subfolders .subfolders .subfolders .subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 70px !important;
}
.subfolders .subfolders .subfolders .subfolders .subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 84px !important;
}
.subfolders .subfolders .subfolders .subfolders .subfolders .subfolders .subfolders div[role="treeitem"]>span {
	padding-left: 98px !important;
}

/* Junk Email count and unread counts */

.subfolders div[role="treeitem"]>span>div>div+span+span+div * {
	font-weight: 200 !important;
}

/* Categories header */

.__Microsoft_Owa_CategoryTree_templates_cs_6 .treeHeaderContainer {
	margin-top: 6px !important;
	margin-bottom: 0 !important;
}

/* Categories items */

.__Microsoft_Owa_CategoryTree_templates_cs_4 {
	padding-left: 56px !important;
}

/* folder big header */

div[aria-label="Mail list"] .folderHeadContainer {
	margin-top: 0 !important;
	margin-right: -2px !important;
}

/* filter drop-down next to folder big header */

div[aria-label="Mail list"] .folderHeadContainer>div:last-child {
	top: 9px !important;
}

/* separator between message list and message */

div[role="separator"][aria-orientation="vertical"].vResize {
	border-left: 1px solid #e5e3e3 !important;
}

/* message big header */

div[aria-label="Reading Pane"] div.ms-font-xl {
	margin-top: 2px !important;
}

/* logo */

a.o365cs-nav-appTitle.o365cs-topnavText.o365button {
	width: 205px !important;
	height: 50px !important;
	background-size: 169px 28px !important;
	background-position: 18px 11px !important;
	margin: 0 0 0 -4px !important;
	padding: 0 !important;
	/* border-right: 1px solid #529dd7 !important; */
}

/* a.o365cs-nav-appTitle.o365cs-topnavText.o365button:hover {
	background-color: #104a7d !important;
} */


/* OLD OUTLOOK.COM */


/* name */

div#c_cb0>div#c_cme>span#c_me {
	border-left: 1px solid rgba(255, 255, 255, 0.3) !important;
	margin-left: 12px !important;
}
div#c_cb0>div#c_cme>span#c_me>span.c_melnkspan>a#c_melink>span.c_mename>span.c_meun {
	padding: 2px 6px 0 6px !important;
}

/* new message */

div#c_cb0>ul.c_cc>li>a#NewMessage>span.is_c {
	position: relative !important;
	top: -1px !important;
	padding-right: 2px !important;
}

/* logo */

div.c_clogo {
	border-right: 1px solid rgba(255, 255, 255, 0.3) !important;
}
div.c_clogo>h1>a#c_clogot>span.c_clogoimg>span.is_c>img.is_img {
	width: 166px !important;
	height: 34px !important;
	background-size: 169px 28px !important;
	background-position: 0 3px !important;
}

/* BOTH NEW & OLD */


/* font smoothing */

body {
	-webkit-font-smoothing: subpixel-antialiased !important;
}

/*
 * Outlook.com logo
 *
 * Replaces the Outlook.com logo PNG with a SVG:
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/4
 */

div.c_clogo>h1>a#c_clogot>span.c_clogoimg>span.is_c>img.is_img, a.o365cs-nav-appTitle.o365cs-topnavText.o365button {
	background-repeat: no-repeat !important;
	background-image: url('data:image/svg+xml;base64,') !important;
}

/*
 * Removes "Upgrade to Premium" button
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/7
 */

._n_15 {
	display: none !important;
}
._n_X4._n_05 {
	bottom: 40px !important;
}

/*
 * Removes unclickable areas at the top and bottom of the folder pane
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/14
 */

.__Microsoft_Owa_CategoryTree_templates_cs_6{
	cursor: default !important;
}
._n_f4, ._n_g4 {
	display: none !important;
}
}

@-moz-document domain("outlook.office.com"), domain("outlook.office365.com") {
/*
 * Shows Office 365 branding text on Office 365:
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/3
 *
 * Hides the branding text until Office 365 has figured out what the text and url should be:
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/6
 */

a#O365_MainLink_Logo[href], a#O365_MainLink_Logo[href] .o365cs-nav-brandingText, a#O365_MainLink_Logo[href]+.o365cs-nav-appTitleLine {
	display: inline-block !important;
}

/*
 * Corrects Office 365 header starting color:
 * https://github.com/EIGHTFINITE/outlook-com-sleek-tidy/issues/5
 */

#O365_NavHeader:not([style]) .ms-bgc-tp:not(:hover), #O365_NavHeader:not([style]).o365cs-base.o365cst.o365cs-topnavBGColor-2, #O365_NavHeader:not([style]).o365cs-base.o365cst .o365cs-topnavLinkBackground-2 {
	background-color: #001A72 !important;
}
}
