css:
@-moz-document url(http://www.vi.nl/), domain(vi.nl) {

body, .block-holder, .dbItemFactsHolder {
  background-color: #1f1f1f !important;
}
.mainContentWrapper {
  background-color: #f9f9f9 !important;
}

aside, .dossierBlockHolder, .footer, #media-teaser-block, .specialPagesCollectionHolder, .newsListHolder .newsList_medium li .icon-comments, .newsListHolder .viewTypes, .reactionSection, .pointer, nav ul.navigation li a.active .activeIcon, #nextArticle, #previousArticle, .adblock, .c-premium-promo-home.c-premium-promo-home--desktop {
  display:none !important;
}

.mainContentHolder {
  width: 840px !important;
}
.mainContent {
  padding-left:0px !important;
  padding-bottom: 20px !important;
}
.block-holder {
  margin-left: 0px !important;
}
.newsListHolder {
  background-color: #f9f9f9 !important;
  padding: 30px 70px 0px !important;
  margin:0 !important;
}
.newsListHolder .newsHolder {
  margin-bottom:0px !important;
  border-bottom: 1px solid #d6d6d6 !important;
}
.newsListHolder .newsList_medium li {
  width: 575px !important;
}
#morenewsBtnContainer {
  border-top:none !important;
  margin: 20px auto 0 auto !important;
  box-shadow:none !important;
}
.moreNewsBtn:hover {
  background-color: #0098d8 !important;
  border: 1px solid #037eb3 !important;
}
.newsListHolder .moreNewsBtnHolder {
  width:700px !important;
}
a.more_btn_tablet {
  background:none !important;
  border: 1px solid #0583bd !important;
  background-color: #009fe3 !important;
  padding: 3px 19px 4px 20px !important;
  text-decoration: none !important;
  border-radius: 4px !important;
  font: 700 18px "Source Sans Pro", sans-serif !important;
  color: #fff !important;
  text-shadow: 1px 1px #07a !important;
}
.fullWidthContentHolder, 
.mainContentHolder, 
.mainContentHolderTablet {
  box-shadow:none !important;
  border-bottom:none !important;
  overflow:hidden !important;
}
.article-wrapper {
  background-color: #f9f9f9 !important;
  margin-left:0px !important;
}
article {
  padding: 0 130px 40px !important;
  margin: 40px auto 0 !important;
}
.socialBlock, .authorSection {
  width:700px !important;
}
.matchCenterTop {
  margin-left: -60px !important;
}
.standardFullWidthBlock, .pageTabsMenuHolder, .pageLevel2MenuHolder {
  width: 840px !important;
}
table.matchOverview tr.gameWeek {
  margin-left: 60px !important;
}
.playerStatsContent .matchOverview td.homeClubLogo {
  padding-left: 60px !important;
}
#fixture_matches_overview.matchOverview td.homeClubLogo {
  padding-left: 0px !important;
}

.topHeader, .bottomHeader {
  box-shadow:none !important;
  border-bottom: solid 1px #383838 !important;
}

}
