css:
@-moz-document url-prefix('http://www.goatlings.com/battle/challengers') {
    #content input[type="submit"] {
    width: 300px;
    height: 200px;
    border: 1px solid black;
    border-radius: 5px;
    background-color: #acd;
    box-shadow: 2px 2px #aaa;
    font-weight: 800;
    font-size: 20px;
    color: #444;
    letter-spacing: 1.6px;
    word-spacing: 10px;
    }

}

@-moz-document url-prefix('http://www.goatlings.com/battle/thebattle') {
    a[href="http://www.goatlings.com/battle/over"] {
    font-size: 40px;
    color: #66a3cc;
    word-spacing: 10px;
    }
}
