css:
@-moz-document domain("godoc.org"), regexp("^https?://(blog.)?golang.org.*$"), regexp("^https?://localhost:6060.*$") {
html,
body,
body div#page div.container h2 {
  background: #181818 !important;
  color: #c0c0c0 !important;
  line-height: 1.4
}
body div#topbar,
body nav,
body nav div.container ul li.active a,
body div#x-footer {
  background-color: transparent !important;
}
body div#topbar div#heading a,
body nav div.container ul li.active a {
  color: #375EAB !important;
}
h1,
h2,
h3,
h4 {
  color: inherit !important;
  font-weight: normal !important;
}
h1 {
  text-transform: lowercase;
}
.image img {
  background-color: white;
}
pre .ln {
  background-color: transparent;
  color: inherit;
}
body div#page div.container div.input,
body div#page div.container div.output,
body div#page div.container div.code,
body div#page div.container div#sidebar,
body div.container div#x-projnav,
body div.container pre,
body div.container div.panel,
input,
.well,
.jumbotron {
  background-color: #202020 !important;
  border-radius: 3px;
  border: 1px solid rgba(255, 255, 255, .1) !important
}
input {
  color: #c0c0c0 !important;
}
body div.container pre,
body nav,
body div.container div.panel,
body div#x-footer {
  border: none !important;
}
body div.container pre, .play textarea.code {
  background: #002b36!important;
  color: #839496!important;
  border: 1px solid rgba(255, 255, 255, .1) !important;
  border-radius: 3px;
}
body h3.highlighted,
body h4.highlighted {
  background-color: #4C004C !important;
}
body div.play textarea {
  color: #DDD;
}
#page>div.container {
  margin: auto calc((100vw - 960px)/2);
}
th,
td,
table,
hr {
  border-color: #484848 !important
}
a {
  color: #4183C4 !important;
  border: none !important;
  background-color: transparent !important;
}
}
