css:
@-moz-document domain("androidfilehost.com") {
h4,h3,a p,a p{
    color:#e5e5e5;
}
body,#what-we-offer {
	color: #777;
	background-color: #333;
}
.navbar-default {
	border-color: #222;
	background-color: #333;
}
.breadcrumb,
header .navbar,
.panel,
.input-group .form-control,
.list-group-item,
.thumbnail,
.dropdown-menu,
.timeline-Widget,
.folder-info .f-title,
.folder-nav .navbar-brand,
.folder-nav .navbar-sort {
	background-color: #181818;
}
.list-group-item {
	border: 1px solid #777;
	color: #777;
}
.badge {
	color: #181818;
}
.btn-default {
	color: #0F0F0F;
	background-color: #333;
	border-color: #0f0f0f;
}
.btn-default:hover,
.btn-default:focus,
.btn-default.focus,
.btn-default:active,
.btn-default.active,
.open > .dropdown-toggle.btn-default {
	color: #0F0F0F;
	background-color: #5D5D5D;
	border-color: #0f0f0f;
}
.input-group .form-control {
	border: 1px solid #181818;
}
.form-control:focus {
	border-color: #a4c639;
	outline: 0;
	-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgb(164, 198, 57);
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgb(164, 198, 57);
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus,
.dropdown-menu .divider,
.panel-default > .panel-heading,
.footer {
	background-color: #0F0F0F;
}
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:hover,
.navbar-default .navbar-nav > .open > a:focus {
	background-color: #333;
}
.thumbnail {
	border: 1px solid #0f0f0f;
}
.pagination > li > a,
.pagination > li > span {
	background-color: #333;
	border: 1px solid #181818;
}
.pagination > .disabled > span,
.pagination > .disabled > span:hover,
.pagination > .disabled > span:focus,
.pagination > .disabled > a,
.pagination > .disabled > a:hover,
.pagination > .disabled > a:focus {
	background-color: #333;
	border-color: #181818;
}
.panel-default {
	border-color: #0f0f0f;
}
.nav-tabs {
	border-bottom: 1px solid #333;
}
.nav-tabs > li > a:hover {
	border-color: #333 #333 #0F0F0F;
	color: #a4c639;
}
.nav > li > a:hover,
.nav > li > a:focus {
	text-decoration: none;
	background-color: #333;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
	background-color: #333;
	border: 1px solid #333;
}
.panel-default > .panel-heading {
	border-color: #333;
}
.dropdown-menu {
	border: 1px solid #cccccc;
	border: 1px solid rgba(0, 0, 0, 0.15);
}
.folder-info .folder-icon,
.browse-site-section-mini,
.browse-site-section {
	display: none;
}
.file-attr-div .file-attr span .file-attr-label {
	color: #737373;
}
.list-group.files .list-group-item .file-name h3 a,
h3,
h1,
.h1,
h2,
.h2,
h3,
.h3,
.list-group.other-devices .list-group-item a {
	color: #2a92c3
}
.browse-site-section .browse-site-section-mini {
	background: none !important;
	background-image: none !important;
}
.toolbar {
	margin-bottom: -15px;
	float: left;
}
.developer-info {
	padding: 0px;
	border-bottom: none;
}
.developer-info .info h4 {
	position: relative;
	left: 125px;
	bottom: 32px;
}
.folder-info,
.folder-nav .navbar-brand {
	margin: 0px 0 0;
	position: relative;
	left: 515px;
	color: #2a92c3;
}
.folder-info {
	top: 5px;
}
.folder-nav .navbar-brand {
	left: 655px;
}
.folder-nav .navbar-sort {
	top: 2px;
	position: relative;
	right: 159px;
}
.navbar-default .navbar-brand:hover,
.navbar-default .navbar-brand:focus {
	color: #333;
	background-color: #181818;
}
.folder-nav {
	padding: 0px;
	border-bottom: none;
}
.page-container h1 {
    margin: 0 0 0px;
}
.list-group.files .list-group-item .file-download-col,.list-group.pop-files .list-group-item .file-downloads {
    border-left: solid 1px #333;
}
.page-container .right-sidebar {
    border-left: solid 2px #181818;
}
.page-container > .col-md-8 {
    border-right: none;
}
.list-group.files .list-group-item .file-download-col a small {
    color: #ccc;
}
/*Theme Icons not being the same color*/
.file-attr .img-responsive{
    filter: invert(100%);
}
.file-attr-div.row div:nth-child(1) img,.fa{
    filter: invert(70%);
}
.list-group.mirrors a h3 span::before{
    filter: invert(70%);
}
.list-group.mirrors .list-group-item,#mirrors {
    background: #585858;
}
.list-group.mirrors .list-group-item:hover{
    background: #6a6a6a;
}
}
