/* i really want this to be global */
[class*="fc_cont"], [class*="nav"]:not([style*="url"]), [class*="item"]:not(a):not([style*="url"]), [id*="wrap"]{background: transparent !important}

[id*="internal"], [class*="rmenu"]:not([style*="url"]), [class*="wr_header"]:not([style*="url"]), [class*="_link"]:not([style*="url"]), [class*="post"]:not([style*="url"]),  [class*="page"]:not(button):not([style*="url"]):not([class*="ico"]),  :not(html):not(body):not(a):not(video):not(img):not(input):not(button):not(textarea):not(ymaps):not(select):not(svg):not([src]):not([onmousedown]):not(i):not([style*="url"]):not([contenteditable=true]):not([class*="album"]):not([class*="oto"]):not([class*="image"]):not([class*="img"]):not([class*="cover"]):not([class*="video"]):not([class*="zoom"]):not([class*="ico"]):not([class*="ink"]):not([class*="Code"]):not([class*="home"]):not([class*="_vis"]):not([class*="profile"]):not([class*="popup"]):not([class*="fl_"]):not([class*="slider"]):not([class*="volume"]):not([class*="progres"]):not([class*="chats_sp"]):not([class*="_x"]):not([class*="wk_arrow"]):not([class*="_btn"]):not([id*="bgvideo"]){background: transparent}

[class*="has_action"], [class*="activ"]:not([style*="url"]):not([class*="ico"]):not([class*="stl_"]):not([class*="_menu_"]), [class*="shown"]:not([class*="page"]):not([class*="post"]):not([class*="layers"]):not([class*="feed"]) div:not([style*="url"]):not([class*="ico"]):not([class*="layers"]):not([class*="feed"]), [class*="_vis"]:not([style*="url"]):not([class*="action"]):not([class*="bar"]), [class*="popup"] div:not([style*="url"]):not([class*="ico"]):not([class*="_x"]), [class*="notify"]:not([style*="url"]), [onmousedown]:not([style*="url"]):not(input):not(a):not([class*="icon"]):not([class*="post"]):not([class*="wk"]), [class$="_box"]:not([style*="url"]), #wk_box{background: #232 url(https://source.unsplash.com/random/1280x720) center /cover fixed !important;}

textarea, select, input, code, [contenteditable=true], [class*="layers"], #mv_box{background: rgba(0,0,0,.3) !important;}
[class*="unread"], [class*="button"]:not([style*="url"]):not([class*="action"]):not([class*="_x"]), button:not([style*="url"]):not([class*="im"]):not([class*="audio"]){background: rgba(255,255,255,.2) !important;}
[class*="Code"], [onmouseover]:not([style])>div:not([style]):not([class*="ico"]), [class*="info"]:not([style*="url"]):not(div), [class*="stl_"]:not([style*="url"]), [class*="select"]:not([style*="url"]):not(button):not([class*="wk"]), li[class*="nim-dialog"]:hover, a:hover:not([style*="url"]):not([class*="image"]):not([class*="_cog_"]), nav:hover:not([style*="url"]):not([class*="image"]), menu:hover:not([style*="url"]):not([class*="image"]){background: rgba(0,0,0,.2) !important}
body {margin: 0; background: rgba(0,0,0,.1) !important; z-index: -99 !important;}
html{ background: #232 !important; background: url(https://source.unsplash.com/random/1280x720) center /cover fixed !important; z-index: -100 !important;}

:not(p):not(a):not(span):not(h1):not(h2):not(h3):not(h4){color: #ddc !important; text-shadow: 0 1px #221, 0 2px #221, 1px 0 #221, 2px 0 #221, 1px 1px #221, 2px 2px #221, 1px -1px #332, -1px 1px #332, -1px -1px #443, -1px 0 #332, 0 -1px #332, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
p, :not(a)>span>span:not([class^="cm-"]), div>span:not([class^="cm-"]){color: #bb8 !important; text-shadow: 0 1px #110, 0 2px #110, 1px 0 #110, 2px 0 #110, 1px 1px #110, 2px 2px #110, 1px -1px #221, -1px 1px #221, -1px -1px #332, -1px 0 #221, 0 -1px #221, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
.cm-s-default, .cm-atom, .cm-builtin {color: #99e !important; text-shadow: 0 1px #110, 0 2px #110, 1px 0 #110, 2px 0 #110, 1px 1px #110, 2px 2px #110, 1px -1px #221, -1px 1px #221, -1px -1px #332, -1px 0 #221, 0 -1px #221, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
h1, h2, h3, h4{color: #d77 !important; text-shadow: 0 1px #300, 1px 0 #300, 1px 1px #300, 0 2px #300, 2px 0 #300, 2px 2px #300, 1px -1px #411, -1px 1px #411, -1px -1px #522, -1px 0 #411, 0 -1px #411, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
a, a *, menu *, nav *, menu, nav{color: #8bc !important; text-shadow: 0 1px #012, 1px 0 #012, 1px 1px #012, 0 2px #012, 2px 0 #012, 2px 2px #012, 1px -1px #123, -1px 1px #123, -1px -1px #234, -1px 0 #123, 0 -1px #123, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
a:hover, a:hover *, button:hover, [class*="button"]:hover{color: #ea8 !important; text-shadow: 0 1px #410, 1px 0 #410, 1px 1px #410, 0 2px #410, 2px 0 #410, 2px 2px #410, 1px -1px #521, -1px 1px #521, -1px -1px #632, -1px 0 #521, 0 -1px #521, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
:visited{color: #a8b !important; text-shadow: 0 1px #102, 1px 0 #102, 1px 1px #102, 0 2px #102, 2px 0 #102, 2px 2px #102, 1px -1px #213, 1px -1px #213, -1px 1px #213, -1px -1px #324, -1px 0 #213, 0 -1px #213, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
::selection{color: #da5 !important; background: #25b !important;}

/* ads */
yatag *, #ads_left *, #friends_possible_block *{height: 0 !important; visibility: hidden !important}

/* Vk.com */
#page_layout{padding: 0 0 !important;}

div, img{border-radius: 3px !important}
ul li[data-list-id^="20000"] span{color: #c66 !important; text-shadow: 0 1px #100, 1px 0 #100, 1px 1px #100, 0 2px #100, 2px 0 #100, 2px 2px #100, 1px -1px #211, -1px 1px #211, -1px -1px #322, -1px 0 #211, 0 -1px #211, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
ul>li[class*="im_out"] div{color: #6a6 !important; text-shadow: 0 1px #010, 1px 0 #010, 1px 1px #010, 0 2px #010, 2px 0 #010, 2px 2px #010, 1px -1px #121, -1px 1px #121, -1px -1px #232, -1px 0 #121, 0 -1px #121, 0 0 0.5em black, 0 0 1em black, 0 0 1em black !important}
