css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(ftp://), url-prefix(file://) {
  
/*Text & Background*/


html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h4, h5, h6, form, footer, .module-header .middle-container, .module-header .bottom-container, .featured-post-row, .nav-bar, header.main-header, nav.navbar.navbar-static-top, section.content, div.box-header.with-border, div.box-body, aside.main-sidebar, span.logo-lg, a.logo, div.wrapper, div.form-group, div.input-group, input.form-control, div.input-group-addon, div.box-footer, textarea.form-control, a, select.form-control.input-sm, section.content, div.content-wrapper, button
   {
     color: #C0C0C0 !important;
     background: #111 !important;
   }

/*Whitelist Button*/
  
i.fa.fa-pencil-square-o
  {
    color: green !important;
}

/*Blacklist Button*/
  
i.fa.fa-ban
  {
    color: red !important;
}

/*Statistics Text*/
  
h3#dns_queries_today.statistic, h3#ads_percentage_today.statistic, h3#domains_being_blocked.statistic, h3#ads_blocked_today.statistic, div.inner p
  {
    color: #111 !important;
  }
  
/*Headings Text*/
  
h3.box-title
  {
    color: #999 !important;
  }
  
/*Progress bars*/
  
div.progress.progress-sm
  {
    background: #111 !important;
  }
  
/*Table column headers*/
  
table.table.table-bordered tr th
  {
    color: #999 !important;
  }

/*Links*/
  
a

  {
    color: #0040ff !important;
  }

}
