css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("c-sharpcorner.com") {

  
  /* Body */
  body{
    color: #ccc;
    background: #222;
  }
  
  .recentactivityBox .right h3 a{
    color: #ccc;
  }
  /* end Body*/
  
    /* Menu */
  .dropDownMenu{
    background: #333;
    color: #ccc;
    
  }
  .dropDownMenu div span span a,
  .category .articlesRow .HeadingAr a{
    color: #ccc;
    
  }
  .featuredBox{
    background: #333;
  }
  .featuredBox li .detail a{
    color: #ccc;
    
  }
  /* LogIn */
  .loginPopup.loggedIn ul{
    background: #333;
  }
  .loginPopup.loggedIn li a{
    color: #ccc;
    
  }
  /* End LogIn */
  
  /* End Menu */
  
  
  /* Main */
  .main{
    background: #222;
  }
  .PaddingLeft5{
    color: #ccc;
  }
  .HeadingBold,
  .headerGray, .headerControls{
    color: #06c;
    
  }
  .article .detail h1.grayText,
  .breakingNewBox li .detail p{
    color: #ccc!important;
  }
  
  /* End Main */
  
  
  /* Right Panel */
  .numbers{
    background: none;
  }
  
  /* End Right Panel */
  
  /* Forum */
  .askQuestion{
    background: #333; 
  }
  .askQuestion .searchTextBox{
    background: #444;
    color: #ccc;
  }
  .LinkNormalGray,
  ul.ForumPostHeadNormal li a{
    color: #ccc;
  }
  ul.latestArticle li.GrayBackground, ul.latestArticle li:nth-child(2n){
    background:#444;
  }
    ul.latestArticle li.GrayBackground, ul.latestArticle li:nth-child(2n +1){
    background:#555;
  }
  /* End Forum */
  
}
