  }

  #srchfield, .searchbox, #pbweather{
    border: 0px #222742 !important;    
    padding: 0 !important;    
    margin: 0 !important;    
  }
  #srchtxt, input, textarea, button{
    border: solid 1px #444 !important;   
    color: #ccc !important; 
    background:#333 !important;   
  }
  a{
    color: #eee !important;
    text-decoration: none !important;
  }
  a:hover{
    color: #D2691E !important;
  }
}
