css:
@-moz-document domain("se7ensins.com") {
/*Header Logo*/
#logo img {
    max-width: 370px;
    width: 0;
    height: 90px;
    padding-right: 385px;
    background: transparent url(http://i.imgur.com/rPWB0EV.png) top left no-repeat;
}
/*Media Category Strip*/
.xmgCarouselContainer .sectionMain .titleStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    margin: 0 0 7px;
    border-radius: 5px;
    min-height: 6px;
    margin-bottom: 10px;
}
/*Category Strip*/
.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    margin: 0 0 7px;
    border-radius: 5px;
    min-height: 6px;
}
/*Post Template Strip*/
.discussionList .sectionHeaders {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    padding: 0;
}
/*Message Heading Strip*/
.messageHeading {
    font-size: 13px;
    color: #FFF;
    font-weight: bold;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    padding: 9px 10px;
    overflow: hidden;
    border-radius: 3px;
    margin-bottom: 5px;
}
/*NavTabs*/
html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(255, 255, 255);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
}
html .navTabs .navTab.PopupOpen .navLink {
    color: rgb(234, 234, 234);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
}
.navTabs {
    font-size: 13px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    background-color: rgb(26, 26, 26);
    padding: 0 25px;
    border-top: 6px solid rgb(26, 26, 26);
    border-bottom: 6px solid rgb(158, 0, 18);
    height: 40px;
}
.Menu {
    font-size: 12px;
    border: 0px solid rgb(26, 26, 26);
    border-top: 5px solid rgb(158, 0, 18);
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}
/*Sidebar*/
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(235, 235, 235);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(158, 0, 18);
    border-radius: 3px;
}
.mast .subHeading, .navigationSideBar .subHeading, .navigationSideBar .heading {
    background: rgb(26, 26, 26);
    border-bottom: 3px solid rgb(158, 0, 18);
    border-radius: 3px 3px 0px 0px;
}
.secondaryContentCollapsed h3 {
    font-weight: bold;
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(235, 235, 235);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(158, 0, 18);
    border-radius: 3px;
}
/*Footer*/
.extraFooter {
    font-size: 12px;
    color: rgb(126, 126, 126);
    background-color: rgb(26, 26, 26);
    padding: 15px;
    border-top: 6px solid rgb(158, 0, 18);
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    display: block;
    overflow: hidden;
}
/*Buttons*/
.button {
    font-style: normal;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 5px;
    border: 1px solid rgb(158, 0, 18);
    border-radius: 5px;
    text-align: center;
    outline: none;
    line-height: 26px;
    display: inline-block;
    cursor: pointer;
    box-sizing: border-box;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    height: 28px;
}
a.callToAction {
    padding: 0px;
    border: 1px solid rgb(158, 0, 18);
    border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ca001e 0%,#9e0012 100%);
    display: inline-block;
    line-height: 28px;
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    outline: 0 none;
    height: 28px;
}
/*Ratings Bar*/
.dark_postrating_bar_positive {
    height: 10px;
    background: #9e0012;
    display: inline-block;
    opacity: 1.0;
    font-size: 0;
}
/*Links*/
.primaryContent a {
    color: rgb(235,235,235);
}
a:link, a:visited {
    color: rgb(235,235,235);
    text-decoration: none;
}
/*Userstyle by Art @ Se7enSins*/
}
