css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.evasiongames.com") {

.container, .itemlist__item{
	background-color: #222!important;
}
.itemlist__item:nth-child(even){
	background-color: #333!important;
}
.itemlist__item .item-title, .st-itemlist__item .item-title, .topictitle, .item-stat__coun, .forum-title{
	color: #fafafa!important;
}
.username, .item-stat__count{
	color: #bababa!important;
}
.nav-breadcrumbs{
	background-color: #931405!important;
    color: #fafafa!important;
}
#cp-box, #topicreview, .attachbox, .faqlinks__block, .forum-moderators--block, .hoverbox, .layout-boxed .nav-breadcrumbs, .layout-stretched .breadcrumbs-bar, .layout-stretched .detached .nav-breadcrumbs{
	background: #222!important;
    background-color: #222!important;
}
#page-body{
	background-color:#222!important;
}
.nav-breadcrumbs .crumb--home:before{
    color:#fafafa!important;
}
.display-options{
	color:#fafafa!important;
    background-color:rgb(65, 69, 75)!important;
}
.display-options .fancyInput--select, .display-options select{
	color:#3cb2fc!important;
}
.btn-success{
	background-color:#0dbc4a!important;
}
.utility-bar.navbar-dark, .utility-bar navbar-dark clearfix{
    background-color:#000!important;
}
.page-footer__before{
	background:#222!important;
}
.content, .page-title, .posting-title{
    color:#fafafa!important;
}

#tabs>ul>.activetab>a, #tabs>ul>.activetab>a:hover, .panel, .st-panel{
	color:#fafafa!important;
    background:#333!important;
}
.postbody .content, .quickreply textarea, .sceditor-container iframe, .sceditor-container textarea, .st-editor__box #message, .st-editor__box textarea{
	background:#333!important;
}
.sceditor-container, .message-box, .sceditor-container ltr, div.sceditor-toolbar
{
	padding-bottom:6px!important;
    color:#fafafa!important;
    background:#222!important;
}
.sceditor-grip{
    background:#222!important;
}
.form-control, .inputbox, input[type=password], input[type=search], input[type=text], select{
	color:#222!important;
}
body{
	color:#fafafa!important;
}
.page-footer .copyright{
	background:#222!important;
}
.fancyInput--select .fancyInput__icon{
	filter:invert(100%)!important;
}
.postAuthorAlign-right .postprofile, .search .postprofile{
	background:#222!important;
}
.post:hover .icon_post_target, .post-buttons .edit-icon:before{
	color:#00fff2!important;
}
.postAuthorAlign-right .postprofile, .search .postprofile{
	border-width: 0 0 0 0px!important;
}
.sidebar-widget, .widget-header, #widget-statistics p, .fi-chart-bar::before, #widget-statistics p>strong{
    color:#fafafa!important;
	background:#333!important;
}
#cp-navigation .cp-nav-list #active-subsection a, .cp-main{
	color:#fafafa!important;
	background:#333!important;
}
