	color:#fafafa!important;
}
.table1 th, .table1 tr:first-child td, .st-itemlist__item .item-inner{
	color:#fafafa!important;
	background:#333!important;
}
.fancyInput--select, .form-control, .inputbox, input[type=password], input[type=search], input[type=text], select, p:last-child{
	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.table1 tr:nth-child(even) td{
	color:#fafafa!important;
	background:#222!important;
}
.table1 tr:nth-child(odd) td{
	color:#fafafa!important;
	background:#333!important;
}
.table1 tr:first-child td{
	color:#fafafa!important;
	background:#111!important;
}
ul.topiclist li:first-child{
	color:#fafafa!important;
	background:#333!important;
}
li.header dt{
	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.mark{
	color:#fafafa!important;
	background:transparent!important;
}
div.sceditor-grip{
	background:#111!important;
}
.input-group-btn:last-child>.btn, .input-group-btn:last-child>.btn-group, span.btn.btn-default{
	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.userprofile-panel{
	color:#fafafa!important;
	background:#333!important;
}
.notifications-dropdown .dropdown-header, .notifications-dropdown .notification-reference, .notifications-dropdown .dropdown-footer>a, .dropdown-header>strong, .utility-bar.navbar-dark .dropdown-menu, .dropdown-menu>li>a{
	color:#fafafa!important;
	background:#333!important;
}
.notification-item
{
   	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.faq-title{
	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.faqlinks__link{
	color:#fafafa!important;
	background:#333!important;
}
.faqlinks__block__title, .faqlinks__block__title:after{
	color:#fafafa!important;
	background:#222!important;
}
.quicksearch .form-control{
	color:#fafafa!important;
	background:#333!important;
    border-style: solid;
    border-width: 1px!important;
    border-color: #00fff2!important;
}
.et_pb_blurb_content, .et_pb_section, #main-footer, #footer-widgets .footer-widget li a, #main-header, .et_pb_section_0.et_pb_section, .et_header_style_left #et-top-navigation nav > ul > li > a, .et_header_style_split #et-top-navigation nav > ul > li > a, .et_pb_blurb h4, .et_pb_text > :last-child, .et_pb_gutters3 .et_pb_column_1_3 .et_pb_module, .et_pb_gutters3.et_pb_row .et_pb_column_1_3 .et_pb_module, .et_section_specialty .et_pb_gutters3.et_pb_row .et_pb_column_1_3 .et_pb_module, .et_section_specialty .et_pb_row .et_pb_column_2_3 .et_pb_gutters3.et_pb_row_inner .et_pb_column_1_3 .et_pb_module, .et_pb_testimonial:before{
	color:#fafafa!important;
	background:#222!important;
}
#logo{
	filter:invert(100%)!important;
}
#et_search_icon:hover, .mobile_menu_bar:before, .mobile_menu_bar:after, .et-social-icon a:hover, .comment-reply-link, .form-submit input, .entry-summary p.price ins, .woocommerce div.product span.price, .woocommerce-page div.product span.price, .woocommerce #content div.product span.price, .woocommerce-page #content div.product span.price, .woocommerce div.product p.price, .woocommerce-page div.product p.price, .woocommerce #content div.product p.price, .woocommerce-page #content div.product p.price, .woocommerce .star-rating span:before, .woocommerce-page .star-rating span:before, .woocommerce a.button.alt, .woocommerce-page a.button.alt, .woocommerce button.button.alt, .woocommerce-page button.button.alt, .woocommerce input.button.alt, .woocommerce-page input.button.alt, .woocommerce #respond input#submit.alt, .woocommerce-page #respond input#submit.alt, .woocommerce #content input.button.alt, .woocommerce-page #content input.button.alt, .woocommerce a.button, .woocommerce-page a.button, .woocommerce button.button, .woocommerce-page button.button, .woocommerce input.button, .woocommerce-page input.button, .woocommerce #respond input#submit, .woocommerce-page #respond input#submit, .woocommerce #content input.button, .woocommerce-page #content input.button, .woocommerce a.button.alt:hover, .woocommerce-page a.button.alt:hover, .woocommerce button.button.alt:hover, .woocommerce-page button.button.alt:hover, .woocommerce input.button.alt:hover, .woocommerce-page input.button.alt:hover, .woocommerce #respond input#submit.alt:hover, .woocommerce-page #respond input#submit.alt:hover, .woocommerce #content input.button.alt:hover, .woocommerce-page #content input.button.alt:hover, .woocommerce a.button:hover, .woocommerce-page a.button:hover, .woocommerce button.button, .woocommerce-page button.button:hover, .woocommerce input.button:hover, .woocommerce-page input.button:hover, .woocommerce #respond input#submit:hover, .woocommerce-page #respond input#submit:hover, .woocommerce #content input.button:hover, .wp-pagenavi span.current, .wp-pagenavi a:hover, .et_password_protected_form .et_submit_button, .nav-single a, .posted_in a, #top-menu li.current-menu-ancestor > a, #top-menu li.current-menu-item > a, .bottom-nav li.current-menu-item > a, .footer-widget h4{
