css:
/*
 https://gist.github.com/account-login/0c5b7cc6e097e6402851ecfd2c71426f
*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
  html {
    background-color: lightyellow;
  }

  body > center {
    text-align: left;
  }

  /* global background */
  #hnmain {
    background-color: inherit;
  }

  /* begin header */
  #hnmain > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    background-color: inherit;
  }

  #hnmain > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) {
    max-width: 580px;
    background-color: #ff6600;
    box-shadow: -1px 1px 6px 0px black;
  }
  /* end header */

  /* begin comment box */
  #hnmain > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > table:nth-child(1) {
    width: 580px;
    background-color: #f6f6ef;
    box-shadow: -1px 1px 6px 0px black;
  }

  /* number of comments and age */
  .subtext a[href^="item?id="] {
    font-weight: bold;
  }

  .subtext .score, .subtext .age {
    font-weight: bold;
  }

  form[action=comment] > textarea {
    width: 525px;
  }
  /* end comment box */

  /* submit button */
  input[type=submit] {
    background-color: #fd0;
    border: none;
    border-radius: 5px;
    transition: all 200ms;
  }

  input[type=submit]:hover {
    background-color: #FF6600;
    box-shadow: -1px 1px 2px 0px black;
  }
  /* end submit button */

  /* br after comment box */
  #hnmain > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > br:nth-child(2) {
    display: none;
  }

  /* begin comment */
  .default p {
    margin-top: 16px;
  }

  code {
    max-width: 500px !important;
    overflow: scroll !important;
  }

  .comment {
    max-width: 500px !important;
    background-color: #f6f6ef;
    padding: 10px;
    line-height: 1.8;

    box-shadow: -1px 1px 4px 0px black;
    transition: all 0.2s linear;
  }

  .comment:hover {
    box-shadow: -2px 2px 8px 0px black;
  }

  #hnmain > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > br:nth-child(5) {
    display: none;
  }

  .comment-tree + br {
    display: none;
  }

  /* reduce indent */
  img[src='s.gif'][width='40'] { width: 25px; }
  img[src='s.gif'][width='80'] { width: 50px; }
  img[src='s.gif'][width='120'] { width: 75px; }
  img[src='s.gif'][width='160'] { width: 100px; }
  img[src='s.gif'][width='200'] { width: 125px; }
  img[src='s.gif'][width='240'] { width: 150px; }
  img[src='s.gif'][width='280'] { width: 175px; }
  img[src='s.gif'][width='320'] { width: 200px; }
  img[src='s.gif'][width='360'] { width: 225px; }
  img[src='s.gif'][width='400'] { width: 250px; }
  img[src='s.gif'][width='440'] { width: 275px; }
  img[src='s.gif'][width='480'] { width: 300px; }
  img[src='s.gif'][width='520'] { width: 325px; }
  img[src='s.gif'][width='560'] { width: 350px; }
  img[src='s.gif'][width='600'] { width: 375px; }
  img[src='s.gif'][width='640'] { width: 400px; }
  img[src='s.gif'][width='680'] { width: 425px; }
  img[src='s.gif'][width='720'] { width: 450px; }
  img[src='s.gif'][width='760'] { width: 475px; }
  img[src='s.gif'][width='800'] { width: 500px; }
  img[src='s.gif'][width='840'] { width: 525px; }
  /* end comment */

  /* begin bottom */
  #hnmain > tbody > tr:last-child > td > * {
    max-width: 580px !important;
  }

  #hnmain > tbody > tr:last-child > td > br {
    display: none;
  }

  #hnmain > tbody > tr:last-child > td > center {
    background-color: #f6f6ef;
    padding-top: 16px;
    padding-bottom: 4px;

    box-shadow: -1px 1px 6px 0px black;
  }
  /* end bottom */

  /* begin main page */
  .itemlist {
    padding: 10px;
  }
  /* end main page */

  /* anchors */
  a:hover {
    text-decoration: underline !important;
  }

  /* text input */
  input[type=text], input[type=password], textarea {
    background-color: white;
    border: none;
    box-shadow: inset -1px 1px 3px 0px black;

    padding: 2px 5px 2px 5px;
  }
}
