css:
@-moz-document domain("chat.zalo.me") {
#app {
	max-width: 400px;
	border-right: 1px solid;
}
nav {
	max-width: 63px;
}
.header-btn {
	margin: 0px 8px;
}
.avatar-online-dot:after {
	top: 6px;
	right: -7px;
}
.tab-red-dot:after {
	width: 13px;
	height: 13px;
	top: -30px;
	right: -24px;
}
#headerBtns {
	position: absolute;
	left: 318px;
	top: 10px;
}
body,
#chatView,
.flx-col,
.zl-scroll,
.item,
header,
.side-header,
#contact-search,
#contact-search.show-guide,
#aboutMe,
#aboutMe-popover,
#chatInput,
#aboutMe,
#emojiSelector>.heading-group,
.chat-notify-alert,
#messageSearch,
aside,
#itemSuggestPopover,
#headerBtns,
header,
.title.header-title,
.subtitle.header-subtitle,
#chatInputTools,
.chat-input-content-wrapper,
.chat-input-content-wrapper.highlight,
#main-tab,
#richInput.empty::before,
.chat-input__content,
.chat-input__content.highlight {
	background-color: #1E1E1E;
	/*root background color*/
}
#headerBtns,
.title.header-title,
.subtitle.header-subtitle,
.shadow-white {
	background: none;
	-webkit-box-shadow: 0 0 7px 12px #1e1e1e;
}
.card.me,
.card,
.item.selected,
.item:hover,
.selected .flx-col,
.item:hover .flx-col,
.item.active,
.item.active .flx-col,
.item-setting:hover,
.aboutMe-subsection:hover,
.btn-txt.image-show-btn:hover,
.btn-txt.modal-close:hover,
.btn.btn-txt:hover,
.item-sticker-cat:hover,
.chat-notify-alert:hover,
#messageSearch .flx-fix input,
.item-sticker:hover,
.active-bg,
.messageSearchResult-item.active,
.item.selected .chat-timestamp,
.item.selected .func-mute,
.item.selected .func-pinned,
.item.selected .func-setting,
.item.selected .item-message,
.item.selected .item-timestamp,
.modal-dialog,
.modal-dialog-title,
.chat-info__header,
.messageView__typing {
	background-color: #2d2d30 !important;
	/*second backgourd color*/
}
.chat-timestamp,
.subtitle {
	color: #494949;
}
.posPinBL:before {
	border-left: 6px solid transparent;
	border-right: 6px solid transparent;
	border-top: 6px solid #2d2d30;
}
* {
	border-color: #2d2d30 !important;
}
.card.first-msg.me:after {
	border-color: #2d2d30 transparent transparent;
}
.card.first-msg:before {
	border-color: transparent #2d2d30 transparent transparent;
}
.chat-item.flx .avatar,
#contact-search.show-guide #search-guide,
.avatar--xm,
.avatar--s,
.avatar--l {
	display: none;
}
.chat-message-picture,
#chatView .chat-message,
#sendBtn,
.chat-date,
#richInput-container,
.rich-input {
	opacity: 0.3;
}
div::-webkit-scrollbar,
.fa-gif:before,
.fa-paperclip:before,
.fa-image:before,
.fa-info-circle:before,
.fa-smile-o:before,
.title,
.typing,
.item-sticker,
#input_line_0 {
	opacity: 0.5;
}
.chat-notify-alert {
	opacity: 0.6;
}
.item.selected .item-timestamp {
	background: none;
}
header {
	background-image: none;
}
#chatView .chat-message,
.item-title,
.side-header,
#contact-search.show-guide input,
#chatInfo div,
#chatInfo span,
#contact-search-input,
#messageSearch .flx-fix input,
aside,
#richInput-container,
.title,
.modal-dialog-title span,
.rich-input {
	color: white;
}
.card--sticker img {
	height: 60px;
}
.chat-message-picture {
	height: 100px !important;
}
.item-sticker-cat {
	border: none;
}
.zl-hscroll::-webkit-scrollbar,
div::-webkit-scrollbar {
	width: 3px;
}
div::-webkit-scrollbar-thumb {
	background: #343638;
}
#contact-search.show-guide input {
	font-size: 16px;
}
.chat-item {
	margin: 0 2px 0 2px;
	padding: 0;
}
.chat-status {
	padding: 5px 0 5px 0;
}
.chat-message-container,
#richInput-container span {
	font-size: 13px;
}
.chat-status {
	color: rgb(72, 78, 80);
	font-size: 10px;
}
aside {
	position: absolute;
	width: 334px;
	border-bottom: 1px solid;
}
.input.flx-cell {
	border: none;
}
.avatar--m>.avatar-img {
	opacity: 0.1;
}
.thumb {
	width: 86px;
	height: 86px;
}
nav:hover {
	/*max-width: 450px;*/
}
nav {
	transition: max-width 200ms 100ms;
}
nav:hover .avatar--m>.avatar-img {
	opacity: 1;
}
.flx-sp-btw text[style~="color"] {
	color: rgb(1, 143, 229);
}
#chatInput {
	margin-top: 5px;
}
#emojiSelector {
	width: 340px;
}
.popover {
	margin-left: -13px;
}
.posPinBL:before {
	left: 20px;
}
#itemSuggestPopover {
	width: 100px;
	overflow-x: auto;
	display: block;
}
.item-title text[style] {
	color: #60a3ea !important;
}
.aboutMe-subsection {
	padding: 5px;
	border-radius: 3px;
}
#loginPageMain div {
	background-color: #fafafc;
}
.emoji-img.emoji-img-large {
	width: 20px;
	height: auto;
	padding: 14px;
}
#main-tab {
	display: block;
}
#composeBtn {
	position: inherit;
}
.btn.btn-dark,
.btn-dark.image-show-btn {
	color: white;
}
div#aboutMe-popover div {
	color: white;
	margin-left: 3px;
}
#contact-search {
	height: 65px;
	position: relative;
}
main,
.main__center.dib.fl-l {
	width: 337px;
}
.card.card--sticker .sticker {
	width: 60px !important;
	height: 60px !important;
}
}
