css:
@-moz-document domain("minerva.ugent.be") {
#sitelogo {
    display: none;
}

@media screen and (max-width: 1200px) {    
#sidebar {
    display: none;
}
    
#content > div {
    margin-right: 0px;
}
}

.notifications .icon {
    margin: 0px 4px;
}

.notifications a:hover, .notifications a:focus, .notifications a:active {
    border-bottom: none;
}

.user_course_category_wrapper .course {
    border-bottom: 1px solid #e9f0fa;
}

.user_course_category_wrapper .course > .icon img {
    content: url('https://fusionsoft.be/stylish/minerva/book-default.png');
    width: 40px;
    margin-top: -10px;
}


div#toolshortcuts img.icon, div.notifications img.icon {
    background-size: 300% !important;
