css:
@-moz-document url-prefix("http://fontawesome.io/icons/") {
    .container .fontawesome-icon-list a,
    .container .fontawesome-icon-list a .fa {
        height: 120px;
        line-height: 120px;
    }
    .container .fontawesome-icon-list .fa {
        width: 44px!important;
        font-size: 44px!important;
        vertical-align: middle!important;
        color: rgb(50, 50, 50);
    }
    .container .fontawesome-icon-list div a {
        color: #aaa;
        font-family: Helvetica, Arial, sans-serif;
        font-weight: 100;
        font-size: 9pt;
    }
    .container .fontawesome-icon-list div a:hover {
        background: black;
        color: white;
    }
    .container .fontawesome-icon-list div a:hover .fa {
        background: black;
        color: white;
    }
    .container .fontawesome-icon-list div a {
        color: #FFF;
    }
    .container .fontawesome-icon-list a span {
        color: inherit;
    }
    .container:hover .fontawesome-icon-list a {
        color: #888;
    }

    /* take the newsletter out of the way */
    #social-buttons{
        display:none;
    }
    #newsletter {
        opacity: 0.75;
        float: right;
    }
    #newsletter .btn {
        width: auto;
    }

    /*decrease top banner*/
    .jumbotron.jumbotron-ad.hidden-print{
        display: none;
        padding: 5px;
    }
    .jumbotron.jumbotron-ad.hidden-print h1{
        font-size: 24pt;
    }
    .jumbotron.jumbotron-ad.hidden-print p{
        font-size: 10pt;
    }


    /*ad*/
    .stripe-ad{
        zoom: 0.5;
    }
}
