css:
@-moz-document domain("www.furvilla.com") {
body {
    background: #7AA5CD url(http://www.furvilla.com/img/bg.png) no-repeat 50% 0;
    color: #1A2C5B !important;
}
.content {
    background: #3B5BA3;
    color: #DCE7F1;
}
.content h1 {
    color: #C9DAEB;
}
.content h2 {
    color: #C9DAEB;
}
a {
    color: #fff;
}
a:hover, a:focus {
    color: #fff;
    text-decoration: underline;
}
.content p {
    font-size: 16px;
    color: #1E3062;
}
element.style {
    color: #DCE7F1;
}
.widget .forum-posts p,
.widget .forum-posts p a {
    color: #EAF1F7;
}
.thread-user-info {
    background: #203573;
    font-weight: bold;
    text-shadow: -1px -1px 0 #203573, 1px -1px 0 #203573, -1px 1px 0 #203573, 1px 1px 0 #203573;
}
.thread-user-info a {
    text-shadow: -1px -1px 0 #203573, 1px -1px 0 #203573, -1px 1px 0 #203573, 1px 1px 0 #203573 !important;
}
span.tooltipster {
    border-bottom: 1px dashed #FDF7D4;
}
.thread-user-post-top a {
    color: #EAF1F7;
    font-weight: bold !important;
    text-shadow: -1px -1px 0 #203573, 1px -1px 0 #203573, -1px 1px 0 #203573, 1px 1px 0 #203573;
}
.fur-residents-item-info {
    background: #6B9BC8;
    border: 1px solid #EAF1F7;
}
.fur-residents-item-info:hover {
    border: 1px solid #1C2F65;
}
.fur-residents-item-info .durability {
    background: #466CB4;
}
.fur-residents-item-actions {
    background: #1C2F65;
    opacity: 0.9 !important;
    color: #FDF6D9;
}
.fur-residents-item-actions a {
    color: #EAF1F7;
}
footer .logo-small {
    display: none;
}
header .user-panel {
    background: #1C2F65;
    color: #DCE7F1;
}
header .user-panel a {
    color: #EAF1F7;
}
.dropdown-menu {
    background: #3B5BA3;
    color: #EAF1F7;
}
header nav .dropdown-menu a {
    color: #EAF1F7 !important;
}
.widget-header h3 {
    background-image: linear-gradient(#456BB4, #1C2F65);
    color: #EAF1F7;
}
.user-info h4 {
    color: #EAF1F7;
}
.btn {
    background-image: linear-gradient(#456BB4, #1C2F65);
    color: #EAF1F7 !important;
}
.jobs li a {
    background: #456BB4;
    border: 1px solid #1C2F65;
}
.jobs li a:hover {
    border-color: #1C2F65;
    -webkit-box-shadow: 0 0 10px #1C2F65;
    box-shadow: 0 0 10px #1C2F65;
}
.jobs a:hover .counter {
    color: #fff !important;
}
.counter {
    color: #DCE7F1 !important;
}
.mayor-announcements li {
    background: #ECF1F7;
    color: #6E9BC9;
}
.mayor-announcements a {
    color: #B0C8E2;
}
.news-date,
.news-date a {
    color: #DCE7F1 !important;
}
.user-comments .user-comment .footer {
    color: #DCE7F1 !important;
}
.user-comments .user-comment .footer a {
    color: #EAF1F7;
}
.alert.info {
    color: #E9F0F7;
    background: #BED3E8;
}
.forum tr:nth-child(even) td:not(.forum-category) {
    background: #1C2E62 !important;
}
.forum tr:nth-child(odd) td:not(.forum-category) {
    background: #1C2E62 !important;
}
.forum tr:hover td:not(.forum-category) {
    background: #466CB0 !important;
    border-bottom: 1px solid #c4ecf9;
}
.content h1 small {
    color: #E9F0F7;
}
label {
    color: #E9F0F7;
}
.thread-user-post-top {
    color: #E9F0F7;
}
.content:after {
	display: none;
}
p:last-child {
    color: #91B5D9;
}
.table thead, .table tr:hover .table thead {
    background: #213674 !important;
}
.table tr:nth-child(odd) td {
    background: #172751;
    color: #C9DAEB !important;
}
.table tr:nth-child(even) td {
    background: #223C7E;
    color: #C9DAEB !important;
}
.table tr:hover td {
    background: #3F61A9 !important;
}
.messages .user-message p {
    color: #16234F !important;
}
.animal-item-info .name {
    color: #3F61A9 !important;
}
.animal-item-info {
    background: #E4EEF7;
}
.animal-item-info .durability {
    background: #C8DAE9;
}
.modal-content {
    background: #E4EEF7;
}
.input, .ui-datepicker select {
    background: #EFF3F7;
}
html, button, input, select, textarea {
    color: #1A2C5B !important;
}
.textarea {
    background: #EFF3F7;
}
.inventory-item-info, .item-block {
    background: #172550;
}
strong.blue {
    color: #DAE6F2;
}
.header-right-toolbar-box {
    color: #C9DAEB;
}
.green {
    color: #DAE6F2 !important;
}
.red {
    color: #fff;
}
.stats-type {
    display: inline-block;
    width: 33%;
    color: #1A2C5B !important;
}
b, strong {
    color: #1A2C5B !important;
}
}
