css:
@-moz-document domain("3dnews.ru") {
#wrapper {
    background: #333;
}

.white {
    background: #333;
}

#full-view #right-sidebar {
    background: #333;
}

#full-view #left-sidebar {
    background: #333;
}

#content-column {
    color: #bbb;
}

body, html {
    background: #222;
}

.content-block-header {
    background: #111;
}

.content-block-header>.left {
    background: #111;
    color: #bbb;
    text-shadow: none;
}

#top-articles {
    background: rgb(51, 51, 51);
}

.content-block-header .right {
    text-shadow: none;
}

#content-column .content-block-data .header .date {
    text-shadow: none;
}

.entry-body .caption {
    text-shadow: none;
    color: #999!important;
}

.cc .mc-comment-msg, .mc-complaint .mc-comment-msg {
    color: #bbb!important;
}

.cc .mc-comment-username, .mc-complaint .mc-comment-username {
    color: #888!important;
}

}
