css:
@namespace url(http://www.w3.org/1999/xhtml);
/* Notes are made throughout this code so that anyone new to CSS can easily customize aspects of the code and know what they are affecting */
@-moz-document domain("community.stpauls.mb.ca") {
  .community {
		background: #0D0D0D; /* Changes base background to near pitch black */
		}
		
	.TMv_CommunitySidebarNavigation > li {
		background: #0D0D0D; /* Changes Sidebar background to near pitch black */
		border:1px solid #232222;/* Inserts a black/grey border that is lighter than the background to separate the sidebar from the background */
		}
	
	.TMv_CommunitySidebarNavigation > li a:hover {
		background: #505052; /* When hovering over selection in the sidebar, it will become grey */
		text-decoration: none;
		}
	
	.TMv_CommunitySidebarNavigation > li ul a {
		color: #5A0000; /* Found the weird orangy colour annoying, changed it to maroon */
		}
	
	.TMv_CommunitySidebarNavigation > li ul li.current a {
		background: #606063; /* The current selected subcategory in the sidebar is made grey. This is only for subcategories, not base categories */
		}
		
	.TMv_CommunitySidebarNavigation > li  .quantity {
		background: #505052; /* Makes the notifications on the sidebar dark grey. I purposely made this the same colour as the hovering colour */
		}	
	.TMv_SportsTicker {
		background-color: #0D0D0D; /* Background colour of the "sports ticker" is made near pitch black. It's basically a calendar */
		border:1px solid #232222; /* Created a border around the sports ticker subsections that was a lighter black/grey */
		border-top: none; /* Removed border from top */
	}
	.TMv_SportsTicker .sports_ticker_btn {
		color: #9A9A9A;
	}
	.TMv_SportsTicker .sports_ticker_item h4 {
		color: #5A0000;
	}
	.TMv_SportsTicker .sports_ticker_item h5 {
		color: #5A0000;
	}
	.TMv_SportsTicker .sports_ticker_item.today h4 {
		color: #9A9A9A;
	}
		.TMv_SportsTicker .sports_ticker_item.today h5 {
		color: #9A9A9A;
	 }
	.TMv_ContentFeedPreview {
		margin-bottom:10px;
		background: #0D0D0D; /* Made content feed background near pitch black to protect retinas */
		border: 2px solid #232222; /* Border that is a lighter black/grey to separate content feed from background */
		}
.TMv_ContentFeedPreview .preview_footer {
		background:#898989; /* Made the background of the footer on content feeds grey. It's the thing that contains who made this content preview and when it was made */
  	border: 1px solid #232222; /* Lighter black/grey border to separate it from background */
	  border-top: none; /* Removed border from top of the preview footer */
	}
	/* For the next section with just a few characters and a colour. To find out what these characters affect, you'll have to use the Inspector and anything beginning with the same character as the one you're changing should be affected */
	a:hover { 
		color: #E8881F; /* A placeholder for the colour for hover over objects under the code of "a" */
		}
	p 	{
		color: #9A9A9A; /* Changed colour to light grey */
		}
	
	h3 	{
		color: #9A9A9A; /* Changed colour to light grey */
		}	
	div {
		color: #9A9A9A; /* Changed colour to light grey */
	}
	span {
		color: #9A9A9A; /* Changed colour to light grey */
	}
	textarea {
		color: #9A9A9A; /* Changed colour to light grey */
	}
	input {
		color: #9A9A9A; /* Changed colour to light grey */
	}
	th {
		color: #5A0000; /* Changed colour to maroon */
	}
	td {
		color: #5A0000; /* Changed colour to maroon */
	}
	li {
		color: #232222; /* Changed colour to lighter black/grey */
	}

	.TCv_FormItem_FileDrop .file_drop_container {
		background: #292929; /* Changed file drop container to grey instead of white, which it was before */
		}
	i:hover {
		color: #670000;	/* Hovering over any object under "i" is coloured a lighter maroon than normal */
		}	
	i {
		color: #7C7B7B; /* Default colour for any object under "i" is a mid-grey */
	}
	.TMv_SPHS_IconNameBox {
		background: #232222;
		border-color: #232222;
	}
	.TMv_HeaderFlyout {
		background: #0D0D0D;
		border: 1px solid #5A0000;
		border-top: none;
	}
	.TMv_SearchForm #q_container:before {
		color: #9A9A9A;
	}
	.TMv_GroupTournamentScoreTable tr.opponent_row td {
		border-bottom:1px solid #232222;
		}
  .TMv_GroupGameScoreTable th {
   border-bottom: 1px solid #232222;
	 border-right-color: #232222;
	 border-left-color: #232222;
}
.community .TMv_CommunityControlBar .item_option_button {
		color:#858692;
	  background: #0D0D0D;
		}
	.community .TMv_CommunityControlBar .right_button {
		background:#232222;
		}
	.TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li.current_menu a,
	.TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li.parent_menu a {
		color: #9A9A9A;
	}
	.TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li {
		background: #9A9A9A;
	}
	#tungsten_main_content .form_table input[type="password"], #tungsten_main_content .form_table input[type="text"] {
		background: #232222;
	}
	.community #tungsten_main_content .TCv_FormItem_SubmitButton input {
		color: #0D0D0D;
	}
	h2.TCv_Messenger_title {
		color: #9A9A9A;
	}
	.TMv_CommunityHeader #header_controls a .fa {
		color: #505052;
	}
	.community .item_option_button.showing {
		background: #232222;
		border: 1px solid #505052;
		border-bottom: none;
	}
	.community .item_option_menu {
		background: #232222;
		border: 1px solid #505052;
	}
	.community .item_option_menu li a:hover {
		background: #232222;
		}
	.community .TMv_ContentFeedPreview .item_option_button:hover {
		background: #232222;
		border: 2px solid #505052;
	}
	#tungsten_main_content .form_table textarea {
		background: #232222;
	}

	.TMv_AnnouncementsHomepage #navigation a {
		color: #9A9A9A;
	}
	.TMv_AnnouncementsHomepage .category_view .icon {
		color: #9A9A9A;
	}
	.TMv_EventCalendar .background_table .today {
		background: #232222;
	}
	.TMv_EventCalendar .style_game {
		background: #505052;
		color: #5A0000;
	}
	.TMv_EventCalendar .style_ {
		color: #9A9A9A;
	}
	.TMv_CommunityControlBar #top_bar_container.full_width_photo_container h1 {
		background: rgba(80, 80, 82, 0.85); /* As far as I know, the slightly transparent background can be made with rgba only */
	}
	#tungsten_main_content .TMv_ContentCommentCreateView .form_table textarea {
		border: 1px solid #7C7B7B;
	}
	.TMv_ContentFeedPreview .title_link .course_type {
		color: #9A9A9A;
	}
	.TMv_ContentFeedPreview .date_added_view a:hover, .TMv_ContentFeedPreview .content_owners a:hover{
		color: #9A9A9A;
	}
	.sphs_box {
		background: #5A0000;
	}
	a.sphs_box:hover i.link_icon {
		color: #9A9A9A;
	}
	.TSv_ModuleNavigation_SubmenuBar li a:hover {
		background: #232222;
	}
	.TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li:hover a {
		background: #232222;
	}
	.TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li span, .TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li a {
		border-right: 1px solid #232222;
	}
	TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li:first-child span, .TMv_CommunityControlBar .TSv_ModuleNavigation_SubmenuBar li:first-child a {
    border-left: 1px solid #232222;
}
	.community #tungsten_main_content .TCv_Form.filter_form, .community .TCv_List .pagination_controls {
		background: #232222;
	}
	.TCv_List .pagination_controls a.current_page:hover {
		background: #606063;
		color: #9A9A9A;
	}
	.TCv_List .pagination_controls a.current_page {
		background: #9A9A9A;
	}
	.community .TCv_List .pagination_controls a:hover {
		background: #606063;
		color: #9A9A9A;
	}
	.community .TMv_MessageThreadList .message_checkbox:hover {
    background: transparent;
	}
	div.TMv_MessageThreadList a.toggle_recipients_button {
		color: #9A9A9A;
	}
	.form_table input[type="text"] {
		background: #232222;
	}
	.TMv_CommunityHeader #header_controls a.flyout_active{
		background: #232222;
	}
	.TMv_SPHS_SearchIconNameBox .type_info {
		background: transparent;
	}
	.TMv_JiraFeedbackForm .form_table {
		background: #5A0000;
	}
	.form_table textarea {
		background: #232222;
	}
	.TCv_FormItem_Select select {
		background: #232222;
		color: #9A9A9A;
	}
	#tungsten_main_content .form_table .TCv_FormItem_Select select {
		background: #232222;
	}
	.community .TCv_SearchableModelList {
		background: #232222;
	}
	.TMv_ContentPermissionForm .owner_box {
		background: #232222;
	}
	.form_table input[type="submit"] {
		background: #232222;
	}
	.form_table input[type="submit"]:hover {
		background: #2D2C2C; /* Lighter grey for mouse hover */
	}
	.TMv_ContentFileView .download_button {
		color: #9A9A9A;
	}
	.form_table .chosen-container > a {
		background: #232222;
	}
	.chosen-container .chosen-drop, #tungsten_main_content .chosen-container-single .chosen-single{
		background: #232222;
	}
	.chosen-container .chosen-results li{
		color: #9A9A9A;
	}
	li.active-result.result-selected.highlighted {
		color: #9A9A9A;
		text-decoration: underline;
	}
	.chosen-container .chosen-results li.active-result.highlighted {
		color: #9A9A9A;
		text-decoration: underline;
	}
	.TMv_HeaderFlyout .TCv_Menu li:hover{
		background: #505052;
		border: 1px solid #5A0000;
	}
	.TMv_CommunityControlBar h1 span.title_inside a.title_section:hover {
		background: #505052;
	  border: 1px solid #5A0000;
	}
	.community .TCv_SearchableModelList #form_filter_search.filter_active, 
	.community .TCv_SearchableModelList .filter_form select.filter_active {
		background: #232222 !important;
	}
	.TMv_Notification:hover .sphs_box { 
	  background: #232222;
	}
	.TMv_NotificationsFlyout #notification_flyout_control_buttons a:hover {
		color: #9A9A9A;
	}
	.sphs_box a {
		color: #9A9A9A;
		font-weight: 600;
	}
	.TCv_List td {
		border-bottom: 1px solid #232222;
	}
	.TMv_GroupGameScoreTable td {
		border-right-color: #232222;
		border-left-color: #232222;
	}
	#password_container > a:nth-child(2) { 
	color: #5A0000 !important;
	font-weight: 600;
	background: none !important;;
	}
	#TCv_Dialog_content {
		background: #0D0D0D;
	}
	#left_button, #right_button {
		color: #9A9A9A !important;
	}
	#TCv_Dialog #buttons a:hover{
		background: #232222;
		}
}
