css:
@-moz-document domain("app.couple.me") {
#chat-view {
    background: #2f3746;
    border-left: 1px solid #2f3746;
}
.message {
    position: relative;
    margin-top: 10px;
    left: 0px;
    right: 0px;
    overflow: visible;
}
#views-wrapper {
    position: absolute;
    top: 0px;
    bottom: 0px;
    right: 0px;
    left: 0px;
    background: white;
    overflow: hidden;
}
#status-header {
    position: absolute;
    top: 0px;
    left: 0px;
    right: 0px;
    height: 75px;
    background-color: white;
    border-bottom: 1px solid #c6cccf;
    z-index: 100;
    text-align: center;
    overflow: hidden;
}
#chat-view::-webkit-scrollbar {
    width: 5px;
}
#chat-view::-webkit-scrollbar-track {
    background: #fff;
}
#chat-view::-webkit-scrollbar-thumb {
    background: #438eff;
}
.sticker-pack-wrapper::-webkit-scrollbar {
    width: 5px;
}
.sticker-pack-wrapper::-webkit-scrollbar-track {
    background: #fff;
}
.sticker-pack-wrapper::-webkit-scrollbar-thumb {
    background: #438eff;
}
.user .photo-bubble-tail:before {
    border-left: 6px solid #2f3746;
}
.partner .photo-bubble-tail:before {
    border-right: 6px solid #2f3746;
}
.user .photo-bubble:hover .photo-bubble-tail:before {
    border-bottom: 6px solid rgba(0, 0, 0, 0.5);
    border-left: 6px solid #2f3746;
}
.partner .photo-bubble:hover .photo-bubble-tail:before {
    border-right: 6px solid #2f3746;
    border-bottom: 6px solid rgba(0, 0, 0, 0.5);
}
.user .photo-bubble-tail:after {
    border-left: 6px solid #2f3746;
}
#input-footer {
    background: #414154;
    border-color: #000000;
}
.partner .photo-bubble-tail:after {
    border-right: 6px solid #2f3746;
}
.user .photo-bubble:hover .photo-bubble-tail:after {
    border-top: 6px solid rgba(0, 0, 0, 0.5);
    border-left: 6px solid #2f3746;
}
.partner .photo-bubble:hover .photo-bubble-tail:after {
    border-top: 6px solid rgba(0, 0, 0, 0.5);
    border-right: 6px solid #2f3746;
}
.user .bubble {
    background-color: #6d71ef;
}
.user .bubble:before {
    border-color: transparent #6d71ef transparent transparent;
}
.bubble {
    color: #ffffff;
    font-size: 16px;
    border-radius: 2px;
}
.partner .bubble {
    background-color: #438eff;
}
#send-button:hover {
    background-color: #438eff;
    border-color: #438eff;
}
#send-button {
    background-color: #6d71ef;
    border: 1px solid #6d71ef;
    border-radius: 2px;
    -webkit-border-radius: 2px;
}
#status-header {
    background-color: #414154;
    border-bottom: 1px solid #000000;
}
#input-field-table:hover {
    border-color: #6d71ef;
}
#input-field-table {
    background: rgb(255, 255, 255);
    border-color: #c2c9cc;
    border-radius: 2px;
    -webkit-border-radius: 2px;
}
#status-partner-name {
    color: #ffffff;
}
#status-partner-status {
    color: #ffffff;
}
.partner .bubble:before {
    border-color: transparent transparent transparent #438eff;
}
body {
    font-family: 'Didact Gothic', 'Open Sans', sans-serif;
}
.message-timestamp-text {
    color: #ffffff;
    font-size: 12px;
}
#moments-view {
    background: #2f3746;
}
#moments-view::-webkit-scrollbar {
    width: 5px;
}
#moments-view::-webkit-scrollbar-track {
    background: #fff;
}
#moments-view::-webkit-scrollbar-thumb {
    background: #438eff;
}
#moments-wrapper {
    background-color: #2f3746;
}
#load-more-moments-button {
    border: 1px solid white;
    color: white;
}
.photo-bubble-content {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
.photo-bubble-content img {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
.message-receipt {
    font-size: 13px;
    color: white;
}
}
