css:
@-moz-document domain(intra.epitech.eu) {



/*
div.data {
  background: #FFFFFF url('http://i.imgur.com/iZwv1eS.gif') bottom right no-repeat fixed !important;
  border: 0px solid black !important;
}  
*/



div.header-wrapper {
  background-color: #2C3E50 !important;
}

.notices {
  padding: 0px;
}

#header .intra .navigation tr td.module .icon {
    background-position: -43px -96px;
}

#header .intra .navigation tr td.planning .icon {
    background-position: -43px -48px;
}

#header .intra .navigation tr td.user .icon {
  background-position: -43px 0px;
}

#header .intra .navigation tr td.e-learning .icon {
  background-position: -42px -144px;
}

#header .intra .navigation tr td.administration .icon {
    background-position: -43px -192px;
}

#header #language .icon {
    display: none;
}

#header .intra .navigation table {
}

div.intra, div.global {
  background-image: none !important;
}

#header #notification {
  float: left;
  border-radius: 0px 10px 0px 0px;
  background-color: #34495E !important;
}

#header #identification .infos {
  float: left;
  border-radius: 10px 10px 0px 0px;
  background-color: #34495E !important;
}

#header .second-column {
  float: right;
}

.activite .warning {
  animation-name: important;
  animation-duration: 5s;
  animation-iteration-count: infinite;
  border-radius: 4px;
}
@keyframes important{
  from{background-color: #FF9999;}
  50%{background: none;}
  to{background-color: #FF9999;}}
@-webkit-keyframes important{
  from{background-color: #FF9999;}
  50%{background: none;}
  to{background-color: #FF9999;}}

.red.alert, span[title='roadblock at the end of the year'] {
  animation-name: red_alert;
  animation-duration: 5s;
  animation-iteration-count: infinite;
}
@keyframes red_alert{
  from{opacity: 0.1}
  50%{opacity: 1}
  to{opacity: 0.1}}
@-webkit-keyframes red_alert{
  from{opacity: 0.1}
  50%{opacity: 1}
  to{opacity: 0.1}}

div.item.collapsed:hover, div.content.html:hover, div.title:hover, div.links:hover {
  background: none !important;
}

section, div.infos {
   background-color: #C5CAE9 !important;
}

#header .intra .reserved {
  background-color: #22313F !important;
  width: 280px;
  height: 70px;
}

div.view_more {
  background-color: #9FA8DA !important;  
}
  
div.view_more:hover {
  background-color: #C5CAE9 !important;  
}
  
article {
  background-color: #C5CAE9 !important;
}
  
article:hover {
  background-color: #9FA8DA !important;
}

div#sidebar, a.infos, div#notification {
  background-color: #263238 !important;
}

#sidebar .rzone .alert {
  background-color: #000000;
  width: 20px;
  height: 20px;
  border-radius: 25%;
  position: absolute;
  top: 6px;
  left: 235px;
}

#sidebar .rzone .green {
  background: #2ecc71;
  box-shadow: 0 0 12px 4px #2ecc71;
}
  
#sidebar .rzone .orange {
  background: #e67e22;
  box-shadow: 0 0 12px 4px #e67e22;
}
  
#sidebar .rzone .red {
  background: #E3000E;
  box-shadow: 0 0 12px 4px #E3000E;
}
  
#sidebar .title {
  padding: 20px 20px 17px;
  background-color: rgba(0, 0, 0, 0.3);
  margin-bottom: 15px;
}

  
#wall #sidebar .title .picture {
  display: none;
}
  
#sidebar > .title > .icon {
  margin: 0 15px 0 0;
}
  
#sidebar > .title > .label {
  margin: 10px 0 0 0;
  padding: 0;
}
  
#wall #sidebar > .title > .label {
  margin-top: 0;
  display: inline-block;
}

#sidebar .index_sidebar {
  font-size: inherit;
}
  
#wall #sidebar ul {
  margin: 0;
}
  
#wall #sidebar li {
  margin-bottom: 0;
}
  
#wall #sidebar a {
  padding: 10px;
  border-left: 4px solid transparent;
  transition: all .5s;
}
  
#wall #sidebar a:hover {
  border-color: #CFD8DC;
  text-decoration: none;
}
 
#wall #sidebar a .icon {
  margin-left: 0;
  padding-left: 15px;
}
  
#sidebar .rzone {
  margin-top: 15px;
  padding: 0;
}
 
#sidebar .rzone hr {
  border: none;
  height: 1px;
  background-color: rgba(255, 255, 255, 0.2);
}
  
#wall #sidebar .title .picture {
  display: none;
}

#wall #sidebar > .title > .label {
  margin-top: 0;
  display: inline-block;
}

#sidebar > .title > .label {
  margin: 10px 0 0 0;
  padding: 0;
}

#sidebar > .title > .icon {
  margin: 0 15px 0 0;
}

#sidebar {
  width: 280px;
  max-width: 300px;
  min-width: 150px;
  border-right: 0px solid rgba(0, 0, 0, 0.1);
  background-color: #182732;
}
  
#sidebar #toggle-sidebar {
  display: none;
}
  
#breadcrumb {
  margin: 1% 1%;
  padding: 0;
  border-bottom: 1px solid lightgray;
}
  
#wall #breadcrumb {
  padding: 0;
}
}
