@-moz-document domain("qzone.qq.com") {
body {
    color: #FFFFFF !important;
}
.user-avatar,
.head-avatar {
    border: 0px !important;
    border-radius: 10px !important;
}
/* TOP bar */

.music-play,
.music-dynamic {
    background-color: #B2DFDB !important;
}
.music-play:hover,
.music-dynamic:hover {
    background-color: #4DB6AC !important;
}
.top-fix-container {
    background-color: #4DB6AC !important;
    border: 0px !important;
}
.nav-list {
    background-color: #4DB6AC !important;
}
.nav-hover {
    background-color: #B2DFDB !important;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    margin: 5px 0px 0px 0px !important;
}
#tb_ic_li:hover,
#tb_index_li:hover,
#tb_friend_li:hover,
#aIcenter:hover,
#aMainPage:hover,
#aMyFriends:hover {
    background-color: #B2DFDB !important;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}
.user-home:hover,
.logout-new:hover,
.user-setting:hover,
.nav-drop-down,
.nav-drop-down.friends-drop-down {
    background-color: #B2DFDB !important;
}
/* background */

.layout-head,
.layout-nav,
.layout-background {
    background-color: #B2DFDB !important;
    border: 0px !important;
}
/* none display */

.mod-side-nav.mod-side-nav-recently-used,
.collet_box.fn_paipai,
.fn_accessLog_tips.bg2.bor2,
#tab_menu_app,
#tab_menu_mv,
.qz-grid.more-toggle,
.user-vip-info,
.control-inner,
.fn-feed-control-v2.clearfix,
.item-line,
.item.item-sp,
.ui-icon.icon-feed-collect,
.ui-icon.icon-comment,
.ui-icon.icon-forward,
.ui-icon.icon-praise,
.praise.qz_like_prase,
.brand-name,
#tb_vip_li,
#QM_Container_100002,
.divide-line,
.fn-visitor-blocked,
.gb-operation-button.hot-msg,
#vfeed_1765169349_311_c5583669cea55258f2440600___recfeed,
#QM_Container_31,
.icon-new-fun,
#tb_app_li,
#tb_dress_li,
#qz-head-level,
.gb-operation-area,
.icenter-right-mod.icenter-right-ad,
.feed-hot-bar,
#hex_20050606_5000_1_1481937917_0_1,
#hex_20050606_5000_1_1481938094_0_1,
.f-vqz-ad,
#sidebar-ic-fixed,
.feed-bar-normal,
.collet_box.fn_fnrecm.icenter-right-mod.icenter-right-photo,
#fct_2513883391_311_0_1481985393_0_1,
.feed-video-recommend,
#div_corner_ad_flash,
#_pos_close,
#js-qq-ad {
    display: none !important;
}
/* all windows */

.f-single.f-s-s,
.f-op-wrap,
.f-ct.f-ct-b-bg,
.f-detail {
    background-color: #80CBC4 !important;
    border: 0px !important;
}
.f-single.f-s-s,
.f-op-wrap {
    border-radius: 10px !important;
    border: 0px !important;
}
.qz_ichotclick.img-three,
.qz_ichotclick,
.img-box,
.f-ct-txtimg.f-ct-imgtxt.f-ct-b-np {
    border-radius: 5px !important;
    background-color: #B2DFDB !important;
    border: 0px !important;
}
.c_tx.q_namecard,
.act-reply {
    color: #FFFFFF !important;
}
.textinput.textinput-default.bor2,
.more._showLikeList,
#content_content,
.qz-inputer.bor2,
.fixed-btn.j-download-music {
    border: 0px !important;
    border-radius: 5px;
    color: #FFFFFF !important;
    background-color: #B2DFDB !important;
}
.qz_btn_reply.item,
.item.qz_retweet_btn,
.item.qz_like_btn_v3,
.qz_btn_reply.item:hover,
.item.qz_retweet_btn:hover,
.item.qz_like_btn_v3:hover {
    border: 0px !important;
    border-radius: 5px !important;
    color: #FFFFFF !important;
    background-color: #B2DFDB !important;
    margin: 0px 10px 10px 0px !important;
    padding: 6px 8px 6px 8px !important;
}
.mod-insert-img.bor2,
.mod-quick-comment.bor2 {
    border: 0px !important;
    border-radius: 2.5px !important;
    position: absolute;
    top: 0px !important;
    bottom: 0px !important;
}
.btn-insert-img.bg,
.btn-quick-comment.bg {
    border-radius: 2.5px !important;
    background-color: #FFFFFF;
}
.emot,
.icon.icon-at,
.emot.c_tx3:hover,
.icon.icon-at:hover {
    margin: 0px 0px 0px 10px !important;
}
/* words */

.ui-mr10.state,
#visitYou,
#refuseYou,
#visitMe,
.who-i-visit,
.send-all,
.info-detail,
.qz-poster-ft,
.description-text {
    color: #E0E0E0 !important;
}
.c_tx2,
.c_tx,
.f-name.q_namecard,
.title,
.head-title,
.user-name.textoverflow,
a {
    color: #FFFFFF !important;
}
.title,
#visitYou,
#refuseYou,
#visitMe,
.who-i-visit {
    margin: 0px 10px 0px 0px;
}
.title-text.ui-mr5 {
    margin: 5px 0px 0px 0px !important;
}
/* right bar */

.icenter-right-mod,
.visitor-count.clearfix {
    border-radius: 10px !important;
    background-color: #80CBC4 !important;
    border: 0px !important;
}
.button.bgr2.c_tx_2,
.button {
    border: 0px !important;
    border-radius: 5px !important;
    background-color: #B2DFDB !important;
    color: #FFFFFF !important;
}
#QM_Container_100003 {
    padding: 10px 10px 10px 10px;
}
/* left */

.mod-side-nav.mod-side-nav-message {
    background-color: #80CBC4 !important;
    border: 0px !important;
    border-radius: 5px;
}
#tab_menu_friend:hover,
#tab_menu_care:hover,
#tab_menu_me:hover,
#tab_menu_sdc:hover,
#tab_menu_friend,
#tab_menu_care,
#tab_menu_me,
#tab_menu_sdc {
    background-color: #80CBC4 !important;
    color: #FFFFFF !important;
}
/* bar before wins */

#qz_poster_editor_v4_container {
    background-color: #FFFFFF !important;
}
.textinput.textarea.c_tx3 {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #FFFFFF !important;
    margin: 0px -3px 0px 0px !important;
}
.item.bor3.bg4.item-other,
.item.bor3.bg4.item-funny,
.item.bor3.bg4.item-pic {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #FFFFFF !important;
    margin: -1px -1px 0px 0px;
}
.qz-poster-ft {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #FFFFFF !important;
}
.btn-post.gb_bt.evt_click {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #B2DFDB !important;
}
.textinput.textarea.c_tx3 {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #FFFFFF !important;
    margin: 0px -3px 0px 0px !important;
}
/* retrun top buttom */

#goto_top_btn,
.gb-operation-area,
.gb-operation-button.report {
    background-color: #80CBC4 !important;
    border: 0px !important;
    border-radius: 5px;
    color: #FFFFFF !important;
}
/* other */

.layout-body-friends {
    background-color: #B2DFDB !important;
    border: 0px !important;
    border-radius: 10px !important;
}
.figure-side-wrap.figure-side-scroll,
.friends-link.a-link.nav-hover.current,
.side-area,
.main-area,
.mod-friends-topbar,
.main-application {
    background-color: #B2DFDB !important;
    border: 0px !important;
}
.imgwall-item {
    border-radius: 5px !important;
}
.f-source-merger.update-more,
.b-line,
.update-more {
    border-radius: 5px !important;
    border: 0px !important;
    background-color: #80CBC4 !important;
}
.qz-left.profile-left,
.figure-side,
.figure-side-bg,
.figure-side-hd,
.mod_comment_poster_wrapper,
.figure-interactive {
    background-color: #B2DFDB !important;
}
.js-report-btn.op-pic-policy,
.suggest-support {
    background-color: #80CBC4 !important;
    border-radius: 5px !important;
    border: 0px !important;
}
.btn-post.gb_bt.evt_click {
    margin: 2px 5px 2px 2px !important;
}
/* lleft */

.inner {
    border-radius: 10px !important;
    border: 0px !important;
    background-color: #80CBC4 !important;
}
li,
.more {
    border: 0px !important;
    color: white !important;
}
.hd {
    border: 0px !important;
    border-top-right-radius: 10px !important;
    border-top-left-radius: 10px !important;
    background-color: #80CBC4 !important;
    color: #FFFFFF !important;
}
}
