@-moz-document domain("corazondemelon.es"), domain("corazondebombon.com") {

a { color: #404040; font-size: 11px; text-decoration: none }

strong, em, i, u, span, b, a, label, tt, p, var { color: #404040; font-size: 11px }

h1, h2, h4, h5 { ; color: Purple!important; font-family: Nilland, sans-serif; font-style: normal; text-shadow: 2px -2px 2px #c2a3e0 }

h3, .integrated-title3 { color: #C1838E!important; }

#right-informations, #counter, .number, .firstword { color: DarkOrchid!important; font-family: Nilland, sans-serif; text-shadow: 2px -2px 2px #FFF }

#container.connected h2 { border-bottom: 1px dashed White }

#copy2 { background: transparent }

.soundController { background: Plum; border: 2px solid #FBE4FF; border-radius: 5px 0 0 5px; border-right: transparent; box-shadow: -2px 4px 5px #C5A3FF; height: 40px; right: -0.5px; width: 35px }

.soundController img { height: 30px; margin-top: -3px; width: 30px }

.sliderContainer { background: Plum; border: 2px solid #FBE4FF; border-radius: 5px 0 0 5px; border-right: transparent; box-shadow: -2px 4px 5px #C5A3FF; height: 40px; left: -70px; top: -2px; transition-duration: 0.2s; width: 35px; }

.sliderContainer:hover { background: Plum!important }

.sliderContainer label { color: DimGray; font-size: 13px; height: 20px; text-align: center }

.ui-slider-horizontal { height: .4em; width: 52px }

.ui-slider .ui-slider-handle { border: 1px solid DimGray; height: .8em; }

.css-button.green { background: linear-gradient(to top, #FFF 30%, #F1DAFF 60%, #D6F6FF 100%); color: DimGray; font-family: Calibri, serif }

a.auto-button-green span { background: linear-gradient(to top, #FFF 30%, #F1DAFF 60%, #D6F6FF 100%); color: DimGray; font-family: Calibri, serif }

a.auto-button-green { background: linear-gradient(to top, #FFF 30%, #F1DAFF 60%, #D6F6FF 100%); color: DimGray; font-family: Calibri, serif }

a.auto-button-green span:hover, a.auto-button-green:hover { background: linear-gradient(to top, #FFCCFF 30%, #F1DAFF 60%, #D6F6FF 100%); color: Black }

.css-button.pink { background: linear-gradient(to bottom, #CCFFFF 10%, #D1FAFF 20%, #D5F6FF 30%, #DAF1FF 40%, #DFECFF 50%, #E3E8FF 60%, #E8E3FF 70%, #ECDFFF 80%, #F1DAFF 90%, #F6D5FF 100%); color: DimGray; font-family: Calibri, serif }

a.auto-button-pink span { background: linear-gradient(to bottom, #CCFFFF 10%, #D1FAFF 20%, #D5F6FF 30%, #DAF1FF 40%, #DFECFF 50%, #E3E8FF 60%, #E8E3FF 70%, #ECDFFF 80%, #F1DAFF 90%, #F6D5FF 100%); border-radius: 5px; color: DimGray; font-family: Calibri, serif }

a.auto-button-pink { background: linear-gradient(to bottom, #CCFFFF 10%, #D1FAFF 20%, #D5F6FF 30%, #DAF1FF 40%, #DFECFF 50%, #E3E8FF 60%, #E8E3FF 70%, #ECDFFF 80%, #F1DAFF 90%, #F6D5FF 100%); border-radius: 5px; color: DimGray; font-family: Calibri, serif; height: 25px }

a.auto-button-pink span:hover, a.auto-button-pink:hover { background: linear-gradient(to bottom, #CCFFFF 10%, #D1FAFF 20%, #D5F6FF 30%, #DAF1FF 40%, #DFECFF 50%, #E3E8FF 60%, #E8E3FF 70%, #ECDFFF 80%, #F1DAFF 90%, #F6D5FF 100%); color: Black }

#container.connected { background: url(http://orig08.deviantart.net/9cfe/f/2016/357/6/0/0ftdpr_by_darknessblack-dasl0p6.jpg)fixed center top!important; background-size: cover!important }

#body::before { border-left: 744px solid rgba(255, 255, 255, 0.6); border-radius: 10px; box-shadow: 0 5px 10px 1px rgba(225, 225, 225, 0.4); }

#cadre { background: rgba(221, 160, 221, 0.5); border: 1px solid transparent; border-radius: 5px }

#news-list .news .date { font-size: 14px; padding-left: 12px }

#news-list .news a { color: DarkOrchid }

#container.connected #header { background-image: url(http://orig03.deviantart.net/8301/f/2016/351/4/1/cabeceramorada_by_darknessblack-darx6w9.png)!important; height: 150px }

/*Color al Bienvenida -----*/
#logged-pseudo { border-bottom: 2px dotted Thistle; color: #fff;}

#logged-pseudo span, #logged-pseudo a { color: #fff; font-family: 'Architects Daughter', cursive; font-size: 15px; letter-spacing: 1px; text-shadow: 2px -2px 2px rgba(0, 0, 0, 0.2); text-transform: none }

#logged-deco { background: url(http://orig05.deviantart.net/04a6/f/2016/351/e/3/boton_by_darknessblack-darx7k2.png) }

#logged-episode { color: #250545; left: 15px; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.2); }

#logged-episodenumber { background: Plum; border-radius: 15px; color: White; left: 105px; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.2); width: 18px }

#logged-messages a { color: #79604C!important; font-size: 11px; text-decoration: none; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.2); text-transform: none }

#logged-messages { left: 40px; }

#logged-messagescount { background: rgba(147, 112, 219, 0.2); border-radius: 10px; color: White; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.4) }

#logged-replays a { color: #79604C!important; font-size: 11px; text-decoration: none; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.2); text-transform: none }

#logged-replays { left: 40px }

#logged-replayscount { background: rgba(147, 112, 219, 0.2); border-radius: 10px; color: White; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.4) }

#logged-money, #logged-pa, #logged-pa .actionpoints, #logged-moral { color: #79604C!important; font-size: 11px!important; padding-left: 1px; text-shadow: 2px 2px 2px rgba(0, 0, 0, 0.2); text-transform: none }

#logged-forum a, #logged-banque a, #logged-faq a, #logged-moncompte a { background: #cba6ea!important; border: 2px solid Lavender!important; border-radius: 4px; box-shadow: 2px 2px 2px #C5A3FF; color: White; font-family: Nilland, sans-serif; font-size: 14px; line-height: 18px; transition-duration: 0.3s }

#logged-moncompte a:hover, #logged-moncompte .actif, #logged-faq a:hover, #logged-faq .actif, #logged-banque a:hover, #logged-banque .actif, #logged-forum a:hover, #logged-forum .actif { background: #FBE4FF!important; border: 2px solid White!important; color: DimGray }

#logged-forum { text-indent: 9px }

#logged-banque { text-indent: 10px }

#logged-moncompte { text-indent: 14px }

#logged-faq { text-indent: 2.5px; }

#logged-faq a { font-size: 11px }

#astrological-sign { color: #303030; left: 38px; top: 118px }

#astrological-sign a { color: #303030; font-family: Nilland, sans-serif; font-size: 11.5px; letter-spacing: 1.5px; text-decoration: none; text-shadow: 2px 1px 4px rgba(0, 0, 0, 0.5); transition-duration: 0.3s }

#astrological-sign a:hover { text-decoration: none }

#discussion table tr.read_0 .author_avatar { background: White }

#discussion table tr.read_0 .author_pseudo { color: MediumPurple }

#discussion table tr.read_0 { border: 1px solid Thistle; background: rgba(221, 160, 221, 0.3) }

#discussion table tr { border: 1px solid Orchid }

#discussion table tr:hover { transition-duration: 0.4s }

#discussion table .author_avatar { background: Thistle }

#discussion table .author_pseudo { color: #d8a6ff; font-size: 110%; letter-spacing: 1px; text-align: center }

#new-message-bloc textarea { border-radius: 10px }

#discussion .message_bloc { margin: 10px }

#discussion .message_bloc .content { border: 1px solid Thistle; color: #202020; letter-spacing: 1px }

#discussion .message_bloc .type-0 { background: #e4d6f4; border-radius: 15px; box-shadow: -3px 3px 3px Beige; border: 1px  #856ca4}

#discussion .message_bloc .type-1 { background: #e4d6f4; border-radius: 15px; box-shadow: -3px 3px 3px Beige; border: 1px  #856ca4 }

#discussion .warning { background-color: rgba(255, 255, 221, 0.4); border: solid 1px Orchid; border-radius: 8px; color: #F6A6FF; }

#profile .player-name .summary { color: Orchid; height: 23px; letter-spacing: 1px }

#profile .player-name { left: 55px; }

#profile div#avatarProfil { background-color: rgba(255, 255, 255, 0.4)!important; background: url(http://orig08.deviantart.net/59ac/f/2016/351/1/f/flower_by_darknessblack-dary1jj.png)right top no-repeat; background-size: 50%; border: 3px groove PaleGoldenrod; border-radius: 10px 10px 0 0; box-shadow: 0 0 10px 2px PaleGoldenrod }

#profile div#actions { background: Thistle; background-size: 50%; border: 3px groove PaleGoldenrod; border-radius: 0 0 10px 10px; border-top: none; box-shadow: 0 0 10px 2px PaleGoldenrod; margin-left: -4.5px; margin-top: 11px; width: 260px }

#profile div#actions li .action-description { color: rgba(0, 0, 0, 0.6); margin-left: 5px; margin-top: -4.5px; transition-duration: 0.3s }

#profile div#right { background-color: transparent; margin-left: 55px; min-height: 550px }

.idcard span { color: #79604C!important; font-weight: bold }

.idcard { border-radius: 16px; box-shadow: 0 0 10px 3px Plum; padding-left: 5px; padding-top: 41.5px }

.idcard-member { background: url(http://orig04.deviantart.net/90dd/f/2016/351/e/5/tarjeta_by_darknessblack-darx92z.png) center left no-repeat!important; border: 1px solid DimGray; border-radius: 8px; margin-bottom: 5px; margin-top: 15px }

.idcard .avatar { background: transparent; border-radius: 100px; border: 1px hidden; height: 130px; width: 130px; margin-left: 5px }

.idcard .avatar, .idcard .name, .idcard .column-right .age, .idcard .column-right .status { float: left }

.idcard .value { background: rgba(147, 112, 219, 0.2)!important; border-radius: 4px }

.idcard .signature .label { margin-left: -225px; margin-top: 20px }

.idcard .signature .value { margin-left: 72px; margin-top: -19px; width: 300px }

#profile blockquote { background-color: AntiqueWhite!important; background: url(http://orig08.deviantart.net/59ac/f/2016/351/1/f/flower_by_darknessblack-dary1jj.png)right top no-repeat; 
	background-size: 30%; border: 3px groove PaleGoldenrod; border-radius: 5px; box-shadow: 0 0 20px 3px rgba(250, 250, 210, 0.3); color: #404040; font-weight: bold; letter-spacing: 1px }

#profile .punmob_spoil .pun_center { background: transparent; }

.idcard .value { background: rgba(255, 255, 255, 0.7); border-radius: 2px; letter-spacing: 1px }

.scene .loveometer * { background: url(https://66.media.tumblr.com/38fce7cfa951c3d26d6eb5cff694df18/tumblr_inline_ochr3fEoVu1u5w4b5_540.png)no-repeat }

.scene .bubble { background: #e4d6f4; border: 1px solid DarkGray; font-family: Calibri, serif; font-size: 14px }

.scene .player { background: url(http://orig09.deviantart.net/a447/f/2016/351/3/7/conv_by_darknessblack-darx93b.png)-6px top no-repeat }

.scene .sentence { background: linear-gradient(to left, #AFCBFF 10%, #FF9CEE 60%, #B28DFF 100%); border-left: 4px solid transparent; border-top: 4px solid transparent }

.scene .responses { border-left: 4px solid transparent }

.scene .responses .response { color: Violet; font-family: Calibri, serif; font-size: 14px; transition-duration: 0.2s }

.scene .response:hover { background: #AFCBFF; color: White; transition-duration: 0.3s }

.scene .responses .response span { color: #181818; font-family: Calibri, serif; font-size: 14px }

h3, .integrated-title3 { color: #c5a6ff; font-family: Georgia, sans-serif; text-shadow: 2px -2px 2px #c0c0c0 }

#objectives .description { font-family: Nilland, sans-serif; font-size: 13px; text-align: justify }

#current-episode li.completed { background: transparent url('https://goo.gl/1gGmAb')left center no-repeat!important; }

#current-episode li.pending { background: transparent url('https://goo.gl/QHbDPH')left center no-repeat!important; }

.episode-progression .progress-left { background: linear-gradient(to top, #FF9CEE 20%, #F6A6FF 50%, #FFCCF9 100%); border: 1px solid DarkGray; border-radius: 5px 0 0 5px; border-right: none }

.episode-progression .progress-inner { background: linear-gradient(to top, #FF9CEE 20%, #F6A6FF 50%, #FFCCF9 100%); border: 1px solid DarkGray; border-left: none; border-right: none }

.episode-progression .progress-right { background: linear-gradient(to top, #FF9CEE 20%, #F6A6FF 50%, #FFCCF9 100%); border: 1px solid DarkGray; border-left: none; border-radius: 0 5px 5px 0 }

.episode-progression .playcount { background: Plum; border-radius: 50px; box-shadow: 2px 2px 2px DarkGray }

.boyfriend .lom span { background: Lavender; border-radius: 10px; color: SlateBlue; font-family: Nilland, sans-serif; letter-spacing: 1px; padding: 4px 6px; text-shadow: 1px 1px #c0c0c0 }

.boyfriend.crush { background: White; border: solid 1px Plum; border-radius: 15px }

.boyfriend { background: White; border: solid 1px Plum; border-radius: 5px }

.boyfriend .description { color: DarkOrchid }

.stamp { border-color: DarkOrchid!important; color: Violet!important; text-shadow: 1px 1px #a9a9a9 }

.boyfriend .thumbnail-wrapper .stamp { color: White; border: solid 2px DarkOrchid; box-shadow: 1px 1px 3px 0 Gray, inset 1px 1px 3px 0 DarkGray; text-shadow: 1px 1px 3px Gray }

.friend .player-name a { color: Orchid; font-family: Nilland, sans-serif; letter-spacing: 1px; text-decoration: none }

.friend .episode-number { color: MediumPurple; margin-bottom: 10px; }

.pagination a.page { background-color: White; border: 1px solid gray; color: Gray; }

.pagination a.selected { background-color: Orchid; border: 1px solid gray; color: White; }

.player-name .player-name-tools li a { background: rgba(255, 255, 255, 0.8); color: #A9856A; font-family: Nilland, sans-serif; font-size: 12px; transition-duration: 0.3s }

.player-name .player-name-tools li { border: 1px solid Thistle }

.player-name .player-name-tools li a:hover { background: Thistle; color: White; letter-spacing: 1px }

#faq { background: transparent }

#faq div#frequency { background: linear-gradient(to top, LightPink 30%, Plum 80%, Thistle 100%); border: 2px White solid }

#faq div#left a { color: White; font-family: Nilland, sans-serif }

#faq div#right a.question { color: PaleVioletRed; font-family: Nilland, sans-serif; text-shadow: 0 3px 4px Silver }

#faq div#right a.category { color: DimGray; }

div.avatarfull { background: linear-gradient(to top, LightPink 30%, Plum 80%, Thistle 100%); border: 2px White solid }

.cupboard .tab-body ul li, #zIndexTools>#current { border-radius: 10px }

#zIndexTools #backward, #zIndexTools #forward { border-radius: 5px }

.cupboard .tab-header a:hover img.normal, .cupboard .tab-header .tab-active img.normal, .cupboard .tab-header a img.hover { border-radius: 10px }

.cupboard .tab-header a img.normal { border-radius: 10px }

#zIndexTools>#description { color: Black; font-family: Nilland, sans-serif; font-size: 14px }

#bank-container #bank-payment div[class^=payment-method] { background: White; border-left: 2px solid Violet!important; border: 2px solid Violet }

#bank-container #bank-selection li { background: rgba(255, 255, 255, 0.4); border: solid 2px Violet; border-right: solid 2px Violet!important; margin-bottom: 5px }

#bank-container #bank-selection li.active { background: White; border: solid 2px Violet; border-right: none!important; margin-bottom: 5px }


/*** A partir de aqui fue escrito por S9Clay (me basé en su código para hacer el menú) ^^ ***/


#sous-menu { background: none; height: 30px; margin-bottom: 5px }

#sous-menu a { background: #b897c0!important; border: 2px solid #e5d3f1; border-radius: 5px; box-shadow: 2px 2px 2px #C5A3FF; color: White; font-family: Nilland, sans-serif; font-size: 15px; font-weight: bold; height: 25px; line-height: 23px; margin-top: 13px; text-align: center; text-transform: none; transition-duration: 0.4s }

.sm0 { margin-left: -15px; }

.sm0:before { content: '?'; font-size: 19px }

.sm1:before { content: "Mi piso" }

.sm2:before { content: "Ciudad" }

#sous-menu a.sm2 { left: 221.5px; width: 107px }

.sm3:before { content: "Mis objetivos" }

.sm4:before { content: "Mis chicos" }

.sm5:before { content: "Mis amigas" }

#sous-menu a:hover, #sous-menu .actif { background: #e5d3f1!important; border: 2px solid White!important; color: DimGray }
}
