css:
@-moz-document domain("imgur.com") {


.aqua {
    color: #89c624!important;
}

.green {
    color: #89c624!important;
}

.red {
    color: #ee4444!important;
}

#topbar .upload-button-container {
    background: #89c624;
    border: 1px solid #89c624;
}

.btn-main {
    background: #89c624;
}

.sentence-sorting .selection {
    border-bottom: 3px solid     #89c624;
}

.panel-header .options .selected
{
color: #89c624!important;
}

::selection {
    background: #89c624;
}

.sentence-sorting .selection:before {
    border-bottom: 8px solid  #89c624;
}

.sentence-sorting .selection:after {
    border-bottom: 8px solid  #89c624;
}

#section .pulse-dot, #topbar .menu-icon .pulse-dot {
    background-color: #89c624;
}

#section .expanding-circle, #topbar .pulse .expanding-circle {
    border: 3px solid #89c624;
}

#section:hover .selection .name {
    color: #89c624!important;
}

.selection:hover {
    color: #89c624!important;
}

#captions .comment-upvoted {
    border-left: 2px solid #89c624;
}

.post.green {
    border: 2px solid #89c624;
}

.post.green .image-list-link:before {
    border-top-color: #89c624;
}

#topbar .account .user-dropdown-container, #topbar .signin-dropdown {
    border-top: 3px solid #89c624;
}

#topbar .account-user-name:after {
    border-top: 4px solid #89c624;
}

.panel-header .options .selected {
    color: #89c624;
}

.right .panel.menu .button.selected, .right .panel.menu .button.selected h2 {
    color: #89c624;
}

.btn-success {
    background-color: #89c624;
    border-color: #89c624;
}

#topbar .link-menu a.active, #topbar .user-dropdown a.active {
    border-left: 5px solid #89c624;
}

.comment-op {
    color: #89c624!important;
}

#account-thumbs .hover-icons {
    border-color: #89c624;
}

.account-upload-button {
    background: #89c624;
}

.FlipInfo, .FlipInfo::after {
    background: #89c624;
}

.total-upload-progress {
    background-color: #89c624;
}

#topbar .logo-icon {
    background: url(https://upload.wikimedia.org/wikipedia/commons/b/bb/Imgur_Title_Screen_Image.svg) center center no-repeat;
    background-size: contain;
}

li.active:before {
    background: #89c624;
}

a.sg-item.up .sg-item-vote {
    background-color: #89c624;
}
    
.sentence-sorting .sort-options a#showfiltered.showing span:before, .sentence-sorting .sort-options a#showusersub.showing span:before, .sentence-sorting .sort-options a#showviral.showing span:before, li.active #search_adv_toggle:before {
    background: #89c624;
}

.post-options-publish {
    background-color: #89c624;
}

#account-thumbs .selected .selection-border {
    border: 3px solid #89c624;
}
#account-thumbs .selection {
    background: #89c624;
}

.panel .thumbs img:hover {
    border: 3px solid #89c624;
}

.btn-confirm-yes {
    background-color: #89c624;
}

}
