css:
@-moz-document domain("idnes.cz") {
body {
    color: #999 !important;
    background: #151515 !important;
}

.emblem-3 {
    background: none;
}
a {
    color: #b4b2b2!important;
}
table[id*="\."], div[id*="\."], .widesquare {
    display: none !important;
}
.m-bg-3 {
    color: #444 !important;
    background: #151515!important;
}
.m-bg-4, .col-ab {
    color: #999 !important;
    background: #333 !important;
}
.row-first, .entry-content, .content, .video-box, .section-list, #sph-emblem {
    color: #999!important;
    background: #333 !important;
}
form input, submit {
    color: #666 !important;
    background: black !important;
}
#foot {
    height: 0px !important;
}
#manager-bar a {
    color: #222 !important;
}
#blogy, #topkontakt-sph {
    height: 0px;
    visibility: hidden !important;
}
.opener {
    color: #999 !important;
}
#f-foot, #p-menu, #minor {
    background: none;
}
#p-dal i {
    color: #999 !important;
}
#p-menu-2 a {
    color: #000 !important;
}
#p-menu-1 a {
    color: inherit !important;
}
.emblem-3 {
    color: inherit !important;
}
.portalmenu-1 a, .portalmenu-1 a:hover {
    color: inherit !important;
}
.portal-g2 {
    padding: 0 15px;
    border-bottom: 1px solid #1a1919;
    background: #545454;
}
.portalmenu-2 a {
    position: relative;
    display: block;
    padding: 0 .6em;
    border: 2px solid #5d5d5d;
    border-radius: 6px;
    color: #E1DCDC !important;
}
.portalmenu-2 a:hover, .portalmenu-2 .act a {
    background: #2D2D2D;
}

.art-info .time, .time, #nejnovejsi li .time {
    color: #7EA7BA;
}
h1[itemprop="name headline"] {
    color: #ccc;
}
h1.list-tit, h1.list-tit2, h2.list-tit, h2.list-tit2, .inc-tit
{
    color: #999;
}
.weather-2 .mask:before {
    background: none;
}
.contribution, .video-block {
    background: none;
}
.less-cancel, .less-cencel, #short-arts-hp .less-cancel {
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(51, 51, 51, 1) 60%)
}
#f-lachtan, #f-seo {
    background: none;
}
#blogy, #topkontakt-sph {
    display: none;
}
#portal-fix.fixed {
    background: none;
    box-shadow: 15px 0 0 0 #333, -15px 0 0 0 #333
}
.art-full .equip-tv {
    background: #222;
}
.art-full .complete, .complete {
    color: #838383;
    background: #272727;
}
.art-full .complete .title, .complete .title {
    color: #b4b2b2
}
#sdileni .bubble-box {
    background: rgba(0, 0, 0, .07);
}
ul.art-community a {
    color: #fff !important;
}
img[src*="nophoto"] {
    filter: invert(82%);
}
a[href*="http://www.idnes.cz/"] {
    filter: none;
}
#p-hld .ico-zoom span {
    filter: invert(1);
}
#p-hld .ico-zoom span {
    border: 0;
}
.embed-social .es-box {
    border: 1px solid rgba(255, 255, 255, 0.2);
    color: #262525;
    background: #262525;
}
.embed-social .es-top, .embed-social .es-mid, .embed-social .es-bot { 
    border: 1px solid rgba(255, 255, 255, 0.2);
}
.embed-social p {
color: #eee
}
.embed-social .es-bot {
    background: #262525;
}
.videoblok {
    padding: 19px;
    border: none;
    background: #333;
}
}

@-moz-document domain("www.idnes.cz") {
.alone a {
    background: url(data:image/png;base64,) no-repeat 0 .3em;
    filter: invert(40%);
}
#p-dal i {
    color: inherit !important;
}
#foot .content
{
    width: 1030px;
}
.kurzovni-listek {
    background: #272727;
}
}

@-moz-document domain("sport.idnes.cz") {
.art h3 {
    color: #798fd2;
}
}

@-moz-document domain("tv.idnes.cz") {
#content, #portal, .img-tit .art-link, .entry-in, .entry .isle-link {
    background: none;
}
.entry-unique h3 {
    color: #f6f6f6;
}
.entry-in {
    box-shadow: 0 0 2px rgba(255,255,255,0.3);
}
.entry h3 {
    color: #fff;
}
}
