css:
@-moz-document url-prefix("https://trello.com/b"), domain("trello.com") {
body {
	background-color: #fff !important;
	background-image: url('http://www.dsi-reisen.de/wp-content/uploads/2016/06/Catin-3-00526-e1466610859576.jpg') !important;
	background-size: cover !important;
}

#header {
/*	background: #2B2B2B !important;*/
    background: rgba(0,0,0,.5)!important;
	padding: 10px !important;
}

.header-logo {
	margin-top: 5px;
}

.board-header {
	padding: 5px 30px 5px 8px !important;
    background: rgba(0,0,0,.4)!important;
}

.boards-drawer {
    background: rgba(0,0,0,.5);
    width: 140px;  
}

@media only screen and (min-width: 1281px), only screen and (max-width: 1280px) and (min-width: 901px){
.body-pinned-drawer #surface {
    margin-left: 140px;
}
}

.sidebar-show-btn {
	margin-top: 5px !important;
}

.sidebar-hide-btn .icon-sm.icon-rightarrow {
	margin-top: -4px !important;
}

.board-widgets {
	padding-top: 6px !important;
	padding-left: 6px !important;
}

.board-widgets-content-wrapper .board-widget-nav h3.toggle-widget-nav {
	padding: 4px 10px !important;
}

.board-widgets-content-wrapper .board-widget-nav h3.toggle-widget-nav .toggle-menu-icon {
	top: 5px !important;
}

.header-user {
	top: 10px !important;
	right: 5px !important;
}

.header-member {
	border-top-left-radius: 20px !important;
	border-bottom-left-radius: 20px !important;
}

body.small-window .header-user .header-member {
	border-radius: 20px !important;
}

.boards-drawer-header {
    background: rgba(0,0,0,.4) !important;
    color: #ddd;
    padding: 61px 13px 11px 13px;
    display: none!important;
}

.sidebar-section-header-title {
    display: none;
}

.compact-board-tile-link-text {
   padding: 5px 0 5px 10px;
}

.compact-board-tile-link {
    border-radius: 2px;
    font-weight: 400;
    font-size: 12px;
    height: 28px;
}

.compact-board-tile-link-thumbnail {
    border-radius: 2px 0 0 2px;
    height: 28px;
    width: 10px;
}

.boards-drawer input {
    display: none;
}

.compact-board-tile-link-text-sub-name {display: none}

.compact-board-tile-link-text.has-sub-name {
    padding-top: 5px;
    padding-bottom: 5px;
}


.compact-board-tile-link-options-remove, .compact-board-tile-link-options-star {
    margin: 5px 0;
}


.member {
	border-radius: 20px !important;
	border: none !important;
}

.member img {
	border-radius: 20px !important;
	border: none !important;
}

.list-area-wrapper {
	padding-left: 10px;
}

.list-area {
	width: 3500px !important;
}

.fancy-scrollbar::-webkit-scrollbar-track-piece {
	background: none !important;
}

.list-header-menu-icon.js-open-list-menu{
	color: #595959 !important;
}

.list-header-menu-icon.js-open-list-menu:hover {
	color: #000000 !important;
}

.list-gradient-top,.list-gradient-bottom {
	display: none !important;
}

.pop-over-list li>a:hover {
	background: #999999 !important;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:vertical {
	background: rgba(0, 0, 0, 0.3) !important;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:horizontal {
	background: rgba(255, 255, 255, 0.5) !important;
}

.list-header h2 {
	font-size: 15px !important;
}

#board {
	padding: 5px 0 0 0 !important;
    white-space: normal;
}

.list-wrapper {
    width: 230px;
    margin: 0 4px;
    height: 49%;
    margin-bottom: 1%;
}

.list {
	padding: 4px 0 0 0 !important;
	background: rgba(252, 252, 252, 0.7) !important;
	-ms-flex: 0 0 230px !important;
	-webkit-flex: 0 0 230px !important;
	flex: 0 0 230px !important;
	width: 230px !important;
}

.list.add-list {
	padding: 6px !important;
}

.list.add-list .list-name-input {
	background: #ebebeb !important;
}

.list.add-list .icon-lg {
	color: #818181 !important;
}

.list-cards {
	padding: 1px 1px 1px !important;
    margin: 0 6px;
}

.list-card {
	font-size: 12px !important;
	line-height: 15px !important;
    margin-bottom: 6px;
    border-bottom: none;
}

.list-card-details {
    padding: 6px 4px 2px 8px;
}


.list-card-labels > span.card-label {
	height: 6px !important;
	border-bottom-right-radius: 3px !important;
	border-bottom-left-radius: 3px !important;
}

.list-card-members .member {
	height: 24px !important;
	width: 24px !important;
}

.list-card-members .member .member-initials {
	height: 24px !important;
	line-height: 24px !important;
}

.list-card-members .member .member-avatar {
	height: 24px !important;
	width: 24px !important;
}

.open-card-composer{
    font-size: 12px;
}

span.card-label {
	opacity: 0.7 !important;
}

.badges {
	padding: 0 !important;
	margin-left: -2px !important;
}

.badge {
	margin: 0 3px 0 0 !important;
}

.badges .badge-icon.icon-sm {
	height: 16px !important;
	font-size: 10px !important;
	line-height: 18px !important;
	width: 16px !important;
}

.badge-text {
	font-size: 11px;
}

.open-card-composer {
	color: #595959 !important;
	margin: 0 !important;
}

.open-card-composer:hover {
	background: rgba(255, 255, 255, 0.5) !important;
	color: #595959 !important;
	text-decoration: none !important;
	margin: 0 !important;
}

.button-link:hover {
	background: #999999;
	background: -webkit-linear-gradient(top, #a6a6a6 0, #8c8c8c 100%) !important;
	background: linear-gradient(to bottom, #a6a6a6 0, #8c8c8c 100%) !important;
	border-color: #999999 !important;
	color: #fff !important;
}

.window-wrapper {
	background: #F9F9F9 !important;
}

.checklist-progress-bar-current {
	border-top-right-radius: 3px !important;
	border-bottom-right-radius: 3px !important;
	background: #858585; /* Old browsers */
	background: -webkit-radial-gradient(center, ellipse cover,  #c5c5c5 0%,#929292 100%) !important; /* Chrome10+,Safari5.1+ */
	background: radial-gradient(ellipse at center,  #c5c5c5 0%,#929292 100%) !important; /* W3C */
	border: 1px solid #858585 !important;
}

.board-widgets {
	background: rgba(255,255,255,0.98) !important;
}

.sidebar-hide-btn {
	top: 5px !important;
}

.atMention {
	background: #A3359F !important;
	border-radius: 5px !important;
	color: #fff !important;
	font-size: 12px !important;
}

code {
	font-family: Menlo,Monaco,"Courier New",monospace !important;
	background-color: #F7F7F9 !important;
	border: 1px solid #E1E1E8 !important;
	border-radius: 3px !important;
	padding: 3px 4px !important;
	font-size: 12px !important;
	color: #D14 !important;
}
}
