css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://extratorrent.cc/"), url-prefix("https://extratorrent.cc/"), url-prefix("https://extra.to/") {
body {
    background-color: #1A1821 !important;
    margin: 0;
    padding: 10px;
    color: #3E4142;
    font-family: Verdana, Arial, Helvetica, sans-serif !important;
    font-size: 12px;
    font-style: normal;
    font-weight: normal;
  }
img[src*="//images4et.com/images/logo.gif"]
{ display:none !important;
}
.easy-autocomplete.eac-blue-light input:hover, .easy-autocomplete.eac-blue-light input:focus {
    border-color: #48bdbd;
    box-shadow: 0 1px 1px rgba(255, 255, 255, 0.1) inset,0 0 8px rgba(255, 255, 255, 0);
}    
.easy-autocomplete input {
    background-color: #100F1E;
    border-color: #151116;
    border-radius: 1px;
    border-style: solid;
    border-width: 1px;
    color: #3E4142;
    float: none;
    padding: 3px 7px;
} 
 .warn {
    font-size: 16pt;
    color: #49465B;
    text-align: center;
}  
.trtyellow {
    color: #1A1821;
    background-color: #49465B;
    padding: 5px;
}    
 table.tablemenu td {
    padding: 3px 7px;
    border: 1px #1a161e solid;
    background-color: #1a161e;
}   
  tr.tlr {
    background-color: #1A1821;
    padding: 3px 7px;
}
tr.tlz {
    background-color: #1A1821;
    padding: 3px 7px;
}  
table.tl th table tr {
    background-color: #1A1821 !important;
}
    td.tabledata0 {
    background-color: #1A1821;
    padding: 5px 10px;
    border: 1px #1A1821 solid;
}
  td.tabledata1 {
    background-color: #1A1821;
    padding: 5px 10px;
    border: 1px #151116 solid;
} 
table.tl th {
    text-align: left;
    border: 1px #1A1821 solid;
    font-weight: normal;
    padding: 5px;
    white-space: nowrap;
}    
 a:hover, .link:hover {
    color: #151116;
    text-decoration: none;
    cursor: pointer;
}
a, .link:hover {
    color: #3E4142;
    text-decoration: none;
    cursor: pointer;
}
.seed, .sy {
    color: #84FF88;
} 
.leech, .ly {
    color: #FF8488;
}    
div.r0, div.r1, div.r2, div.r3, div.r4, div.r5, div.r6, div.r7, div.r8, div.r9, div.r10 {
    width: 42px;
    height: 13px;
    background-image: url('https://i.imgur.com/62dF7hx.png');
} 
div.cat_subcats {
    line-height: 20px;
    background-color: #252235;
    border: 1px solid #151116;
    float: left;
    margin: 3px;
    padding: 4px 8px;
} 
b.pager_no_link {
    border: 1px #252235 solid;
    color: #151116;
    font-weight: bold;
    padding: 4px 7px;
    background-color: #252235;
}  
a.pager_link:hover {
    color: #151116;
    text-decoration: none;
    background-color: #252235;
}
a.pager_link {
    border: 1px #252235 solid;
    color: #3E4142;
    text-decoration: none;
    padding: 4px 7px;
    background-color: #252235;
}    
tr.tlz:hover, tr.tlr:hover {
    background-color: #252235;
} 
td.td_menu {
    background: url('https://i.imgur.com/s47rEAf.gif') repeat-x;
    padding: 0 20px 2px 20px;
}  
div.menul {
    background: url('https://i.imgur.com/g9aqCxB.gif') no-repeat;
    width: 6px;
    height: 30px;
}    
div.menur {
    background: url('https://i.imgur.com/sKmdyCM.gif') no-repeat;
    width: 6px;
    height: 30px;
}       
div.blog_top {
    float: left;
    font-weight: bold;
    padding: 7px 8px 9px 15px;
    width: 204px;
    background: url('https://i.imgur.com/5XdU30L.gif') repeat-x;
}
div.blog_left {
    float: left;
    height: 30px;
    background: url('https://i.imgur.com/5XdU30L.gif') no-repeat;
    width: 6px;
}
 div.blog_right {
    float: left;
    height: 30px;
    background: url('https://i.imgur.com/5XdU30L.gif') no-repeat;
    width: 6px;
}  
 div.top_cat {
    background: url('https://i.imgur.com/5XdU30L.gif') repeat-x #f2f2ff;
    text-align: left;
    padding: 4px 10px;
    color: #3E4142;
    height: 15px;
    margin: 5px 0 0 0;
}   
div.top_pic img {
    border: 1px solid #3E4142;
}   
  div.chat_msg {
    background-color: #0b0d0e;
    padding: 3px 7px;
    border: 1px #181f24 solid;
    margin: 5px 0;
} 
div.top_icon {
    float: left;
    margin-top: -8px;
   padding-left: 22px !important;
    padding-top: 22px !important;
    background: url(https://i.imgur.com/xFz3hYH.gif) no-repeat !important;
}
img[src*="//images4et.com/images/hot_small.gif"]
{ display:none !important;
}
img[src*="//images4et.com/images/download_normal.gif"]
{ display:none !important;
} 
.highlight {
    background-color: #0d1027;
} div.usrm {
    position: absolute;
    background-color: #1E2033;
    border: 1px solid #1E2033;
    margin-top: 15px;
    display: none;
    font-size: 10px;
    padding: 1px 5px 5px 5px;
    text-align: left;
    line-height: 20px;
    width: 116px;
    z-index: 3;
}   
}
