css:
@-moz-document domain("backpack.tf") {
  body{
    font-family: "Roboto",sans-serif
  }
  body.app-440{
    background-image: none;
    background-color: #591E1D
}
  a{
    color: #c02925
  }
  a:hover, a:focus{
    color: #90211e
  }
  .app-440 .price-change .price-new{
    background-color: #c02925
  }
  .progress-bar{
    background-color: #591E1D
  }
  .alert-info {
    background-color: #f7d9d9;
    border-color: #f1bcbc;
    color: #8f3131
  }
  .alert-info .alert-link {
    color: #722626
}
  .app-440 .incentive-list .incentive header {
    background-color: #832d2b
}
  /*---------------*/
  /* NOT DISPLAYED */
  /*---------------*/
  .app-440 .badge{
    display: none
  }
  .app-440 .navbar .nav > li > a{
    text-shadow: none
  }
  .app-440 .row-media > .col-md-5{
    display: none
  }
  .app-440 .col-md-4 > .panel-default.panel:nth-of-type(1) > .panel-body{
    display:none
  }
  .app-440 #media-side{
    display: none !important
  }
  /*---------------------------*/
  /* BUTTONS , PANELS & NAVBAR */
  /*---------------------------*/
  .app-440 .navbar{
    border: none;
    box-shadow: none
  }
  .app-440 .nav .dropdown-toggle .caret{
    border-top-color: #aa3a37;
    border-bottom-color: #aa3a37
  }
  .app-440 .site-search-dropdown li.header{
    border-bottom-color: #832d2b;
    background-color: #832d2b
  }
  .app-440 .dropdown-menu li > a:hover{
    background-color: #591E1D
  }
  .app-440 .dropdown .dropdown-menu .dropdown-header{
    padding: 5px 10px;
  }
  .app-440 .navbar-nav > li > .dropdown-menu{
    margin-top: 2px;
    border: none
  }
  .app-440 .btn.btn-primary{
    background-color: #aa3a37;
    border-color: #973331
  }
  .app-440 .btn.btn-primary:hover, .app-440 .btn.btn-primary:focus, .app-440 .btn.btn-primary.focus, .app-440 .btn.btn-primary:active, .app-440 .btn.btn-primary.active, .open > .app-440 .btn.btn-primary.dropdown-toggle{
    background-color: #832d2b;
    border-color: #682422
  }
  .app-440 .navbar{
    border: none;
    box-shadow: none
  }
  .app-440 .navbar, .app-440 .navbar-header{
    background: none;
    background-color: #832d2b
  }
  .app-440 .navbar-avatar{
    border-color: #aa3a37
  }
  .app-440 .modal-header, .app-440 .panel-main > .panel-heading{
    background: none;
    background-color: #832d2b;
    box-shadow: none !important;
    border-color: #832d2b;
    max-height: 35px;
    padding: 5px
  }
  .app-440 .nav-pills > li.active > a, .app-440 .nav-pills > li.active > a:hover, .app-440 .nav-pills > li.active > a:focus{
    background-color: #832d2b;
    border-color: #832d2b
  }
  .app-440 .nav-pills > li > a{
    color: #832d2b
  }
  .app-440 .panel-body {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border: none
}
  .app-440 .popover .popover-title{
    background: #832d2b;
    box-shadow: none;
    border-color: none
  }
  /*-------------------------------*/
  /* BORDERS , PADDING AND MARGINS */
  /*-------------------------------*/
  .app-440 .panel-main{
    border: none
  }
  .app-440 .panel{
    border: none;
    box-shadow: none
  }
  .app-440 .index-latest-changes{
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px
  }
  .app-440 .top-prices {
    margin-bottom: 5px;
    margin-left: 160px;
    margin-top: 50px
}
  .app-440 .row-media > .col-md-7 {
    width: 610px;
    margin-left: 170px
}
  .app-440 .row .padded {
    padding: 5px
}
  .app-440 .inventory .page-number {
    margin-bottom: 15px;
    margin-left: 440px;
    margin-top: 10px
}
  .app-440 .panel-main .table tbody td, .panel-main .table tbody th{
    background: none
  }
  .app-440 #unusual-pricelist {
    margin-bottom: 10px;
    margin-left: 15px;
    margin-top: 20px
}
}
