css:
@-moz-document domain("calendar.google.com") {
html >*, body, td, input, textarea, select, button, .g, .kno-ecr-pt, .cic .cic-dm .cic-tmr {
	font-family: 'Helvetica Neue' !important;
    font-weight: 300 !important;
	}

.mv-container >* {
    margin-left: 10px;
    border-top: 1px solid #fff;
    border-right: 1px solid #fff;
}
.mv-dayname, .wk-weektop .wk-dayname {
    font-family: 'Helvetica Neue' !important;
    font-weight: 300;
    font-size: 13px;
    color: salmon;
    text-transform: uppercase;
    text-align: center;
    border-bottom: none;
}
.st-dtitle {
    padding: 7px !important;
    font-family: 'Helvetica Neue' !important;
    font-size: 18px;
    font-weight: 300;
    color: #8e8e8e;
    text-transform: uppercase;
    border-top: none;
    border-left: 1px solid #fff;
    background-color: #f2f2f2;
}
.st-dtitle-nonmonth {
    color: #ccc;
}
.st-bg {
    border-left: 1px solid #fff;
    background-color: #f2f2f2;
}
.st-c-pos {
    font-family: 'Helvetica Neue' !important;
    font-size: 12px;
    font-weight: 300;
}
/* mini calendar */ 
.dp-cell {
    font-family: 'Helvetica Neue' !important;
    font-size: 11px;
    font-weight: 300;
}
.dp-days .dp-dayh{
    color: salmon;
}
.dp-with-events {
    font-weight: 500 !important;
}
.date-top {
    font-family: 'Helvetica Neue' !important;
    font-weight: 300;
    font-size: 18px;
    color: salmon;
    text-transform: uppercase;
}
.calHeaderSpace, .calList .calListLabel-sel {
    font-family: 'Helvetica Neue' !important;
    font-weight: 300;
}
.sng-frame >*{
    font-family: 'Helvetica Neue' !important;
    font-weight: 400;
}
/* start with Monday */
  td.st-bg:nth-last-child(2), td.st-bg:nth-last-child(1){
    background-color: #e5e5e5;
  }
  td.st-dtitle:nth-last-child(2), td.st-dtitle:nth-last-child(1){
    background-color: #e5e5e5;
  }
/* colorise today */
td.st-bg-today, .st-dtitle-today, .st-bg-td-last{
	background-color: salmon !important;
    color: white;
    border-right: 1px solid salmon;
}
/* Gatget panel*/
#gadgetcell .sn-container .sng-frame .sng-title-bar{
    font-family: 'Helvetica Neue' !important;
    font-weight: 300;
    font-size: 18px;
    color: salmon;
}
/* box event chip*/
.lv-datelink, .lv-event-time, .lv-event-title{
    font-family: 'Helvetica Neue' !important;
    font-weight: 300;
}
.lv-lastevent .lv-eventcell, .lv-datecell{
	border-bottom: 1px solid #f2f2f2;
}

.chip dl >*{
	font-family: 'Helvetica Neue' !important;
    font-weight: 300;    
}
}
