css:
@-moz-document url-prefix("http://nexusmods.com"), url-prefix("http://www.nexusmods.com"), url-prefix("https://nexusmods.com"), url-prefix("https://www.nexusmods.com") {
* {
    border-radius: 0 !important;
    font-size: 1em;
    font-weight: normal !important;
    letter-spacing: normal !important;
    text-shadow: none !important;
    font-family: Arial, Helvetica, sans-serif;
}
html {
    background: #333;
}
body {
    background: #333;
    background-image: none;
}
a {
    color: #0FADED !important;
}
#top {
    background: #111;
    background-image: none;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    margin-bottom: .5em;
    padding-bottom: 0;
}
#top-inner {
    background-image: none;
    width: 100%;
}
#top .right {
    width: 71%;
}
#logo {
    margin: 1em 0 1em .75em;
}
.game {
    bottom: 60px !important;
    color: #0FADED;
    font-size: 1.1em !important;
    left: 75px !important;
}
#top .sub-bar .account {
    background-color: #111 !important;
}
.nmm {
    border: 0 !important;
    height: 2em !important;
}
.search {
    border: 0 !important;
    height: 2em !important;
    margin-right: 1.1em;
}
.search > .bubble-open {
    background-color: #111 !important;
}
.search > a > img {
    margin-top: .8em;
}
.account {
    height: 2em !important;
}
.account > img {
    display: none;
}
.account > .bubble-open {
    background-color: #111 !important;
    color: #0FADED !important;
    display: block;
    line-height: normal;
    padding: 0;
}
.account > .bubble-collapse > .bubble-box {
    background-color: #444 !important;
    border: 0;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    left: 4.4em !important;
    top: .25em !important;
    width: 100%;
}
.account > .bubble-collapse > .bubble-box > .bubble-arrow {
    border-bottom: 11px solid #444;
}
.account > .bubble-collapse > .bubble-box > .bubble-arrow-border {
    border-bottom: 11px solid #333;
}
.account > .bubble-collapse > .bubble-box > .heading-pad {
    display: none;
}
.account > .bubble-collapse > .bubble-box > .options {
    border: 0 !important;
    padding: 0;
    width: 100%;
}
.account > .bubble-collapse > .bubble-box > .options > div {
    background-color: #444 !important;
    padding: 0 !important;
    width: 100%;
}
.account > .bubble-collapse > .bubble-box > .options > div > a {
    color: #0FADED !important;
    padding: .6em 0;
    text-align: center;
    width: 100%;
}
#nav {
    border-top: none;
}
#nav > li {
    margin: 0 !important;
    padding: 1px !important;
}
#nav > li > a {
    background: none !important;
    background-color: #444 !important;
    background-image: none !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    padding: .8em .8em;
}
#nav > li > a:hover {
    background-color: #313131 !important;
}
#nav > li > a > .inner {
    background: none !important;
}
#nav > li > a > .inner > .subnav-arrow {
    background: none !important;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 5px solid #000;
    height: 0;
    margin-top: 10px !important;
    width: 0;
}
#nav .item .txt {
    font-weight: bold !important;
}
.subnav {
    border: 0;
    border-radius: 0;
    padding: 0 1px !important;
    top: 98px !important;
}
.subnav a {
    font-size: 1em !important;
    padding: .60em 5px !important;
    width: 180px;
}
.subnav-bg {
    border-radius: 0 !important;
}
.subnav a:hover {
    background-color: #313131;
}
.messages {
    display: none !important;
}
.notifications {
    display: none !important;
}
#main {
    margin-bottom: 1em;
    padding-bottom: 0;
    width: 80%;
}
.file-header {
    border-radius: 0;
    margin: 0 auto;
    width: 100%;
    background-size: cover !important;
}
.file-header-default {
    border: 0;
    width: 100%;
}
.file-name {
    background: rgba(0, 0, 0, 0.7) !important;
    padding: 0 10px !important;
    max-width: 100%;
}
.file-info-bg {
    background-color: #292929;
    background-image: none !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    margin-bottom: .5em;
    padding: 0;
    width: 100%;
}
#content {
    background: none !important;
    padding-bottom: 1em;
}
.file-info-bg > #left-col {
    display: block;
    float: none;
    margin: 0 auto;
    width: 100%;
}
.file-stats {
    background: none;
    margin: 15px 0;
    width: 100% !important;
}
.file-stats-inner {
    padding: 0;
}
.file-stats-inner > p {
    font-size: 1.20em;
    width: 20%;
    text-align: center;
    margin: 1em 0;
}
.file-stats-inner > p > span {
    margin: 0 auto;
    padding: 0 !important;
    width: 100% !important;
    color: #DDD !important;
}
.file-stats-inner .file-version {
    color: #0FADED;
}
.file-stats-inner .file-version .version {
    color: #fff;
}
.file-stats-inner .file-unique-dls {
    color: #0FADED;
}
.file-stats-inner .file-unique-dls .tipsied {
    color: #fff;
}
.file-stats-inner .file-total-dls {
    color: #0FADED;
}
.file-stats-inner .file-total-dls span {
    color: #fff;
}
.file-stats-inner .file-total-views {
    color: #0FADED;
}
.file-stats-inner .file-total-views span {
    color: #fff;
}
.file-stats .actions {
    margin: 0 60px;
    width: 90%;
}
.file-stats .actions > li {
    margin: 0 10px;
}
.file-stats .uploader {
    float: right;
    margin: 0;
    margin-bottom: .9em;
    margin-right: .9em;
}
.file-image-scroller {
    display: none;
}
#left-col {
    clear: both !important;
    float: none;
    width: 100%;
}
#right-col {
    display: none;
}
.homepageFeedAreaContainer {
    clear: both;
    margin: 0 auto;
    padding: 0;
    width: 100%;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
#feed-preferences {
    display: none;
}
.feed-tabs {
    border: 0;
}
#feed_tab_vid {
    border-right: 1px solid black !important;
}
#feed_tab_sub {
    border-right: 1px solid black !important;
}
.file-image-container {
    background-image: none !important;
}
.news-list {
    width: 100% !important;
}
.date-comments {
    background: none !important;
    background-color: #0FADED !important;
    color: black !important;
    height: 22px !important;
}
.date-comments .date {
    font-size: .9em;
}
.date-comments .right {
    display: none;
}
.news-list h2 {
    background: none !important;
    background-color: #0FADED !important;
    text-shadow: none;
    width: 100%;
}
.news-list h2 > a {
    color: black !important;
}
.news-list h2 > a > span {
    font-weight: bold !important;
}
#main-top {
    display: none;
}
.bottom-corner {
    background: none;
    margin: 0;
    width: 100% !important;
}
#hotfilesbar {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
#hotfiles-container {
    border: 0;
    margin: 0 auto;
    width: 100% !important;
    height: 275px;
}
#hotfiles {
    margin: 0 auto;
    width: 100% !important;
    height: 275px !important;
}
.hotfiles-box {
    background-color: #191919;
    opacity: 1 !important;
    right: 0 !important;
    width: 30%;
    height: 275px;
}
.hotfiles-text {
    font-size: .8em;
    right: 0 !important;
    width: 26%;
}
.hotfiles-text > strong {
    font-weight: bold !important;
    font-size: 1.10em;
}
#hotfilesbar {
    border: 0;
    margin: 0 auto;
    margin-bottom: .5em;
    width: 100% !important;
}
.hotfiles-element {
    width: 100% !important;
    height: 300px !important;
}
.hotfiles-element > a > img {
    width: 70%;
    height: 275px;
}
.hotPreviewImg {
    margin: 20px 20px;
    text-align: center;
    width: 154px;
}
.boxPreviewImgImage {
    border: 0;
    margin: 0 auto;
    width: 120px;
}
.boxPreviewImgImage img {
    width: 100%;
}
.bb-content {
    color: #CCC;
    font-size: 1em !important;
    padding: 0 1em 1em !important;
}
.bb-content > img {
    float: left;
    padding-top: 1.5em;
    margin: 0 1em 0 0 !important;
}
#UnderContent {
    background-color: #292929;
    background-image: none !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    padding: 0;
}
.files-tab-content {
    background-color: #292929;
    font-size: 1.4em !important;
}
.file-tabs {
    background-color: #303030;
    border-top: 0;
}
.file-tabs > ol {
    border-top: 0;
    margin-bottom: 4px;
    padding: 0px 0;
    width: 100% !important;
}
.file-tabs > ol > li {
    background: none;
    background-color: #333;
    box-shadow: 1px 2px 2px 0 rgba(0, 0, 0, 0.20);
    margin-right: .2em;
}
.file-tabs > ol > li:hover {
    background-color: #292929;
}
.file-tabs .active {
    background-color: #292929;
    box-shadow: 3px 2px 2px 0 rgba(0, 0, 0, 0.30);
}
.files-tab-header {
    background: none;
    background-image: none !important;
}
.files-tab-header h2 {
    font-size: 2em !important;
    letter-spacing: normal;
    text-shadow: none !important;
}
#footer {
    display: none;
}
#main-bottom {
    display: none;
}
.index-big-buttons {
    display: none;
}
.index-content-left {
    display: none;
}
.index-content-right {
    display: none;
}
.index-games-list .description {
    color: #CCC !important;
}
.comment-list > li > .right {
    width: 92.5% !important;
}
.game-link {
    background-color: #444;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: auto !important;
    padding: 0 5px;
}
.game-link:hover {
    background-color: #333;
}
.index-game-list {
    border: 0;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
.index-game-list .game {
    width: 155px;
}
.game-link .game {
    height: 205px !important;
    text-align: center;
}
.game-link .game > img {
    margin: 0 auto !important;
    width: 80px !important;
}
.game-link .game .name {
    color: #CCC;
    font-size: 1.2em !important;
}
.game-link .game .stats2 {
    font-size: .8em !important;
    margin: 0 auto !important;
}
.files-list > li {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    margin-bottom: 1em;
    padding-bottom: 0;
}
.files-list .file {
    background: none !important;
    background-color: #444 !important;
    color: #CCC !important;
    padding: 0 10px !important;
}
.files-list a.file {
    font-weight: bold !important;
    font-size: 1.25em;
    color: #0FADED !important;
}
.files-list a.file .top-number {
    font-weight: lighter !important;
    font-size: .85em !important;
    color: #CCC;
}
.files-list a.file .game-name {
    font-weight: lighter !important;
    font-size: .70em !important;
    color: #CCC;
}
.files-list .file:hover {
    background-color: #292929 !important;
}
.files-list .file-arrow {
    display: none !important;
}
.files-list .file-desc {
    font-size: 1em !important;
    width: 81.5% !important;
}
@media (min-width: 2000px) {
    .files-list .file-desc {
        width: 85.5% !important;
    }
}
.files-list .file-image {
    width: 15% !important;
}
@media (min-width: 2000px) {
    .files-list .file-image {
        width: 12.5% !important;
    }
}
.files-list .file-image img {
    height: 150px;
    width: 100% !important;
}
ho .subnav-border {
    border-bottom: 1px solid black !important;
    margin-bottom: 0 !important;
}
.action-buttons .tabbed-buttons .green {
    background-color: #444 !important;
}
.action-buttons .tabbed-buttons .green:hover {
    background-color: #333 !important;
    cursor: pointer;
}
.action-buttons .tabbed-buttons .green > a:hover {
    text-decoration: none !important;
}
.action-buttons .tabbed-buttons .active {
    background-color: #c62828 !important;
    color: #000 !important;
    cursor: pointer;
}
.action-buttons .tabbed-buttons .active:hover {
    background-color: #b71c1c !important;
}
.action-buttons .tabbed-buttons .active a {
    color: #CCC !important;
}
.action-buttons .tabbed-buttons .active a:hover {
    text-decoration: none !important;
}
#next {
    background-color: #444 !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    font-weight: bold !important;
    padding: .65em;
}
#next:hover {
    background-color: #313131 !important;
    text-decoration: none;
}
.radio-info {
    padding: .65em 0 .2em !important;
}
#hotfiles-container #hotNav #next {
    background: rgba(0, 0, 0, 0) !important;
}
.bottom-corner-s {
    display: none;
}
.images-filters {
    background: none;
}
.filters li.divider {
    background: none;
}
#tabsrc_1 {
    background-color: #444 !important;
}
#btAsrc {
    background: none;
    background-color: #444;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    padding: .6em 1.1em;
    text-align: center;
}
#btAsrc:hover {
    background-color: #333;
}
#btAsrc span {
    background: none;
    padding: 0;
}
.buttons {
    background-color: #444 !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 2em !important;
    margin-right: .4em;
    padding: 0 !important;
}
.buttons:hover {
    background-color: #333 !important;
}
.buttons a {
    background: none;
    font-family: 'Trebuchet MS', Helvetica, sans-serif;
    height: 100%;
}
.buttons a span {
    background: none;
    color: #0FADED;
    font-weight: bold !important;
    line-height: 40px;
}
.game-search > div > .buttons {
    height: 3em !important;
    vertical-align: middle;
    background-color: #444 !important;
}
.buttons > .primary {
    padding: 0 !important;
    width: 6em;
    box-shadow: none !important;
}
.popbox {
    padding: 0 !important;
}
.popbox a {
    background-color: #444;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    color: #CCC !important;
    display: inline-block;
    height: 2em;
    line-height: 35px;
    padding: 0 .5em;
}
.popbox a:hover {
    background-color: #333;
    text-decoration: none;
}
.bubble-open {
    background-color: #222 !important;
    box-shadow: none !important;
}
.bubble-open img {
    padding-bottom: .5em;
}
.files-tab-files-list li {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    margin: 1em auto;
    background-color: #222;
    padding: .5em;
    max-width: 97%;
    width: auto;
}
.files-tab-files-list li > .name {
    font-size: 1em;
    width: 100%;
}
.files-tab-files-list li > .name > a {
    padding-left: 1em;
}
.files-tab-files-list li > .unique, .files-tab-files-list li > .downloads, .files-tab-files-list li > .size {
    font-size: .75em;
}
.files-tab-files-list li > .buttons > .primary {
    width: auto;
    height: auto;
}
.files-tab-files-list li > .desc {
    padding: 1.25em 1em;
    color: #DDD;
    border-color: #444;
    margin-bottom: .25em;
}
.files-tab-files-list li > .version {
    font-size: .85em;
    padding: .5em .5em 0 0;
    font-weight: bold !important;
}
.files-tab-files-list li > .upload-date {
    font-size: .85em;
    font-weight: bold !important;
    padding: .5em .5em 0 0;
}
.files-tab-files-list li > .buttons > .primary > span {
    padding: 1em;
}
.changelog {
    color: #CCC;
    margin-top: 0;
    padding: 0 1em;
}
.changelog h2 {
    color: #CCC;
}
.files-tab-tags {
    padding: 1em;
}
.files-tab-files-list > li > .popbox > .bubble-collapse > .bubble-box {
    bottom: -4em !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    left: 22.6em !important;
}
.articlesFeedArea {
    width: 100%;
    margin: 0 auto;
}
.latestmodsFeedArea > div:nth-child(4n) {
    display: none;
    clear: none !important;
    border: 1px red solid;
}
.latestmodsFeedArea > .latestFeedContainer {
    margin: .5em 1.35em !important;
}
.latestimagesFeedArea > div:nth-child(4n) {
    display: none;
    clear: none !important;
    border: 1px red solid;
}
.latestimagesFeedArea > .latestFeedContainer {
    margin: .5em 1.35em !important;
}
.latestvideosFeedArea > div:nth-child(4n) {
    display: none;
    clear: none !important;
    border: 1px red solid;
}
.latestvideosFeedArea > .latestFeedContainer {
    margin: .5em 1.35em !important;
}
#hotfiles-title {
    display: none;
}
#hotNav {
    left: auto;
    right: 0;
    width: 30%;
    text-align: center;
    margin-bottom: .5em;
}
#hotfiles-container #hotNav > #next {
    background-color: #444 !important;
    vertical-align: middle;
}
#hotfiles-container #hotNav > #next:hover {
    background-color: #333 !important;
}
#hotNav > #next > img {
    display: none;
}
#hotNav > #next:after {
    content: "<< Prev";
}
#hotfiles-container #hotNav > #prev {
    background-color: #444 !important;
    vertical-align: middle;
    padding: .65em;
    font-weight: bold !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
#hotfiles-container #hotNav > #prev:hover {
    background-color: #333 !important;
    text-decoration: none;
}
#hotNav > #prev > img {
    display: none;
}
#hotNav > #prev:after {
    content: "Next >>";
}
#horizontalpub {
    display: none;
}
}
