css:
@-moz-document url-prefix("https://ticktick.com/") {
/* wider right column for task information/description */

@media screen and (max-width: 920px) {
  .right-menu-t {
      top: 26px;
  }

  .left-menu-t {
       top: 24px;
  }
}
  
@media screen and (min-width: 921px) {
  .g-center {
      right: 0;
      width: 30%;
      left: auto;
  }
  .g-right {
      left: 0;
      width: 70%;
      border: none;
  }
  
  .td-btns {
      margin-left: 30px;
  }

  .left-menu-t {
       top: 28px;
  }

  .td-body {
	border-right: 1px solid #f4f4f4;
  }
}
  
@media screen and (min-width: 1021px) {
  .g-center {
      right: 0;
      width: 29%;
      left: auto;
  }
  .g-right {
      left: 23%;
      width: 48%;
      border: none;
  }

  .td-btns {
      margin-left: 25px;
  }

  .g-left{
      width: 23%;
  }
}
  
@media screen and (min-width: 1300px) {
  .g-center {
      right: 0;
      width: 29%;
      left: auto;
  }
  .g-right {
      left: 19%;
      width: 52%;
      border: none;
  }

  .g-left{
      width: 19%;
  }
}
  
.td-footer .toolbar {
    width: 25%;
}

.td-header{
  padding: 17px 14px 4px 20px;
}

.td-header {
    background-color: #fff;
	border-bottom: 1px solid #f4f4f4;
}
  
.td-footer {
    color: #fff;
    border-top: 1px solid #f4f4f4;
}

.td-header .task-progress-wrapper .task-progress {
    height: 12px;
    background-color: #a24c9f;
    opacity: 1;
}

.td-header .task-progress-wrapper .task-progress-ruler .task-progress-item:after {
    height: 20px;
    width: 2px;
    top: 0px;
    background-color: #a24c9f;
}

.td-header .task-progress-wrapper .task-progress-ruler .task-progress-item span {
    top: 22px;
  	color: #a24c9f;
}

.link, .link:visited, a, a:visited, h1, h2, h3, h4, h5, h6 {
    color: #a24c9f;
}

.task.active, .task.selected:hover {
    background-color: #fffff7;
    border: 1px solid #eee;
    border-left: none;
}

.task:hover {
    background-color: #fffff7;
}

/* smaller font task-pane */
.task{
 height: 28px   
}
    
.task .t-inner {
    height: 26px;
    line-height: 26px;
}

.text-def {
    font-size: 12px;
}

h3, h4, h5 {
    font-size: 18px;
}

/* monospace font for description text */
.td-content,
.td-sub-desc{
    font-family: monospace !important;
    color: #444!important;
}

.td-body, .td-body textarea {
    background-color: #fffff7;
}

.td-body {
  padding-top: 58px;
}

.td-body .td-task-desc {
    padding-bottom: 20px;
}

.td-body .td-task-text,.td-body .sub-task, .td-body .td-task-desc {
    padding: 0 40px 0 40px;
}
  
.td-body .td-task-text, .td-body .td-task-desc {
    padding-bottom: 30px;
}

.td-body .td-caption {
    padding: 30px 14px 30px 40px;
}
}
