css:
@-moz-document domain("apod.nasa.gov") {
	/* RESET ------------------------------------------------------------ */

	* {
		box-sizing: border-box;
		margin: 0;
		padding: 0;
	}

	center {
		text-align: left;
	}

	/* GENERAL ---------------------------------------------------------- */

	html, body {
		font-size: 100%;
	}

	body {
		background: #312F2B;
		color: #eee;
		font-family: 'Source Serif Pro', Heuristica, 'PT Serif', Cambria, Georgia, serif;
		line-height: 1;
		margin: 0 auto;
		padding: 1em;
	}

	img {
		max-width: 100%;
	}

	a:link {
		color: #9cc;
		border-bottom: 1px dotted #455;
		text-decoration: none;
	}

	a:visited {
		color: #baa;
		border-bottom: 1px dotted #544;
	}

	a:hover,
	a:focus {
		border-bottom: 1px solid #366;
		color: #9ff;
		text-shadow: 0 1px 0 #000, 0 0 5px #000, 0 0 20px #fff;
	}

	a:visited:hover,
	a:visited:focus {
		border-bottom: 1px solid #644;
		color: #fcc;
	}

	::-moz-selection {
		background: #9cc;
		color: #333;
		text-shadow: none !important;
	}
	::selection {
		background: #9cc;
		color: #333;
		text-shadow: none !important;
	}

	h1 {
		color: #d0cfcf;
		font-family: inherit;
		font-size: 2em;
		font-weight: bold;
		margin: -0.5em -0.5em 0 -0.5em;
		padding: 0.25em 0.5em;
	}
}

@-moz-document url("http://apod.nasa.gov/apod/archivepix.html") {
	/* HEADER ----------------------------------------------------------- */

	body > center:first-child {
		background: #272522;
		margin: -1em -1em -0.5em;
		padding: 0.5em 1em;
	}

	body > center:first-child img {
		display: none;
		visibility: hidden;
	}

	body > center:first-child h1 {
		background-image: url(data:image/png;base64,);
		background-position: 0 center;
		background-repeat: no-repeat;
		background-size: auto 2em;
		margin: 0;
		padding: 1rem 1rem 1rem 2.5em;
		text-shadow: 0 1px 10px #000;
	}

	/* NAVBAR----------------------------------------------------------- */

	body > center + center {
		padding-left: 2em;
		position: relative;
		top: -1em;
	}

	body > center + center > hr {
		border-color: #1f1d1b;
		border-style: solid;
		margin: 0 -2em 0 -3em;
		position: relative;
		z-index: 10;
	}

	body > center + center > hr:first-child {
		border-width: 0 0 2em 0;
		bottom: -2em;
		margin-bottom: 0.25em;
		margin-top: -1em;
	}

	body > center + center > hr:last-child {
		border-width: 2em 0 0 0;
		margin-bottom: -1em;
		margin-top: 0.25em;
		top: -1.8em;
	}

	body > center + center > a { /* nav links */
		border-bottom: 0 !important;
		font-variant: small-caps;
		position: relative;
		z-index: 100;
		text-transform: lowercase;

		display: inline-block;
		padding: 0.25em; /* total height = 1.75em */
	}

	/* LINKS ------------------------------------------------------------ */

	body > b {
		display: block;
		font-weight: normal;
		padding: 0 2.5em;
		position: relative;
		top: -1em;
	}

	body > b a {
		margin-left: 0.25em;
		padding: 0 0.25em;
	}

	body > b a::after {
		content: "\A";
		font-size: 200%;
		white-space: pre;
	}

	body > b br {
		display: none;
	}

	/* FOOTER ----------------------------------------------------------- */

	body > b + hr {
		display: none;
		visibility: hidden;
	}

	body > center:last-child {
		background: #1f1d1b;
		font-size: 80%;
		font-style: italic;
		line-height: 2;
		margin: 0 -1rem -1rem;
		padding: 1.25rem 3.5rem 1rem;
	}
}

@-moz-document url("http://apod.nasa.gov/apod/astropix.html"), url-prefix("http://apod.nasa.gov/apod/ap") {
	/* HEADER ----------------------------------------------------------- */

	h1, h1 + p {
		background: #272522;
		text-align: center;
		text-shadow: 0 1px 10px #000;
	}

	h1 + p { /* Discover the cosmos! Each day... */
		display: none; /* show for bigger screens, below */
		font-style: italic;
		line-height: 1.5;
		margin: 0 -2em;
		padding: 1em 2em;
		text-align: center;
	}

	h1 + p a {
		font-weight: bold;
	}

	/* IMAGE ------------------------------------------------------------ */

	h1 + p + p { /* date */
		color: #d0cfcf;
		font-size: 2em;
		font-style: italic;
		font-variant: small-caps;
		margin: 0.5em auto;
		text-align: center;
		text-transform: lowercase;
	}

	h1 + p + p a {
		border-bottom: 0 !important;
	}

	h1 + p + p img,
	h1 + p + p object,
	h1 + p + p iframe { /* picture */
		bottom: -0.5em;
		position: relative;
	}

	h1 + p + p img {
		border: 1px solid #000;
		box-shadow: 0 0 0.25em rgba(0, 0, 0, 0.75);
	}

	h1 + p + p a img:hover {
		box-shadow: 0 0 0.25em rgba(255, 255, 255, 0.25);
	}

	/* PICTURE NAME / CREDITS ------------------------------------------- */

	center + center {
		margin: 1em auto;
		text-align: center;
	}

	center + center,
	center + center > b { /* credits */
		font-variant: small-caps;
		font-weight: normal;
		line-height: 1.5;
		text-transform: lowercase;
	}

	center + center > b:first-child { /* picture name */
		color: #d0cfcf;
		display: block;
		font-size: 2em;
		font-variant: normal;
		font-weight: bold;
		line-height: 1;
		text-shadow: 0 1px 0 #000, 0 2px 2px #000;
		text-transform: none;
	}

	center + center > b:first-child + br {
		line-height: 0.5rem;
	}

	/* EXPLANATION ------------------------------------------------------ */

	center + center + p { /* explanation */
		line-height: 1.5;
		margin: 1em auto;
		max-width: 42em;
		text-align: left;
	}

	center + center + p > b:first-child { /* Explanation: */
		display: none;
	}

	/* FOOTER ----------------------------------------------------------- */

	body > p + p + center { /* footer */
		background: #272522;
		clear: both;
		color: #d0cfcf;
		display: block;
		line-height: 1.5;
		margin: 0 -1em -1em -1em;
		padding: 1em;
		text-align: center;
	}

	hr {
		border-color: #1f1d1b;
		border-style: solid;
		margin: 0 -2em;
		position: relative;
		z-index: 10;
	}

	p + hr { /* first hr */
		border-width: 0 0 2em 0;
		bottom: -2em;
		margin-bottom: 0.25em;
		margin-top: -1em;
	}

	a + hr { /* second hr */
		border-width: 2em 0 0 0;
		margin-bottom: -1em;
		margin-top: 0.25em;
		top: -1.8em;
	}

	hr ~ a { /* nav links */
		border-bottom: 0 !important;
		font-variant: small-caps;
		position: relative;
		z-index: 100;
		text-transform: lowercase;

		display: inline-block;
		padding: 0.25em; /* total height = 1.75em */
	}

	hr + a,
	hr ~ a:last-of-type { /* prev/next links */
		color: transparent;
		font-weight: bold;
		bottom: -0.05em;
		text-transform: uppercase;
	}
	hr + a:link,
	hr + a:visited,
	hr ~ a:last-of-type:link
	hr ~ a:last-of-type:visited { /* prev/next links */
		color: transparent;
	}
	hr + a:hover,
	hr + a:focus,
	hr ~ a:last-of-type:hover,
	hr ~ a:last-of-type:focus {
		color: transparent;
		text-shadow: none;
	}

	hr + a::before,
	hr ~ a:last-of-type::after {
		display: inline-block;
		font-size: 4em;
		font-variant: normal;
		line-height: 0;
		padding: 0;
		text-transform: none;
		position: relative;
		bottom: -0.175em;
		vertical-align: baseline;
	}
	hr + a:link::before,
	hr + a:link::after, 
	hr ~ a:link:last-of-type::before,
	hr ~ a:link:last-of-type::after {
		color: #9cc;
	}
	hr + a:visited::before,
	hr + a:visited::after, 
	hr ~ a:last-of-type:visited::before,
	hr ~ a:last-of-type:visited::after {
		color: #baa;
	}
	hr + a:hover::before,
	hr + a:focus::before,
	hr + a:hover::after, 
	hr + a:focus::after, 
	hr ~ a:last-of-type:hover::before,
	hr ~ a:last-of-type:focus::before,
	hr ~ a:last-of-type:hover::after,
	hr ~ a:last-of-type:focus::after {
		color: #9ff;
		text-shadow: 0 1px 0 #000, 0 0 5px #000, 0 0 20px #fff;
	}
	hr + a:visited:hover::before,
	hr + a:visited:focus::before,
	hr + a:visited:hover::after, 
	hr + a:visited:focus::after, 
	hr ~ a:last-of-type:visited:hover::before,
	hr ~ a:last-of-type:visited:focus::before,
	hr ~ a:last-of-type:visited:hover::after,
	hr ~ a:last-of-type:visited:focus::after {
		color: #fcc;
	}

	hr + a::after { /* prev link */
		content: ' Prev';
	}
	hr + a::before {
		content: '«';
		margin-right: -0.6rem;
	}

	hr ~ a:last-of-type::before { /* next link */
		content: 'Next ';
	}
	hr ~ a:last-of-type::after {
		content: '»';
		margin-left: -0.6rem;
	}

	hr + p { /* site credits */
		font-size: 0.8em;
		line-height: 1.5;
		opacity: 0.8;
	}

	/* NOT A PHONE... --------------------------------------------------- */

	@media screen and (min-width: 650px) {
		h1 {
			padding: 0.25em 0.5em 0 0.5em;
		}
		h1 + p { /* Discover the cosmos! Each day... */
			display: block;
			font-size: 0.8em;
			padding-left: 16%;
			padding-right: 16%;
		}
		body > p + p + center .db-wrapper { /* Digg button */
			float: right;
			margin-left: -100%;
		}
		body > p + p + center br { /* br after Digg button */
			line-height: 0;
		}
	}

	/* BIGGER ----------------------------------------------------------- */

	@media screen and (min-width: 950px) {
		body {
			padding: 2em;
		}
		h1 {
			margin: -1em -1em 0 -1em;
			padding: 0.5em 1em 0 1em;
		}
		h1 + p { /* Discover the cosmos! Each day... */
			font-size: 1em;
			padding-left: 26%;
			padding-right: 26%;
		}
		h1 + p + p { /* date */
			margin: 1em auto;
		}
		center + center + p { /* explanation */
			font-size: 1.1em;
			max-width: 30em;
		}
		body > p + p + center { /* footer */
			margin: 0 -2em -2em -2em;
			padding: 1em 2em;
		}
	}

	/* EVEN BIGGER ------------------------------------------------------ */

	@media screen and (min-width: 1450px) {
		h1 + p { /* Discover the cosmos! Each day... */
			padding-left: 28%;
			padding-right: 28%;
		}
		h1 + p + p { /* date, image */
			float: left;
			margin: 0.5em auto;
			max-width: 50%;
			padding: 0 1em 1em 0;
			text-align: right;
		}
		center + center { /* title, credits */
			float: left;
			max-width: 50%;
			text-align: left;
		}
		center + center + p { /* explanation */
			float: left;
			margin-top: 0;
			max-width: 50%;
			padding-bottom: 1em;
		}
	}

	/* THE BIGGEST ------------------------------------------------------ */

	@media screen and (min-width: 1850px) {
		h1 + p { /* Discover the cosmos! Each day... */
			padding-left: 33%;
			padding-right: 33%;
		}
		h1 + p + p { /* date, image */
			max-width: 55%;
		}
		center + center { /* title, credits */
			max-width: 45%;
		}
		center + center + p { /* explanation */
			font-size: 1.25em;
			max-width: 45%;
		}
	}
}
