css:
@-moz-document domain("azreader.net") {
.contentBody { /*阅读界面颜色修改*/
    max-width: 1100px;
    background-color: rgb(38,47,49);
    color: rgb(190,190,190);
    font-size: 20px;
}
.titleSpan { /*标题字体加大*/
    max-width: 900px;
    height: 30px;
    font-size: 140%;
    }
.feedTitleSpan { /*信息来源下移一点*/
    top: 30px;
}
.feedEntryBar { /*标题栏间隙加大*/
    margin-bottom: 2px;
}
#leftTreePanel {
    background-color: rgb(38,47,49);
    color: rgb(190,190,190);
}
#contentPanel {
    background-color: rgb(38,47,49);
    color: rgb(190,190,190);
}
}
