css:
@-moz-document domain("jisho.org") {
h1.logo a {
    display: block;
    width: 132px;
    height: 64px;
    background: url(http://i.imgur.com/XYbVGpi.gif);
    background-size: 132px 64px;
    text-indent: -10001px;
}
#what_is_this {
    color: #999999;
}
header.row,
header.page {
    margin-top: 8px;
    background-color: #222222;
    border-bottom: 0px solid #ccc;
}
.tabs dd>a,
.tabs .tab-title>a {
    background-color: #333333;
    color: #bababa;
}
svg[class^="stroke_order_diagram"] {
    background: #0157a0;
}
body {
    background-color: #222222;
    padding: 0 15px;
    color: #b9b9b9;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: "Helvetica Neue", Helvetica, Arial, "HiraKakuPro-W3", "Hiragino Kaku Gothic Pro W3", "Hiragino Kaku Gothic Pro", "ヒラギノ角ゴ Pro W3", "メイリオ", Meiryo, "游ゴシック", YuGothic, "ＭＳ Ｐゴシック", "MS PGothic", "ＭＳ ゴシック", "MS Gothic", sans-serif;
    color: #43ac6a;
}
form.search .main {
    position: relative;
    left: 0px;
    top: 0px;
    background-color: #5e5e5e;
    border-radius: 5px;
    padding: 4px;
    box-sizing: content-box;
}
form.search .inner {
    width: 100%;
    border-radius: 2px;
    background: #555555;
    padding: 4px;
}
form.search .search-form_clear-button {
    float: right;
    margin: 0;
    padding: 7px 7px 2px 5px;
    border: none;
    background: #555555;
    font-weight: normal;
    color: #555;
}
.clear-icon {
    padding: 4px;
    width: 22px;
    height: 22px;
    fill: #bababa;
}
.f-dropdown {
    background: #313131;
    border: solid 1px #000000;
}
body a:hover {
    color: #cccccc;
}
form.search .submit {
    float: right;
    margin: 0;
    padding: 7px 7px 2px 8px;
    border: none;
    background: #555555;
    font-weight: normal;
    color: #b9b9b9;
    border-radius: 0 1px 1px 0;
}
input[type="text"],
input[type="password"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="week"],
input[type="email"],
input[type="number"],
input[type="search"],
input[type="tel"],
input[type="time"],
input[type="url"],
input[type="color"],
textarea {
    color: #b9b9b9;
}
button,
.button {
    background-color: #555555;
    color: #bababa;
}
.reveal-modal,
dialog {
    background-color: #333333;
}
.radical_table {
    background: -webkit-repeating-linear-gradient(top, #fff, #fff 32px, #EDF9FF 32px, #EDF9FF 64px);
    background: repeating-linear-gradient(180deg, #555555, #555555 32px, #666 32px, #666 64px);
    background-repeat: repeat-y;
    background-color: #555555;
}
.radical_table .radical {
    color: #45a769;
}
form.search .results .result,
form.search .results {
    background-color: #3c3c3c;
}
.result_label {
    color: #b9b9b9 !important;
    background: #222222 !important;
}
.handwriting .panel {
    background-color: #434343;
}
#zen_bar li[data-pos=Noun] a,
#zen_bar li[data-pos=Suffix] a,
#zen_bar li[data-pos=Prefix] a,
#zen_bar li[data-pos=Symbol] a,
#zen_bar li[data-pos=Interjection] a,
#zen_bar li[data-pos=Propernoun] a {
    color: #2e9755;
    border-bottom: 1px solid #2e9755;
}
#zen_bar .furigana {
    color: #558132;
}
}
