css:
@-moz-document domain("www.wuxiaworld.com") {
body {
	background-color: #3B3F45!important;
	color: #C8F7C5;
	font-family: "Book Antiqua";
	margin: 0;
}
body,
button,
input,
select,
textarea {
	color: #C8F7C5;
	font-size: 16px;
	font-size: 1rem;
	line-height: 1.5;
}
body.custom-background {
	background-image: none;
}
a,
a:hover,
a:visited {
	color: white!important;
	text-decoration: none;
	border-bottom: 1px solid #000;
}
@media screen and (max-width: 1223px) {
	#primary {
		width: 100%;
	}
	.ww_action_click .right {
		width: 699px;
	}
	.post,
	#comments,
	.hentry,
	.error-404,
	.not-found,
	.searchPage,
	.page-header {
		background: #575B63;
		width: 100%;
		font-size: 16px;
	}
}
@media screen and (min-width: 1224px) {
	#primary {
		width: 100%;
	}
	.ww_action_click .right {
		width: 899px;
	}
	.post,
	#comments,
	.hentry,
	.error-404,
	.not-found,
	.searchPage {
		background: #575B63;
		width: 100%;
		font-size: 16px;
	}
}

.ww_action_click .right {
	background: #575B63;
}
body.page #primary {
	position: relative;
	top: 110px;
}
body.single #primary {
	position: relative;
	top: 110px;
}
#secondary {
	width: 100%;
	max-width: inherit;
	position: absolute;
	top: 57px;
	left: 0px;
	clear: both;
}
.widget {
	display: none;
}
.widget_simple_progress_bar {
	display: block;
	float: left;
	background: inherit;
}
.widget_simple_progress_bar:nth-of-type(1) {
	float: left;
}
.widget_simple_progress_bar:nth-of-type(2) {
	float: right;
}
.stacked-list-item:hover .ww_action_click .right {
	background: #3B3F45;
}
.sp-wrap-default {
	background: none repeat scroll 0% 0% #E9E9E6;
	border-color: #C3CBD1;
	color: black;
}
.sp-wrap-default {
	background: none repeat scroll 0% 0% #E9E9E6;
	border-color: #C3CBD1;
	color: black;
}
.sp-wrap-default a {
	color: black !important;
}
div[itemprop=articleBody] p,
div[itemprop=articleBody] span {
	background: inherit !important;
	color: #C8F7C5 !important;
}
}
