@-moz-document domain(lowcygier.pl) {


body {
    background-image: none!important;
    background-color: #242426;
    color: #999999;
}


#header .wp-united-loginuser-info a {
    color: white!important;
}

#precontent {

    background: #353c3b;
}

#content-main > .post {
    border: none;
    background-color: #282b2c;
}

.post-title, .post-title a {
    color: #f0f0f0
;
}

a {
    color: #f0f0f0;
}

a:hover {
        color: #0287c7;
}

#content-main > .post, #disqus_thread, .forum-content-area {
    border-radius: 0px;
}

.post-header .post-icon {
    border-radius: 0px;
}

#sidebar1, #sidebar2 {
    border-radius: 0px;
}

#sidebar1, #sidebar2 {
    border: none;
    background-color: #282b2c;
}

.srp-post-content-container {
    padding: 10px;
	border-bottom: 1px solid #404645;
}

.sidebar ul li {
    border-bottom: 1px solid #404645;
}

.lowcy-category-filter .filter_pre {
    width: 100%;
    background-color: rgba(53, 60, 59, 0.5);
    border: none;
}


.lowcy-category-filter .filter_pre:hover {
    background-color: rgba(53, 60, 59, 0.8);
}

.series-child-title, .series-child-title a, .series-pagination h3, .series-pagination h3 a {
    color: #f0f0f0;
}

.next-post {
    width: 45%;
}

.previous {
    width: 45%;
}

.default_searchform input[type="text"] {
    border: none;
    background: #242426;
    color: #8a8f95;
}

.preindex_searchform input[type="text"] {
    background: #242426;
    color: #8a8f95;
    border: 1px solid #353c3b;
}


#pricetracker-right {
    float: left;
    width: 850px;
    height: 100%;
    background-color: #282b2c;
}

#pricetracker-stop {
    background-color: #242426;
}

#pricetracker-stop {
    background-color: #242426;
}


.price-news a {
    color: #999 !important;
    padding: 6px;
}

#pricetracker-holder {
    background: #353c3b;
}

#disqus_thread {
    background-color: #282b2c;
    border: none;
}

body.dark {
    color: #999999!important;
}



/*FORUM*//*FORUM*//*FORUM*/
/*FORUM*//*FORUM*//*FORUM*/
/*FORUM*//*FORUM*//*FORUM*/
/*FORUM*//*FORUM*//*FORUM*/


.forum-content-area li.row dl.icon dt a {
    color: white!important;
}

.forum-content-area .navbar {
    background-color: #282b2c;
}

.forum-content-area ul.navlinks {
    border-bottom-color: #242426;
}

.forum-content-area {
    color: #999999;
    background-color: #282b2c;
    border: none
}


.forum-content-area ul.forums {
    background: #282b2c;
}

.forum-content-area ul.topiclist li {
    color: #999;
}

.forum-content-area a.forumtitle {
    color: #f0f0f0;
}

.forum-content-area a.forumtitle:hover {
    color: #0287c7;
}

.forum-content-area a:visited {
    color: #f0f0f0;
}

.forum-content-area a:link {
    color: #f0f0f0;
}

.forum-content-area a.topictitle:hover {
    color: #0287c7;
}

.forum-content-area li.row:hover {
    background: rgba(53, 60, 59, 0.5);
    -webkit-transition: all 0.2s ease-out;
    -moz-transition: all 0.2s ease-out;
    -ms-transition: all 0.2s ease-out;
    -o-transition: all 0.2s ease-out;
}

.forum-content-area ul.topiclist dd {
    border-left: none;
}






#content-main .forum-content-area {
    border: none;
}

.forum-content-area li.row {
    border-top: none;
    border-bottom: 1px solid #242426;
}

.forum-content-area li.row strong {
    color: #999;
}

.deleted_topic, .deleted_post {
    background-color: #000000 !important;
}

ul.topiclist {
    background: #353c3b;
}

.forum-content-area select {
    border-color: #282b2c;
    background-color: #242426;
    color: #999;
}

.forum-content-area .inputbox {
    background-color: #242426;
    border-color: #242426;
    color: #999;
}

.forum-content-area .inputbox {
    background-color: #242426;
    border-color: #242426;
    color: #999;
}

.forum-content-area .post:target .content {
    color: #999!important;
}

.forum-content-area #message-box textarea {
    color: #999;
    border: 1px solid #535758;
}

.forum-content-area .postbody h3.first {
    font-size: 1.7em;
    color: white!important;
}

.forum-content-area .post:target .content {
    color: #ffffff;
}


/*ikony*/

.icon-rules {
    background-image: none;
}

.forum-content-area .icon-faq {
    background-image: none;
}

.icon-medal {
    background-image: none;
}

.forum-content-area .icon-members {
    background-image: none;
}

.forum-content-area .icon-logout {
    background-image: none;
}

.forum-content-area .icon-ucp {
    background-image: none;
}

.forum-content-area .icon-home {
    background-image: none;
}

.forum-content-area .sitehome, .forum-content-area .icon-faq, .icon-rules, .forum-content-area .icon-members, .forum-content-area .icon-home, .forum-content-area .icon-ucp, .forum-content-area .icon-register, .forum-content-area .icon-logout, .forum-content-area .icon-bookmark, .forum-content-area .icon-bump, .forum-content-area .icon-subscribe, .forum-content-area .icon-unsubscribe, .forum-content-area .icon-pages, .forum-content-area .icon-search {
    background-position: 0 50%;
    background-repeat: no-repeat;
    background-image: none;
    padding: 0;
}

/*PODFORUM*/

ul.topiclist {
    background: #353c3b;
}

.forum-content-area .bg1 {
    background-color: #282b2c;
}

.forum-content-area .bg2 {
    background-color: #282b2c;
}

.forum-content-area div.rules {
    background-color: #242426;
    color: #D31141;
    padding: 10px 20px;
}

.forum-content-area .pagination span a, .forum-content-area .pagination span a:link, .forum-content-area .pagination span a:visited {
    color: #666;
    background-color: #242426!important;
    border-color: #242426;
}

.forum-content-area .pagination span a, .forum-content-area .pagination span a:link, .forum-content-area .pagination span a:visited {
    color: #666;
    background-color: #ECEDEE;
    border-color: #242426;
}

.forum-content-area .row .pagination span a:hover, .forum-content-area li.pagination span a:hover {
    color: #999;
}


/*STRONA FORUM*/

.forum-content-area .content {
    color: #999;
}

.forum-content-area .postprofile {
    border-right: 1px solid #242426;
}

.forum-content-area .postprofile strong {
    color: #999;
}

.forum-content-area .postlink:hover {
    color: #0287c7;
}

.forum-content-area blockquote {
    background-color: #282b2c;
    color: #777;
    background-image: none;
    border-color: #333333;
}

.forum-content-area .signature {
    border-top-color: #242426;
}

.forum-content-area .signature a {
    color: #999
}

.forum-content-area .panel {
    background-color: #242426;
    color: #ffffff;
}

.forum-content-area label {
    color: #ffffff;
}

.forum-content-area .content h2, .forum-content-area .panel h2 {
    color: white;
    border-bottom-color: #282b2c;
}

.forum-content-area hr {
    border-color: #242426;
    border-top-color: #242426;
}

.forum-content-area .post:target h3 a {
    color: white!important;
}

.forum-content-area .post:target h3 a {
    color: white!important;
}

/*CHAT*/

div.postbody.mChatBodyFix {
    background-color: #282b2c !important;
    border-left: #282b2c;
    margin: 0px! important;
}

div.mChatBG1 {
    background-color: #282b2c;

    border-bottom: 1px dotted black;
}

div.mChatHover:hover {
    background-color: #353C3B;
}

.forum-content-area .postbody {
    color: #999;
}

.forum-content-area .username-coloured {
    color: #C5B358!important;
}

div.mChatBG2 {
    background-color: #242426;
    border-bottom: 1px dotted black;
}

.forum-content-area h3 {
    border-bottom-color: #242426;
    color: white;
}

.forum-content-area .inputbox:focus {
    color: #ffffff;
}

}
