css:
@-moz-document domain("forosperu.net") {
#content .pageContent {
    background-color: transparent
}  
body {
    color: rgb(252, 252, 255);
    background: url(https://wallpaperscraft.com/image/black_background_pattern_light_texture_55291_2560x1440.jpg)#000 fixed!important;
    background-size:cover!important
}
.primaryContent {
    background-color: transparent
}
.messageUserBlock {
    background: transparent;
    border: 1px solid rgb(49, 49, 49);
}
.messageUserBlock .arrow span {
    border-left-color: rgb(49, 49, 49);
}
.messageUserBlock div.avatarHolder {
    background-color: rgb(49, 49, 49);
}
.secondaryContent {
    background: transparent none repeat-x top;
    border-bottom: 1px solid rgb(49, 49, 49);
}
.sectionMain {
    background-color: transparent;
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    background-color: rgb(49, 49, 49);
}
.node .nodeLastPost {
    background: rgb(49, 49, 49) none repeat-x top;
    border: 1px solid rgb(60, 60, 60);
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    border-bottom: 1px solid rgb(60, 60, 60);
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    border-bottom: 1px solid rgb(60, 60, 60);
}
a:link,
a:visited,
.primaryContent a,
.secondaryContent a {
    color: rgb(153, 153, 153);
    text-decoration: bold
}
.TrophyProgressMeter {
    background: rgb(60, 60, 60);
    border: 1px solid rgb(60, 60, 60);
}
.breadcrumb {
    font-size: 11px;
    background: rgb(49, 49, 49) none repeat-x top;
    border: 1px solid rgb(49, 49, 49);
}
.discussionListItem {
    background-color: rgb(49, 49, 49);
    border-bottom: 1px solid rgb(60, 60, 60);
}
.discussionListItem .posterAvatar,
.discussionListItem .stats {
    background: rgb(49, 49, 49) none repeat-x top;
}
.discussionListItem .stats dl {
    border-left: 1px solid rgb(60, 60, 60);
    border-right: 1px solid rgb(60, 60, 60);
}
.messageList .message {
    border-top: 1px solid rgb(49, 49, 49);
}
.message .likesSummary {
    border: 1px solid rgb(49, 49, 49);
}
.bbCodeQuote .quoteContainer {
    background: rgb(49, 49, 49) none repeat-x top;
}
.bbCodeQuote .quoteContainer .quoteExpand {
    background: linear-gradient(to bottom, rgba(239, 228, 228, 0) 0%, rgb(14, 14, 14) 80%);
}
.redactor_toolbar {
    background: rgb(49, 49, 49) none repeat-x top;
    border-bottom: 1px solid rgb(49, 49, 49);
}
.redactor_box {
    border: 1px solid rgb(49, 49, 49);
}
.quickReply {
    border-top: 1px solid rgb(49, 49, 49);
}
.avatar img,
.avatar .img,
.avatarCropper {
    background-color: rgb(60, 60, 60);
    border: 1px solid rgb(60, 60, 60);
}
.PageNav a {
    background-color: rgb(49, 49, 49);
}
.messageUserBlock .arrow {
    border-left-color: rgb(49, 49, 49);
}
.discussionListItems .unread .title a {
    color: #7c7c7c;
}
.hasJs .bbCodeSpoilerText {
    background-color: rgb(49, 49, 49);
    border: 1px solid rgb(49, 49, 49);
}
#exposeMask {
    background-color: #000;
}
.searchResult .snippet a {
    color: rgb(198, 198, 198);
}
.tabs {
    background: transparent
}
.actionFilterRow {
    background: transparent
}
.gridSection {
    background: transparent
}
.profilePage
.tabs.mainTabs{
    background: transparent
}
.Menu .primaryContent {
    background-color: rgb(51, 51, 51);
}
.Menu .secondaryContent {
    background-color: rgb(49, 47, 47);
}
.Menu .sectionFooter {
    background-color: rgb(51, 51, 51);
}
a.PageNavPrev, a.PageNavNext {
    color: rgb(252, 252, 255);
}
}
