.blue3 .is2d .cg-board,
#top div.color_demo.blue3
{
background-image:url("http://i.imgur.com/iHtoJwW.jpg") !important;
}

/* Dark Marble */

.canvas .is2d .cg-board,
#top div.color_demo.canvas
{
background-image:url("http://images.chesscomfiles.com/chess-themes/boards/marble/80.png") !important;
}


/* Grey Blue Blur */

.wood .is2d .cg-board, #top div.color_demo.wood {
	background-image:url("http://i.imgur.com/0IVI7BU.png")  !important;
	}

