.wood3 .is2d .cg-board, #top div.color_demo.wood3 {
background-image: url(http://lichess1.org/assets/images/board/yellow.png) ;
}


/* Newspaper Board */


.maple .is2d .cg-board, #top div.color_demo.maple  {background-image:url("https://images.chesscomfiles.com/chess-themes/boards/newspaper/80.jpg")  !important; }  
 .theme:first-child {display: block !important; border: 1px solid #ff8100; padding: 2px !important; margin: 5px;}  
  .theme:first-child:hover {
    border-color: burlywood;
    box-shadow: 0 0 10px #fff;
  }

/* White Aqua */

.green .is2d .cg-board, #top div.color_demo.green {
