css:
@-moz-document domain("t24.com.tr") {
/* T24 main page */

.advertorial, .top-banner-wrapper, .adsbygoogle {
    display: none !important;
}
/* banners */

#google_image_div, #leftpsdiv {
 display: none !important;
}

.outer-wrapper > div > [data-google-query-id] {
 display: none !important;
}

.cdxhd_catfish, .cdxhd_imagead, .cdxhd_masthead, .cdxhd_scroll_body {
 display: none !important;
}

.header-logo {
	background-image: url('data:image/png;base64,') !important;
}
}
