css:
@-moz-document domain("nobelium.xyz") {
.navbar-brand img {
    content: url("http://i.imgur.com/196Y7M2.png");
}
.panel-primary>.panel-heading {
    background-color: #000080;
}
.navbar,
.navbar-default {
    background-color: #000080;
    border-color: transparent;
}
.panel-heading {
    padding: 10px 15px;
    border-bottom: 0px solid transparent;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
body {
    font-family: 'Trebuchet MS', Trebuchet MS;
    -webkit-font-smoothing: antialiased;
    background: #000080;
    -moz-background-size: cover;
    background-size: cover;
    -o-background-size: cover;
    -webkit-background-size: cover;
    background-image: url(http://image.prntscr.com/image/7afc2444bdb44e25b645087ba9ba1644.png)
}
/*-----NAVBAR-----*/

.navbar-collapse.collapse 
.navbar,
.navbar-default {
    background-color: #000080;
}
.navbar .navbar-nav>li>a:hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Trebuchet MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
.navbar .navbar-nav>li>a {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Trebuchet MS;
    font-weight: normal;
    font-size: 1.25em;
}
.navbar .navbar-nav>li>a:active {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Trebuchet MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
.navbar .navbar-brand {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Trebuchet MS;
    font-weight: normal;
    font-size: 1.30em;
}
.navbar .navbar-brand:hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Trebuchet MS;
    font-weight: normal;
    font-size: 1.30em;
    text-decoration: underline;
}
span.panel-panel-primary > .panel-heading-center {
    display: none;
}
.footer.container {
    background-color: transparent;
    color: #000080;
    font-family: Arial;
    font-weight: normal;
    font-size: 0em;
    height: 1;
}
.footer.container hr {
    border-top: transparent
}
.container .row .col-md-4.center.well-lg {
    background-color: white;
}
.alert.alert-info.center {
    background-color: #000080;
    border-color: transparent;
}
.container .row .col-md-10.col-md-offset-1 .panel.panel-default .panel-body .row .col-md-8 .label.label-info {
	background-color:#000080;
}
.container .row .col-md-9 .hk-post-comments h4 {
	background-color: white;
    padding-top: 10px;
    padding-bottom: 10px;
}
.container .row.flex-override .col-md-6.vertical-align-center .valign-center {
	background-color: white;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 10px;
}
.container h3 {
    background-color: white;
	padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 10px;
}
}
