css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("logiclx.humnet.ucla.edu") {
  body {
    font-family:sans-serif !important;
    color: #333 !important;
  }
  body > p:empty {
    display: none !important;
  }
  .headerwrap {
    color: #333 !important;
  }
  .page_content {
    background-color: aliceblue !important;
    margin-top: 0px !important;
  }
  table.commontable {
    border-right: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
  }
  table.commontable td {
    border-left: 1px solid #333 !important;
    position: inherit !important;
    text-align: center !important;
    vertical-align: middle !important;
  }
  table.commontable td.adjustable {
    background-color: inherit !important;
  }
  table.commontable td.adjustable.attention {
    background-color: #b7cadd !important;
  }
  table.commontable td.adjustable.latecorrect {
    background-color: #e9b3b3 !important;
  }
  table.commontable td.adjustable.adjusted, table.commontable td.adjustedthis {
    border-width: 0px 0px 0px 1px !important;
    background-color: #6fa4dd !important;
  }
  table.commontable th {
    background-color: #eee !important;
    color: #333 !important;
    font-weight: normal !important;
    border-color: #333 !important;
    position: inherit !important;
    text-align: center !important;
    vertical-align: middle !important;
  }
  table.commontable thead {
    position: sticky !important;
    top: 0 !important;
  }
  table.commontable ul, table.commontable ol {
    text-align: left !important;
  }
  table.commontable tr:first-child th.heading {
    padding-left: 25% !important;
    padding-right: 25% !important;
  }
  table.commontable span {
    color: #333 !important;
  }
  .commontable .even {
    background-color: #ddd !important;
  }
  .totals td {
    background-color: #eee !important;
    border-color: #333 !important;
    color: #333 !important;
    border-right: none !important;
  }
  a {
    color: #333 !important;
  }
  .tabmenu {
    margin-bottom: 0px !important;
  }
  .tabmenu li {
    display: inline-block !important;
    position: relative !important;
    bottom: -2px !important;
  }
  .tabmenu a.tab {
    display: inline-block !important;
    background-color: #eee !important;
    padding-bottom: 3px !important;
    vertical-align: middle !important;
  }
  .tabmenu a.selected{
    background: aliceblue !important;
    border-bottom-color: aliceblue !important;
  }
  .tabmenu a.tab.selected {
    border-bottom: 2px solid aliceblue !important;
  }
  .tabmenu a.tab.selectedparent {
    border-bottom: 2px solid #eee !important;
  }
  .tabmenu .tabheading {
    margin-bottom: 0px !important;
  }
  td.clear, th.clear {
    display: none !important;
  }
  .probadjust td {
    border-width: 0px !important;
    border-top: 1px solid black !important;
  }
  tr.ui-widget-content {
    background-color: #fafafa !important;
  }
  tr.ui-widget-content td {
    background-color: inherit !important;
  }
}
